css:
@-moz-document domain("b-ok.global"), domain("z-lib.org"), domain("booksc.org") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

.col-md-12,

.modal-content
{
  background: #3d3d3f;
}

.dropzone,

.popover,

.navbar-default .navbar-nav> .open> a, .navbar-default .navbar-nav> .open> a:hover, .navbar-default .navbar-nav> .open> a:focus, .dropdown-menu,

div.blogContentBox,

#subprojectsSearch li.active, #searchResultBox,

.userpoll-container, .suggestion-book-container,
.table-striped> tbody> tr:nth-of-type(odd),

div.tab-content, .nav-tabs> li.active> a, .nav-tabs> li.active> a:hover, .nav-tabs> li.active> a:focus
{
    background: #363639;
}

.cBox1
{
  background: #3b680f;
}

div.cBoxError
{
  background: #680f0f;
}

input, select, textarea,

#searchModeTabs li.active, #searchModeTabs li.active a, .b-search-input,
.b-search-input .button input, .b-search-input .button button,

.etcFt,

.dropdown-menu> li> a:hover, .dropdown-menu> li> a:focus,

.membership-pricing-table table .plan-header-free,

.input-group-addon,
.nav> li> a:hover, .nav> li> a:focus,

.book-list-container,
.radial-progress-bar .overlay,

.trumbowyg-editor, .trumbowyg-button-pane

/*,
I dunno, if you want a dark header and footer it's all here for ya

#footer,
.container-fluid,

*[style="height:10px;"] td .col-md-12
*/
{
    background: #2e2e2f !important;
}

.membership-pricing-table table td
{
    background: #363639;
}

div[style="display:inline-block;font-size: 13px;line-height:18px;padding: 0 25px; background:#fff;position:relative; top: -13px;"]
{
  background: #0000 !important;
}

.progress-40.color-green
{
  background-image: linear-gradient(90deg, #3d3d3f 50%, transparent 50%), linear-gradient(54deg, #3d3d3f 50%, #8ecd51 50%);
}

.progress-80.color-green {
    background-image: linear-gradient(198deg, transparent 50%, #8ecd51 50%), linear-gradient(90deg, #3d3d3f 50%, #8ecd51 50%);
}

body, input, select, h1, h2, h3, h4, h5, h6,
textarea,

.itemFullText div,

.cBox1 *, .cBox1,

#searchModeTabs li.active, #searchModeTabs li.active a,
#searchModeTabs li a:hover,

.etcFt,

.dropdown-menu *,
.navbar-default .navbar-nav> li> a:hover, .navbar-default .navbar-nav> li> a:focus,

#subprojectsSearch li.active, .resItemBox h3 a:link, .resItemBox h3 a:visited,

.resItemBox .bookDetailsBox a:link, .resItemBox .bookDetailsBox a:visited,

.membership-pricing-table table tr td:first-child,
ul.stepsList li.undone,

div.resItemFuzzyMatch h3 a:link, div.resItemFuzzyMatch h3 a:visited
{
  color: #ccc !important;
}

.trumbowyg-box svg, .trumbowyg-modal svg
{
  fill: #ccc;
}

*
{
  border-color:#0000 !important;
  box-shadow: none !important;
}

a
{
  text-decoration: none !important;
}

.membership-pricing-table table tr td:first-child,

div[style="margin:0 0 0 30px;font-size:80%;"]
{
  font-style: italic;
}

.container-fluid
{
  margin-bottom: 25px;
}

#footer .container-fluid
{
  margin-bottom: -254px;
}
.b-search-input .button input, .b-search-input .button button
{
  border-left: 5px solid #363639 !important;
}

.popover
{
  box-shadow: 0px 0px 15px #111 !important;
}
.popover.bottom> .arrow:after
{
  border-bottom-color: #363639 !important;
}
.itemFullText
{
  border: 2px solid #363639 !important;
  border-radius: 5px;
}

#jscommentsTextarea
{
  width: 90% !important;
  min-height: 50px !important;
}

#searchFormWithLogo img
{
    filter: invert(1) hue-rotate(185deg) saturate(5) brightness(1.2);
    mix-blend-mode: lighten;
}

