css:
@-moz-document domain("cpu-world.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3) !important;
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
			-webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3) !important;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3) !important;
             background-color: #C08040;
        }


body {
	background: #101010;
	color: #fff;
}
table.nav_table {
	background: #000;
	border-color: #181818;
}
.cpu_content, .news_content, .info_content, .vs_content, .dialog_content, .popup_content {
	background: #000;
	border-color: #303030;
}
.yhb {
	border-color: #000;
}
h2 div, h2 span, h3 div, h3 span {
    background-color: #fff;
    border-bottom: 5px solid #505050;
    border-right: 5px solid #505050;
}
table.spec_table td.spec_hd span {
	background: #000;
	border-color: #fff;
}
table.vs_s_table tr td, table.spec_table tr td {
	border-color: #000;
}
.spec_table > tbody > tr > td, .center_td.sh_table > tbody > tr > td {
	background: #000;
}
tr.sh_subhead th, table th.sh_subhead {
	background: #282828;
	color: #909090;
}
.center_td.sh_table > tbody > .sh_subhead > th {
	border-color: #000 !important;
}
a:hover {
    color: #FFF0D0;
    background-color: #4d4d4d;
    box-shadow: -3px 0 5px #303030, 3px 0 5px #303030;
}
[src='/Images/Price_chart/Core_i7-2600K_price.gif'] {
	filter: invert(100);
}
input, select, textarea, button {
    color: #b0b0b0;
    background-color: #000;
    border-left: 1px solid #505050;
    border-top: 1px solid #505050;
    border-right: 1px solid #181818;
    border-bottom: 1px solid #181818;
}
.ibutton, .ibuttonr, .ef_button, .comment_rbutton, .ibuttonl, .ibuttonlr, .cal_button {
    background-color: #2b2a28;
    color: #b0b0b0;
    border-left: 1px solid #1e1d1c;
    border-top: 1px solid #1e1d1c;
    border-right: 1px solid #5e5b56;
    border-bottom: 1px solid #5e5b56;
}
.side_h4, .side_hs {
    background-color: #000;
    border: 1px solid #303030;
    border-bottom: 1px solid #202020;
}
.row1, .div1 {
	background: #000;
}
.side_div2 {
	border-color: #101010;
}
.row2, .div2 {
	background: #181818;
}
table.bm_tablew td {
	border-color: #303030;
}
table.bm_tablec th, table.bm_tablew th {
    color: #000;
    background-color: #505050;
    border-left: 1px solid #101010;
    border-bottom: 3px solid #000;
}
.bc_i00C {
    background-color: #26262b;
    border-color: #2020B0;
}
.bc_iC00 {
    background-color: #2f2929;
    border-color: #601111;
}
.bc_i000 {
    border-color: #fff;
}
table.sh_table th, div.sh_t_th, div.sh_t_title {
    background-color: #505050;
    color: #000;
}
table.sh_table tr.row1:hover, table.sh_table tr.row2:hover, table.lh_table tr.row1:hover, table.lh_table tr.row2:hover {
    background-color: #15171a;
}
table.sh_table td {
	border-color: #181818;
}
.ie_wfix > .sh_table > tbody > tr.row1 > td, .ie_wfix > .sh_table > tbody > tr.row2:nth-of-type(3) > td:nth-of-type(4) {
	 background: #000 !important;
}
.ie_wfix > .sh_table > tbody > tr.row2 > td {
	border-right-color: #000 !important;
}
.ie_wfix > .sh_table > tbody > tr.row1 > td {
	border-color: #000 !important;
}
table.sh_table th {
	border-color: #000;
}
table.relc_table tr.rel_family td {
    color: #909090;
    background-color: #101010;
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
}
table.relc_table th {
    color: #000;
    background-color: #505050;
}
table.lh_table, table.lh_table th {
	border-color: #181818;
}
table.lh_table tr.row1 td {
	border-color: #000;
}
table.lh_table tr.row2 td {
	border-color: #181818;
}
.worse, td.worse, tr.row1 td.worse, tr.row1 td.bg_r, .worsef {
    background-color: #2b2424;
}
.better, td.better, tr.row1 td.better, tr.row1 td.bg_g {
    background-color: #272d27;
}
tr.row2 td.worse, tr.row2 td.bg_r {
    background-color: #221b1b;
}
tr.row2 td.better, tr.row2 td.bg_g {
    background-color: #1a201a;
}
table tr td._linkv {
	background: #000;
	border-color: #000;
}
a._linkv, span._linkv {
	background: #101010;
	border-color: #202020;
}
a._linkv:hover {
    box-shadow: 2px 0 3px #303030;
}
.smaller {
	border-color: #101010 !important;
}
.comments h3, .comments_title {
	background: #303030;
	border-color: #000;
}
.comments_author {
	background: #101010;
}
.comment_right {
	border-color: #000;
}
table.ft_table th {
	background: #000;
}
.comment_buttons2 {
	border-color: #303030;
}
.yhb_footer, .yhb_ifooter {
	border-color: #101010;
}
.side_hl, .side_hl2, .breadcrumbs, table.yhb_table td.breadcrumbs {
    background-color: #1a1814;
}
.spec_menu {
	filter: invert(100);
}
.AutoSuggest, .PDMenu {
	background-color: rgba(0, 0, 0, .85);
    color: #fff;
}
.AutoSuggest2, .PDMenu2 {
	background: #000;
	border-color: #303030;
}
.AutoSuggestChoice, .AutoSuggestChoice2, .PDMenuChoice {
	color: #fff;
}
table.nav_table td {
	color: #b0b0b0;
}
th.h_mmenu {
	border-color: #000;
}
#tab-container {
	background: #000;
}
.tab-header ul.tab-list span, .tab-header ul.tab-list a.tab-active, .tab-header ul.tab-list a.tab-active:hover {
	border-color: #202020;
	border-bottom-color: #000;
}
.tab-header ul.tab-list span, .tab-header ul.tab-list a.tab-active, .tab-header ul.tab-list a.tab-active:hover {
	background: #000;
	color: #b0b0b0;
}
.tab-header ul.tab-list a {
	border-color: #000;
	color: #fff;
	background: #000;
}
.tab-content {
	border-color: #202020;
	background: #000;
}
h1.vs_h1 {
    color: #000;
    background-color: #404040;
}
.h1_sep {
	border-color: #404040;
}
.news_date {
	background: #101010;
	color: #b0b0b0;
}
.sn_header {
	background: #404040;
	border-color: #000;
	color: #000;
}
.sn_content {
	background: #181818;
}
.sn_footer {
	border-color: #000;
}
.side_sect > div:nth-of-type(2) {
	border-color: #303030 !important;
}
.more, .jsc_a, table td.jsc_a, [src='/Images/ab_z91.gif'] {
	filter: invert(100);
}
.more a {
	filter: invert(100);
}
.side_ad160, .side_ad300, .side_ad336 {
	border-color: #303030;
}
div#DIV_O_COMMENTS > div:nth-of-type(4) {
	border-color: #303030 !important;
}
.main_div2 > div:nth-of-type(3) {
	border-color: #303030 !important;
}
.html-attribute-value {
    color: rgb(26, 112, 166);
}
.news_updated {
	background: #101010;
	color: #fff;
}
.img_zoom {
	border-color: #000 !important;
}
.img_zoom:hover, .img_zoom:active {
	border-color: rgb(30, 30, 40) !important;
}
}
