css:
@-moz-document regexp('https?://informatics.m(ccme|sk).ru.*') {
    .page-item.disabled .page-link {
        color: #ffffff;
        background-color: #505050;
        border-color: #505050;
    }
    .page-link {
        background-color: #505050;
        border: 1px solid #505050;
    }
    @-webkit-keyframes bg-pulse-grey {
        0% {
            background-color: #2e2e2e;
        }

        50% {
            background-color: #4e4e4e;
        }

        100% {
            background-color: #2e2e2e;
        }
    }
    @-o-keyframes bg-pulse-grey {
        0% {
            background-color: #2e2e2e;
        }

        50% {
            background-color: #4e4e4e;
        }

        100% {
            background-color: #2e2e2e;
        }
    }
    @keyframes bg-pulse-grey {
        0% {
            background-color: #2e2e2e;
        }

        50% {
            background-color: #4e4e4e;
        }

        100% {
            background-color: #2e2e2e;
        }
    }

    .col-12 {
        background-color: #2e2e2e;
    }
    button,
    input,
    optgroup,
    select,
    textarea {
        border-color: #222222;
        background-color: #2f2f2f;
        border-style: solid;
        color: #fefefe;
    }
    .table {
        color: #fefefe;
    }
    .bg-gray {
        background-color: #505050!important;
    }
    .list-group-item-action:focus,
    .list-group-item-action:hover {
        color: #495057;
        background-color: #f8f9fa;
    }
    .dimmed_category,
    .dimmed_category a,
    .dimmed_text,
    .dimmed_text a,
    .dimmed_text a:link,
    .dimmed_text a:visited,
    .usersuspended,
    .usersuspended a,
    .usersuspended a:link,
    .usersuspended a:visited,
    a.dimmed,
    a.dimmed:link,
    a.dimmed:visited,
    a.dimmed_text,
    a.dimmed_text:link,
    a.dimmed_text:visited {
        color: #c8ccd0;
    }
    .token {
        background-color: #222 !important;
    }
    :focus {
        outline: #222;
    }
    .btn-secondary {
        color: #fefefe;
        background-color: #505050;
        border-color: #505050;
    }
    .btn-secondary:focus,
    .btn-secondary:hover {
        color: #fefefe;
        background-color: #404040;
        border-color: #404040;
    }
    .btn-light {
        color: #fefefe;
        background-color: #505050;
        border-color: #505050;
    }
    .btn-light:focus,
    .btn-light:hover {
        color: #fefefe;
        background-color: #404040;
        border-color: #404040;
    }
    a.bg-gray:focus,
    a.bg-gray:hover,
    button.bg-gray:focus,
    button.bg-gray:hover {
        background-color: #404040!important;
    }
    .close {
        color: #fff;
    }
    .navbar-light .navbar-brand:focus,
    .navbar-light .navbar-brand:hover {
        color: #fefefee6;
    }
    .bg-white {
        background-color: #222!important;
    }
    .navbar-light .navbar-brand {
        color: #fefefe;
    }
    .form-control {
        color: #fefefe;
        border: none;
        background-color: #404040;
    }
    .form-control:focus {
        color: #fefefe;
        background-color: #505050;
    }
    .alert-info {
        color: #46a6b9;
        background-color: #171717;
    }
    [data-region="drawer"] {
        background-color: #222;
    }
    body {
        background-color: #2f2f2f;
        color: #fefefe;
    }
    #region-main {
        background-color: #222;
    }
    .card {
        background-color: #222;
    }
    pre {
        color: #fefefe;
    }
    .problem-statement .sample-tests pre {
        background-color: #1e1e1e;
    }
    .list-group-item {
        background-color: #2f2f2f;
    }
    .list-group-item-action {
        color: #fefefe;
    }
    .bg-secondary {
        background-color: #2f2f2f!important;
    }
    .navbar-light .navbar-nav .nav-link {
        color: #fefefe;
    }
    .navbar-light .navbar-nav .nav-link:hover, .navbar-light .navbar-nav .navlink:focus {
        color: #ccc;
    }
    .dropdown-item {
        color: #fefefe;
        background-color: #1e1e1e;
    }
    .dropdown-item:hover, .dropdown-item:focus {
        color: #ddd;
        background-color: #2e2e2e;
    }
    .dropdown-menu {
        background-color: #1e1e1e;
    }
    .dropdown-divider {
        border-top: 1px solid #2e2e2e;
    }
    :not(pre) > code[class*="language-"], pre[class*="language-"] {
        background: #2e2e2e;
    }
    code[class*="language-"], pre[class*="language-"] {
        color: #ccc;
        text-shadow: none;
    }
}
