css:
@-moz-document domain('gamekyo.com') {
  #main-container{
      width: 100%;
  }
  #column-0, #column-2 { margin: 0px 20px;}

  #column-1 {
      width: calc(100% - 680px);
      margin: 0px;
  }

  #container { position: relative; overflow: hidden;}

  #container, #container ul, .slideshow-div { width: 100%;}

  .slideshow-div { background-size: cover !important; }

  #container li {
      left: 0px;
      right: 0px;
  }

  body {
      overflow: auto !important;
  }
}
