css:
@-moz-document domain("scriptinghelpers.org") {
body {
    background-color: #000;
}
.header-container{
    background-color: #000;
    border-color: #fff;
}
.chat-pane{
    background-color: #000;
    border-color: #fff;
}
.chat-title{
    background-color: #333;
}
.chat-content{
    color: #fff;
}
.chat-avatar{
    background-color: #fff;
    border-radius: 999px;
}
#chatinput{
    background-color: #000;
    color: #fff;
    border-top: 1px solid #fff;
}
#chats-container{
    background-color: #000;
}
header{
    background-color: #000;
}
nav ul li a{
    color: #fff !important;
}
nav ul li:hover{
    border-color: #999;
}
nav ul li.nav-active{
    border-color: #fff;
}
.notifications{
    background-color: #fff;
    border-radius: 999px;
}
.notification-icon{
    margin-top: 0px;
    margin-right: 0px;
    padding: 7px;
    transform: scale(1.4,1.4);
}
.loggedin-avatar{
    background-color: #fff;
    border-radius: 999px;
}
.loggedin-name{
    color: #fff !important;
}
.loggedin-name:hover{
    background-color: #000 !important;
}
.dropdown{
    background-color: #000;
}
.dropdown a:hover{
    background-color: #333;
}
.dropdown a{
    color: #fff;
}
.container-outer{
    background-color: #000;
    color: #fff;
    margin-top: 15px;
}
.question-comment:hover{
    background-color: #333;
}
.question-comment-post{
    background-color: #000;
    color: #fff;
}
.question-comment-upvote{
    background-color: #fff;
    opacity: 1;
    border-radius: 999px;
    padding: 2px;
}
.question-comment-upvote:hover{
    background-color: #ccc;
    opacity: 1;
    border-radius: 999px;
    padding: 2px;
}
.cta{
    background-color: #000;
    color: #fff; 
}
.question-points{
    color: #fff;
}
.question-points img{
    background-color: #fff;
    opacity: 1;
    border-radius: 999px;
    transform: scale(0.5,0.5);
}
.question-markdown{
    color: #fff;
}
.syntaxhighlighter{
    border: 1px solid #fff !important;
}
.syntaxhighlighter .line .number{
    color: #fff !important;
}
.syntaxhighlighter .plain{
    color: #fff !important;
}
.syntaxhighlighter .line.alt1{
    background-color: #000 !important;
}
.syntaxhighlighter .line.alt2{
    background-color: #333 !important;
}
.question-code-view-all:hover{
    background-color: #333 !important;
}
.question-service-links a{
    color: #fff !important;
    background-color: #000;
    border-radius: 999px;
    border: 1px solid #fff;
}
.question-service-links a:hover{
    background-color: #333;
}
.question-textarea{
    background-color: #000;
    color: #fff;
}
.question-textarea::placeholder{
    color: #fff;
}
.q-link .a{
    color: #fff;
    border-top: 1px solid #fff;
}
.q-link .a:hover{
    background-color: #333;
}
.ask-button-container .ask-button{
    color: #fff;
    border-radius: 999px;
    border: 1px solid #fff;
}
.ask-button-container .ask-button:hover{
    background-color: #333;
}
.q-sort-type ul li{
    color: #fff;
}
.q-sort-type ul li:hover{
    color: #ccc;
}
.q-sort-type ul li.active{
    color: #fff;
    border-radius: 999px;
    border: 1px solid #fff;
}
.nav-search{
    background-color: #000;
    color: #fff;
}
.nav-search::placeholder{
    color: #fff;
}
.blog-post{
    background-color: #000;
}
.blog-catinfo{
    background-color: #000;
}
code{
    background-color: #333;
    color: #fff;
    border-radius: 999px;
    border: 1px solid #fff;
}
.settings label{
    color: #fff !important;
}
.settings .input input{
    background-color: #000;
    color: #fff
}
.user-profile-reputation label{
    color: #fff;
}
.link-dark{
    color: #fff !important;
}
.help-article a{
    background-color: #000;
    color: #fff;
    border-top: 1px solid #fff;
}
.help-article a:hover{
    background-color: #333;
}
.user-profile-avatar{
    background-color: #fff;
    border-radius: 999px;
}
.q-list-points{
    color: #fff;
}
.site-logo{
    border-radius: 10px;
    border: 1px solid #fff;
    padding: 15px 10px 10px;
    background-color: #333;
}
.site-logo:hover{
    border-color: #fff;
    background-color: #666;
}
span{
    color: #fff !important;
}
.notification-box{
    background-color: #000;
    color: #fff;
}
.notification{
    border-top: 2px solid #fff;
}
.login-area a{
    color: #fff;
}
.login-area a:hover{
    color: #fff;
    background-color: #333;
}
.user-profile-info label{
    color: #fff;
}
#question-title{
    background-color: #000;
    color: #fff;
}
#question-title::placeholder{
    color: #fff;
}
input, textarea{
    background-color: #000;
    color: #fff
}
input, textarea::placeholder{
    color: #fff
}
}
