css:
@-moz-document domain("gugeys.cc") {
body{
    background: #181818;
}
#um, #um a,.xg2,body, input, button, select, textarea{
    color: #c4d2d9;
}
a {
    color: #eee;
    text-decoration: none;
}
.chart em{
    color: #777;
}
.fl .bm_c, #online .bm_c, .lk .bm_c {
    background: #333335;
}
.man_show,.hdm-top,.hdm-normal,.fl .bm_h,.bm{
    background: #3c3d3e;
    border:0;
}
.t_f a{
    color: #7ed5ff;
}
.hdm-hover{
    color: #1e150f;
    background: #959597;
}
.man_show .com-3-top span,.hdm-normal,.man_show .com-1-top span,.bmw .bm_h a{
    color: #65b0ff;
}
.pls{
    background: #313133;
}
.ad{
    display:none;   
}
#toptb,#nv{
    background: #313133;
    border-bottom: 1px solid #666;
}
#nv li,#nv li.a,#mu ul,#scbar,.scbar_icon_td{
    background: #313133;
}
#nv li.a a, #nv li.a a:hover{
    background: #a6c5ff;
    color: #102b6a !important;
}
#scbar{
    border-top: 1px solid #666;
    border-bottom: none;
}
.p_pop, .p_pof, .sllt,#extcreditmenu.a{
    background: #313133;
    border: none;
}
.p_pop a{
    border-bottom: 1px solid #666;
}
.bm_h{
    background: #602929;
}
/*个人页*/
.block {
    background: #313133;
    border: none;
}
.block .title{
    color: #1e150f;
    border-bottom: 1px solid #595959;
    background: #959597;
}
}
