css:
@-moz-document url-prefix("https://northeastern-csm.symplicity.com/students/app/jobs") {
.list-item.list_rows {
    padding: 8px;
    padding-bottom: 0px;
}
.list-item .list-item-subtitle {
    padding-top: 0px;
    flex: 4;
}
.list-item {
    overflow: wrap !important
}
.list-item-logo {
    display: none;
}
.list-item .list-item-body {
    display:flex;
    justify-content: space-between;
    flex: 12;
}
.list-item-title {
    flex: 7;
}
.list-item .list-item-title h3 {
    font-size: 12px;
}
.list-data-columns-job_type {
    flex:1;
}
.container {
    width: 98%;
}
.list-item-actions {
    display: flex;
	flex: 1;
    justify-content: space-between;
}
.list-item-subtitle.list-data-columns-employer {
    font-size: 12px;
    display: flex;
    justify-content: space-between
}
.list-data-columns.list-data-columns-location {
    display: block;
    flex: 4;
    font-size: 12px;
}
.list-item-subtitle.list-data-columns-employer .list-data-columns-location {
    display: none;
}
.list-data-columns-location {
    flex: 2;
}
#student_job_list_content li {
    min-height: 0px;
}

job-element:first-child{
	display:flex;
}


#student_job_list_content .actions-toggle-wrap a.icn-thumbs_down, #list .actions-toggle-wrap a.icn-thumbs_down, #student_job_list_content .actions-toggle-wrap a.icn-thumbs_down_filled, #list .actions-toggle-wrap a.icn-thumbs_down_filled{
	margin-left:3px;
}


.list-item .list-data-columns.list-data-columns-location{
	display: block;
}

}

@-moz-document url-prefix("https://northeastern-csm.symplicity.com/students/app/jobs/") {
.job_display {
    display:flex;
}
.job_summary {
    flex: 1;
}
.job_display_desc {
    flex: 7;
    margin-right: 24px;
}
.job-bfields {
    flex:3;
}

.job_display_desc .field{
	width: 90%;
    max-width: unset;
}

.job_summary_inner{
	max-width: 80px;
    margin-right: 24px;
    border-bottom: unset;
}

.job_summary_inner .field{
	max-width: 90px;
    width: 90px;
}

.job_review_wrap .job_summary_inner > div, .visual-header-banner .job_summary_inner > div{
	min-width: 120px
}

.has-sidebar .form-col{
    width: calc(80% - 10px);
}

.field .field-label, .field .FormText {
	font-weight: 700;
}

#async_panel_div_0{
    margin: 24px;
	border-top: 1px solid #ececec;
}

.job_review_wrap .job_summary_inner, .visual-header-banner .job_summary_inner, .job_review_wrap .job_display_desc, .visual-header-banner .job_display_desc{
	border-bottom: unset;
}
}
