@-moz-document domain("web.whatsapp.com") {
:root {
    --panel-background-colored: #2aa198;
    --background-default: #fdf6e3 !important;
    --background-default-hover: #eee8d5 !important;
    --background-default-active: #eee8d5 !important;
    --dropdown-background: #fdf6e3 !important;
    --dropdown-background-hover: #eee8d5 !important;
    --compose-input-background: #fdf6e3 !important;
    --compose-input-border: transparent !important;
    --incoming-background: #fdf6e3 !important;
    --outgoing-background: #f9fece !important;
    --rich-text-panel-background: #eee8d5 !important;
    --startup-background: #fdf6e3 !important;
    --incoming-background-deeper: #eee8d5 !important;
    --border-default: #93a1a1 !important;
}

/* Fullscreen */
.h70RQ {
    margin: 0 !important;
    top: 0 !important;
    width: 100% !important;
    height: 100% !important;
}

/* keep your phone connected screen */
._1kdBg {
    background: #fdf6e3 !important;
}

/* groups bar scroll */
._1qDvT._3R02z {
    background: #93a1a1 !important;
}

/* groups search container */
._2EoyP {
    background: #eee8d5 !important;
}

/* groups search */
._3xpD_ {
    background: #fdf6e3 !important;
}

/* top bar left part */
._1QUKR {
    background: #eee8d5 !important;
}

/* top bar right part */
header._1iFv8 {
    background: #eee8d5 !important;
}

/* main chat background */
.z_tTQ {
    background: #eee8d5 !important;
}

/* replace chat message shadow */
._3sKvP.wQZ0F {
    box-shadow: #93a1a1 1px 2px 0;
}

/* remove message arrow background */
._2nBjH,
._4tndQ {
    background: none !important;
}

/* add shadow to compose area */
._2vJ01 {
    box-shadow: #93a1a1 0 -2px 2px;
}
}
