/* SCROLLBAR */

/* width */
::-webkit-scrollbar {
  width: 15px;
}

/* Track */
::-webkit-scrollbar-track {
    background: linear-gradient(to top, #a143d090, #7CA3E690)!important;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: #ffffff90; 
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #fff; 
}
}
