css:
@-moz-document url-prefix("https://knigavuhe.org")
{
body {
    background: #222 !important;
    color: #999 !important;
}

.header {
    background: #222 !important
}

.header_logo {
    filter: invert(0.25)
}

.header_link2 {
    color: #777
}

.subheader {
    display: none;
}

.page_title,
.book_serie_block_title > a,
.bookkitem_name > a,
.books_right_block_title > a,
.last_comments_comment_book_name > a {
    color: limegreen !important;
}

.user_rating0 {
    color: #999 !important
}

.index_ipbook {
    color: #386ca8;
}

.index_ipbook_chapters {
    color: #999 !important;
}

.pn_show_more {
    background-color: #333;
    border: 1px solid #777;
    color: #999;
}

.bookkitem_date_sep {
    background-color: #333;
}

.bookkitem_date_sep_inner {
    background: #222;
    color: #999;
}

.index_onlyfree_bg {
    background-color: #333;
}

.bookkitem_about {
    color: olive;
}

input[type=text].header_search {
    background: black;
}


a.header_link {
    color: steelblue
}

a.header_link:hover {
    color: darkorange
}

.book_player {
    width: 365px !important;
}

.book_player_btn.-backward::before,
.book_player_btn.-forward::before,
.book_player_btn.-next::before,
.book_player_btn.-prev::before {
    filter: invert(0.5)
}

.book_player_progress_btn_icon {
    fill: #999;
}

.book_middle_ads {
    display: none
}

.suggested_book,
.header_search_results_section_item_serie_authors {
    color: forestgreen
}

.comments_input_wrap {
    background-color: #333
}

.suggested_book:hover {
    color: darkgoldenrod
}

.comments_input {
    background: #555 !important;
}

div.comments_input:empty::before {
    color: black !important;
}

button[type=button].default {
    background: #777;
}

input[type=text].header_search {
    color: lawngreen;
}

.header_search_results {
    background-color: #111;
    color: #777;
}

a.header_search_results_section_item {
    color: forestgreen;
}

.header_search_results_section_item_book_line2 {
    color: #999;
}

.header_search_results_section_item_book_line1 > .__name {
    color: limegreen;
}

.header_search_results_section_item_book_line1 > .__author_label {
    color: darkcyan;
}

.footer_copyright a :first-child {
    filter: invert(0.5);
}

.pn_button {
    background-color: #777;
    border: 1px solid #777;
}
.pn_button_sep,
input.pn_goto_input {
    background: #555;
    border: 1px solid #555;
}

.pn_button.-page_cur {
    background-color: lightslategray;
}

.books_cr_book_name,
.books_preview_book {
    color: limegreen;
}

.books_cr_book:hover,
.books_preview_book:hover,
.book_action_fav_btn:hover > span {
    color: darkgoldenrod;
}

.books_cr_book_author_href,
.suggested_book_author_href {
    color: #386ca8;
}

.comment_spoiler {
    background-color: #444 !important;
}

input[type="text"]::placeholder {
    color: #999 !important;
}

.pl {
    background: #333;
}

.pl_book__name > a {
    color: forestgreen;
}

h2.book_title {
    color: forestgreen;
}

.pl_controls > div.btn_backwards,
.pl_controls > div.btn_forwards,
.pl_controls > div.btn_play,
.pl_sleep_icon,
.pl_list,
.pl_speed,
.pl_volume.volume3 {
    filter: invert(1);
}

.book_small_player_outer {
    background-color: #444;
}

.subscribe2_btn,
.subscribe_btn {
    filter: invert(1);
}

.subscribe2_btn.is_selected {
    background-color: lightcoral;
}

button[type=submit].blue:disabled,
button[type=button].light {
    background: #444;
    color: #999;
}

label.checkbox > span:before {
    background-color: #999;
}

a.is_black {
    color: forestgreen;
}

.scroll_fix,
.footer_inner,
.footer_inner > div > a {
    background: #333;
}

.book_info_summary > a,
.book_info_line a.book_info_line_link,
.comments_order_tab.is_selected,
.pl_book__authors > a {
    color: darkcyan;
}

.sidebook_name {
    color: forestgreen;
}

.sidebook_info_line {
    color: darkcyan;
}

.header_link:hover {
    background-color: #333;
}

.comment_vote.is_active > div,
.user_rating-1:hover,
.user_rating0:hover,
.user_rating1:hover,
.user_rating2:hover,
.user_rating3:hover,
.user_rating4:hover,
.user_rating5:hover,
.user_rating6:hover {
    color: peru !important;
}

.book_info_summary > a:hover,
.book_info_line a.book_info_line_link:hover,
.comments_order_tab.is_selected:hover,
.pl_book__authors > a:hover {
    color: peru;
}

.comment_reply > a:hover {
    color: peru;
}

.comment:target > .comment_head {
    background: #444;
}

.plist {
    background: #222;
}

.user_header_name {
    color: darkcyan;
}

.user_about {
    color: #777;
}

.user_header_actions_rating_count {
    color: #999;
}

.header_user_menu {
    background: #333;
}

a.header_user_menu_item {
    color: #999;
}

a.header_user_menu_item:hover {
    background: #222;
}

.bookitem_cover_genre,
.bookitem_cover_fav,
.book_snippet_genre,
.book_snippet_fav {
    background-color: #555;
}

.genre_header_info_text > a,
.bookitem_meta_link {
    color: darkcyan;
}

.author_header_name,
.genre_header_name {
    color: forestgreen;
}

.book_snippet {
    color: forestgreen;
}

a.book_snippet:hover {
    color: peru;
}

input[type=text] {
    color: limegreen;
}

.search_block_title > a {
    color: steelblue;
}

.search_book_name > a {
    color: forestgreen;
}

.book_small_playlist_item:hover .book_small_playlist_item_name {
    color: peru;
}

.comment_vote.is_active > div,
.comment_vote:not(.is_disabled):not(.is_unavailable):hover > div,
.comments_order_tab:hover:not(.is_selected),
.pl_book__authors > a:hover,
.pl_speed:active,
.plist_item:hover .plist_item_left_no,
.plist_item:hover .plist_item_name,
.plist_item:hover .plist_item_time,
.user_my_btn:hover {
    color: peru;
}
}
