css:
@-moz-document url-prefix("https://winworldpc.com") {
body {
    background-color: #1d1d1d;
    min-height: 100%;
}

nav#navbar {
    background: url('https://i.imgur.com/K5lvIKr.png');
    border-bottom: 1px solid #777788;
}

footer.footer {
    background: url('https://i.imgur.com/K5lvIKr.png');
    border-top: 1px solid #777788;
}

footer.footer div.container span.text-muted {
    color: #ff6600 !important;
}

footer.footer div.container span.text-muted a {
    color: #FF9900;
}

footer.footer div.container span.text-muted a:hover {
    color: #ffcc00;
}

a.navbar-brand {
    width: 216px;
    height: 45px;
    background: url('https://i.imgur.com/CVGo3CG.png');
    text-indent: 100%;
    white-space: nowrap;
    overflow: hidden;
}

a.nav-link {
    color: #cfdffc !important;
    font-weight: 700;
}

div.navbar-collapse form.form-inline input.form-control {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
    box-shadow: none !important;
}

div.navbar-collapse form.form-inline button.btn-outline-primary {
    color: #ffffff;
    background-color: #ff6600;
    border: 1px solid #ff6600;
    border-radius: 0;
    box-shadow: none !important;
}

div.navbar-collapse form.form-inline button.btn-outline-primary:hover {
    background-color: #d65600;
    border: 1px solid #d65600;
}

div.dropdown-menu {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
}

div.dropdown-menu a.dropdown-item {
    color: #ffff99;
    background-color: #2f2f2f;
}

div.dropdown-menu a.dropdown-item:hover {
    color: #ffcc00;
    background-color: #575555;
}

button.navbar-toggler {
    border: 1px solid #777788 !important;
    border-radius: 0;
}

button.navbar-toggler span.navbar-toggler-icon {
    filter: invert(1);
}

div.col-md-8 div.panel-primary {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    padding: 15px;
}

h2.text-primary {
    color: #ff6600 !important;
    text-shadow: 0.1em 0.1em 0.3em #000000;
}

h2.text-primary small i {
    color: #cfdffc;
}

div.panel-body p {
    color: #ffff99;
}

div.panel-body p a {
    color: #ff6600;
}

div.panel-body p a:hover {
    color: #ffcc00;
}

div.alert {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
}

div.alert h3.text-primary {
    color: #ff6600 !important;
    text-shadow: 0.1em 0.1em 0.3em #000000;
}

div.timeline-Widget {
    border-radius: 0;
}

div.alert-dismissable p {
    color: #ffff99;
}

div.alert-dismissable p a {
    color: #ff6600;
}

div.alert-dismissable p a:hover {
    color: #ffcc00;
}

.close {
    color: #777788;
    opacity: 1;
    text-shadow: none;
}

.close:hover {
    color: #777788;
}

div.alert-info,
div.alert-success,
div.alert-warning,
div.alert-danger {
    color: #ffffff;
}
}

@-moz-document url("https://winworldpc.com/home") {
div.panel-body p:nth-of-type(4) {
    color: #3c763d;
}

div.panel-body p:nth-of-type(4) a {
    color: #ff9900;
}

div.panel-body p:nth-of-type(4) a:hover {
    color: #ffcc00;
}
}

@-moz-document url-prefix("https://winworldpc.com/library") {
nav.nav-pills a.nav-link {
    font-weight: 400;
    color: #ff6600 !important;
    margin-right: 2px;
}

nav.nav-pills a.active,
nav.nav-pills a.nav-link:hover {
    background-color: #2f2f2f !important;
    color: #ffcc00 !important;
    border-radius: 0;
}
}

@-moz-document url("https://winworldpc.com/library/operating-systems") {
h3.text-primary {
    text-shadow: 0.1rem 0.1rem 0.3rem #000000;
}

h3.text-primary,
li.list-inline-item a {
    color: #ff6600 !important;
}

li.list-inline-item a:hover {
    color: #ffcc00 !important;
}
}

@-moz-document url-prefix("https://winworldpc.com/library/applications"), url-prefix("https://winworldpc.com/library/games"), url-prefix("https://winworldpc.com/library/dev"), url-prefix("https://winworldpc.com/library/sys") {
dl#libraryList dt a {
    color: #ff6600;
}

dl#libraryList dt a.badge {
    color: #ffffff;
    background-color: #ff6600;
    margin-left: 4px;
    float: right;
}

dl#libraryList dt a.badge-secondary {
    background-color: #5bc0de;
}

dl#libraryList dd p {
    color: #ffff99;
}

dl#libraryList dd p a {
    color: #ff6600;
}

dl#libraryList dd p a:hover {
    color: #ffcc00;
}

nav.flex-wrap ul.pagination li.page-item a {
    border-radius: 0;
    color: #ff6600;
    background-color: #2f2f2f;
}

nav.flex-wrap ul.pagination li.page-item a:hover {
    color: #ffcc00;
    background-color: #575555;
}

nav.flex-wrap ul.pagination li.active a {
    color: #ffffff;
    background-color: #ff6600;
    border: 1px solid #ff6600;
}

div a#advancedSearchButton,
div div#tagDropdown button.dropdown-toggle,
div div#platformDropdown button.dropdown-toggle {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
    box-shadow: none !important;
}

div a#advancedSearchButton:hover,
div div#tagDropdown button.dropdown-toggle:hover,
div div#platformDropdown button.dropdown-toggle:hover {
    background-color: #181717;
    border: 1px solid #5A5A67;
}
}

@-moz-document url("https://winworldpc.com/irc"), url("https://winworldpc.com/contact"), url("https://winworldpc.com/links"), url("https://winworldpc.com/about"), url("https://winworldpc.com/donate"), url("https://winworldpc.com/search-help"), url("https://winworldpc.com/copyright"), url-prefix("https://winworldpc.com/product"), url-prefix("https://winworldpc.com/screenshot"), url-prefix("https://winworldpc.com/download"), url-prefix("https://winworldpc.com/user") {
h1,
h2,
h3,
h5 {
    color: #ff6600;
    text-shadow: 0.1rem 0.1rem 0.3rem #000000;
}

p,
div.container ul li {
    color: #ffff99;
}

p a,
div.container ul li a {
    color: #ff6600;
}

p a:hover,
