    color: #ffcc00;
}

a.btn {
    color: #ffffff;
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
    box-shadow: none !important;
}

a.btn:hover {
    background-color: #181717;
    border: 1px solid #5A5A67;
}

a.btn:not(:disabled):not(.disabled):active {
    background-color: #181717;
    border: 1px solid #5A5A67;
}

div.card {
    border-radius: 0;
    border: 1px solid #777788;
}

div.card h3.card-header {
    background-color: #000000;
    font-size: 1;
    border-radius: 0;
}

div.card div.card-block {
    background-color: #2f2f2f;
    border-top: 1px solid #777788;
}

div#infoSheet div.card-header,
div#fileInfoSheet div.card-header {
    background-color: #000000;
    border-bottom: 1px solid #777788;
    border-radius: 0;
}

div#infoSheet div.card-header h2,
div#fileInfoSheet div.card-header h2 {
    color: #ffcc00;
}

div#infoSheet,
div#serialSheet,
div#fileInfoSheet {
    background-color: #2f2f2f;
}

div#infoSheet dl#infoSheetBody dt,
div#infoSheet dl#infoSheetBody dd {
    color: #ffff99;
}

div#infoSheet dl#infoSheetBody dd a {
    color: #ff6600;
}

div#infoSheet dl#infoSheetBody dd a:hover {
    color: #ffcc00;
}

div#infoSheet dl#infoSheetBody dd a.badge-primary,
div#infoSheet dl#infoSheetBody dd a.badge-secondary {
    color: #ffffff;
