}

div#infoSheet dl#infoSheetBody dd a.badge-info {
    color: #ffffff;
    background-color: #ff6600;
}

div#serialSheet div.card-header {
    background-color: #ff6600;
    border-bottom: 1px solid #ff6600;
    border-radius: 0;
}

div#serialSheet div.card-header h2 {
    color: #ffffff;
}

div#serialSheet {
    background-color: #2f2f2f;
    border: 1px solid #ff6600;
}

div#releaseInformation div.card {
    background-color: transparent;
    border: none;
}

nav#releasesNav {
    padding: 0;
}

nav#releasesNav h6.card-subtitle {
    display: none;
}

nav#releasesNav ul#releasesList li.nav-item a.nav-link {
    font-weight: 400;
    color: #ff6600 !important;
    margin-right: 2px;
}

nav#releasesNav ul#releasesList li.nav-item a.active,
nav#releasesNav ul#releasesList li.nav-item a.nav-link:hover {
    background-color: #2f2f2f !important;
    color: #ffcc00 !important;
    border-radius: 0;
}

div#releaseInformation h3 a {
    color: #ff6600;
}

div#releaseInformation h3 a:hover {
    color: #ffcc00;
}

div#releaseInformation p.alert-danger {
    color: #ffff99;
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
}

div#screenshotPanel {
    visibility: hidden;
}

div#screenshotPanel ol.carousel-indicators,
div#screenshotPanel div.carousel-inner,
div#screenshotPanel a.carousel-control-prev,
div#screenshotPanel a.carousel-control-next {
    visibility: visible;
}

div.container {
    color: #ffff99;
}

table#downloadsTable thead tr th {
    border-top: 1px solid #777788;
}

table#downloadsTable thead tr,
table#downloadsTable tbody tr {
    border-left: 1px solid #777788;
    border-right: 1px solid #777788;
}

table#downloadsTable thead tr th {
    color: #ffff99;
    background-color: #2f2f2f !important;
}

table#downloadsTable tbody tr td {
    color: #ffff99;
}

table#downloadsTable tbody tr td a {
    color: #ff6600;
}

table#downloadsTable tbody tr td a:hover {
    color: #ffcc00;
}

span.downloadTagPrerelease {
    color: #ffffff;
    background-color: #d9534f;
}

span.downloadTagUpgrade {
    color: #ffffff;
    background-color: #ff6600;
}

table#downloadsTable tbody tr:nth-child(2n+1) td {
    background-color: #000000;
}

table#downloadsTable tbody tr:nth-child(2n) td {
    background-color: #2f2f2f;
}

table#downloadsTable tbody tr:hover td {
    background-color: #575555;
}

table#downloadsTable tbody tr:last-child {
    border-bottom: 1px solid #777788;
}

div.vanilla-credit {
    border-top: 1px solid #777788;
}

div.vanilla-credit a.vanilla-anchor {
    color: #ff6600;
}

div.vanilla-credit a.vanilla-anchor:hover {
    color: #ffcc00;
}

div.vanilla-credit a.vanilla-anchor span.vanilla-logo {
    filter: invert(1);
}

div.form-group input {
    color: #ffffff;
    background-color: #000000;
    border: 1px solid #777788;
    border-radius: 0;
    box-shadow: none !important;
}
}

@-moz-document url("https://winworldpc.com/about") {
div.container.mt-2 p:nth-of-type(5)::after {
    float: right;
    content: url('https://i.imgur.com/jp27Agi.png');
}

div.container.mt-2 p:nth-of-type(7)::after {
    float: right;
    padding-left: 76.8%;
    content: 'WinWorld is Majestically Powered';
    font-style: italic;
    font-weight: bold;
    color: #ffcc00;
    text-shadow: 0.1em 0.1em 0.3em #000000;
}
}

@-moz-document url-prefix("https://winworldpc.com/screenshot") {
div.container h1 a {
    color: #ff9900;
}

div.container h1 a:hover {
    color: #ffcc00;
}

div.col-sm a img {
    visibility: visible;
    background-color: transparent;
    border: none;
}

figure.figure figcaption.figure-caption {
    color: #ffff99;
}

div.btn-group a {
    color: #ffffff;
    box-shadow: none !important;
}

div.btn-group a:not(:disabled):not(.disabled):active {
    background-color: #181717;
    border: 1px solid #5A5A67;
}
}

@-moz-document url-prefix("https://forum.winworldpc.com/discussion/embed") {
div#Content div.Embed h2 {
    color: #ffcc00;
