    border: 1px solid #777788;
    border-bottom: none;
    padding: 10px;
    margin-bottom: 0;
}

div.MessageForm {
    border: 1px solid #777788;
    border-top: none;
}

div.MessageForm div.FormWrapper {
    background-color: rgba(255, 255, 255, 0.04);
}

div.MessageForm div.FormWrapper form#Form_Comment div span div.bodybox-wrap div.TextBoxWrapper textarea#Form_Body,
div.Form-Body div.FormWrapper form div div.bodybox-wrap div.TextBoxWrapper textarea.InputBox {
