    background-color: #2f2f2f;
    border: 1px solid #777788;
}

div.Buttons div.Author span.SignOutWrap a {
    color: #ff6600;
}

div.Buttons div.Author span.SignOutWrap a:hover {
    color: #ffcc00;
}

a.Button,
input.Button {
    color: #ffffff;
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
}

a.Button:hover,
input.Button:hover {
    color: #ffffff;
    background-color: #181717;
    border: 1px solid #5A5A67;
}

div.Foot.Closed {
    background-color: #181717 !important;
    border-top: 1px solid #777788;
    border-bottom: 1px solid #777788;
}

div.Foot.Closed div.Note.Closed {
    color: #ffcc00;
}

ul.Comments li.ItemComment {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-top: none;
}

ul.Comments li.ItemComment div.Comment div.CommentHeader div.AuthorWrap span.Author a.Username,
ul.Comments li.ItemComment div.Comment div.CommentHeader div.CommentMeta span.DateCreated a.Permalink time,
ul.Comments li.ItemComment div.CommentHeader div.CommentMeta span.CommentFlag a,
ul.Comments li.ItemComment div.Comment div.Item-BodyWrap div.Item-Body div.Message a,
div#Content div.Embed div.Foot a {
    color: #ff6600;
}

ul.Comments li.ItemComment div.Comment div.CommentHeader div.AuthorWrap span.Author a.Username:hover,
ul.Comments li.ItemComment div.Comment div.CommentHeader div.CommentMeta span.DateCreated a.Permalink time:hover,
ul.Comments li.ItemComment div.CommentHeader div.CommentMeta span.CommentFlag a:hover,
ul.Comments li.ItemComment div.Comment div.Item-BodyWrap div.Item-Body div.Message a:hover,
div#Content div.Embed div.Foot a:hover {
    color: #ffcc00;
}

li.ItemComment div.Comment div.Options span.OptionsMenu span.OptionsTitle {
    filter: invert(1);
}

li.ItemComment div.Comment div.Options span.OptionsMenu ul.Flyout {
    background-color: #2f2f2f;
    border: 1px solid #777788;
    border-radius: 0;
}

li.ItemComment div.Comment div.Options span.OptionsMenu ul.Flyout:before {
    border-bottom: 7px solid #777788;
}

li.ItemComment div.Comment div.Options span.OptionsMenu ul.Flyout:after {
    border-bottom: 7px solid #2f2f2f;
}

li.ItemComment div.Comment div.Options span.OptionsMenu ul.Flyout li a {
    color: #ffff99 !important;
}

li.ItemComment div.Comment div.Options span.OptionsMenu ul.Flyout li a:hover {
    color: #ffcc00 !important;
    background-color: #575555 !important;
}

ul.Comments li.ItemComment div.Comment div.Item-BodyWrap div.Item-Body div.Message {
    color: #ffffff;
}

blockquote.UserQuote,
div.Quote {
    background: rgba(0, 0, 0, 0.2);
    border-left: 4px solid rgba(0, 0, 0, 0.15);
}

.codeBlock,
code,
pre {
    background-color: #262626;
    border: 1px solid #777788;
}

div.editor-file-preview.file-readonly {
    background-color: #181717;
    border: 1px solid #777788;
    border-radius: 0;
}

div.editor-file-preview.file-readonly div.file-data a.filename {
    color: #ffffff;
}

div.Form-Body {
    border-top: 1px solid #777788;
}

div#Content div.Embed div.Foot {
    background-color: #000000;
    border-left: 1px solid #777788;
    border-right: 1px solid #777788;
    padding-bottom: 3px;
    padding-right: 3px;
}
}

