css:
@-moz-document url-prefix("https://wikiwiki.jp/") {
.container-wrapper {
    background-color: #121a42;
}

body {
    color: white;
}

.title2 {
    color: white;
}

h1.title {
    background-color: #4f7194;
    color: #000;
}

h2,h3,h4,h5,h6 {
    background-color: #4f7194;
    color: #000;
}

h2 a:link {
    color: #0400ff;
}

h3 a:link {
    color: #0400ff;
}

h4 a:link {
    color: #0400ff;
}

h5 a:link {
    color: #0400ff;
}

h6 a:link {
    color: #0400ff;
}


h3 {
    border-bottom-color: #0b164e;
    border-top-color: #0b164e;
    border-left-color: #0b164e;
    border-right-color: #0b164e;
}

h4 {
    border-left-color: #0b164e;
}

a:link {
    color: #5b89d8;
}

div#topicpath {
    color: white;
}

input[type="text"] {
    border-color: #3d4a8a;
    background-color: #0b0f25;
}

.pcommentform {
    border-color: #4f7194;
}

.list2 {
    border-left-color: #4f7194;
}

table th {
    background-color: #333b61;
    border-color: dimgrey;
    color: white;
}

table td {
    background-color: #647dff;
    border-color: dimgrey;
    color: black;
}

table thead th, table tfoot th {
    background-color: #254361;
}

td a:link {
    color: #0400ff;
}

.wikicolor :not(td) {
    color: #ecd109 !important;
}

p .wikicolor {
	color: #ecd109 !important;
}

.pcmt {
    border-top-color: #aaaaaa;
}

img#logo {
    filter: invert(1) hue-rotate(180deg);
}
}
