/* ==UserStyle==
@name           unshort.link - gray
@namespace      github.com/openstyles/stylus
@version        1.5.0
@description    Gray for unshort.link
@author         Thorsten Willert
==/UserStyle== */
@-moz-document domain("unshort.link") {

    * {
        font-family: Tahoma, Arial, Helvetica !important;
    }

    body {
        background: #bdc3c7;
        background: -webkit-linear-gradient(to bottom, #333, #bbb);
        background: linear-gradient(to bottom, #333, #bbb);
    }

    #buttonRedirect {
        filter: invert();
        font-weight: bold;
    }

    .logo,
    #buttonRedirect,
    #unshortDirectButton,
    #headerLinks,
    a:hover {
        filter: grayscale(100%);
    }
    
     .btn-primary:focus, .btn-primary:active,
    #redirectLinkTextArea{
        background-color: rgba(255,255,255,0.5) !important;
        border: 1px solid rgba(255,255,255,0.9) !important;
    }
}
