css:
@-moz-document domain('youtube.com')
{

ytd-app {
    --app-drawer-width: 240px;
    --app-drawer-content-container_-_background-color: #000000;
    background: url(https://i.postimg.cc/B6qFqCkp/1.jpg)fixed #000!important;
    background-size: cover!important;
    display: block;
    left: 0;
    min-height: 100%;
    position: absolute;
    right: 0;
    top: 0;
}

ytd-video-meta-block[rich-meta] #channel-name.ytd-video-meta-block, ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block {
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 1.8rem;
    color: black;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:visited {
    color: black;
}

ytd-video-meta-block, .ytd-video-meta-block[meta-block] {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    background: white;
    border-radius: 7px;
    padding-left: 5px;
}

a.yt-formatted-string {
    color: black;
}

#details.ytd-rich-grid-video-renderer {
    position: relative;
    cursor: pointer;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    background: white;
    padding-left: 4px;
    border-radius: 11px;
}

ytd-mini-guide-renderer.ytd-app {
    position: fixed;
    left: 0;
    top: var(--ytd-masthead-height, var(--ytd-toolbar-height));
    bottom: 0;
    background: #08080800;
}

ytd-mini-guide-entry-renderer {
    display: inline-block;
    border-radius: 8px;
    background-color: #ffffff;
}

.text-wrapper.ytd-video-renderer {
    max-width: 600px;
    min-width: 0;
    display: inline-block;
    cursor: pointer;
    text-decoration: none;
    color: var(--yt-endpoint-color, var(--yt-spec-text-primary));
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    background: white;
    padding-left: 8px;
    border-radius: 8px;
}

#contents.ytd-channel-list-sub-menu-renderer {
    max-width: var(--ytd-channel-list-sub-menu-max-width, 560px);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-pack: justify;
    -webkit-justify-content: space-between;
    justify-content: space-between;
    background: white;
    padding-left: 3px;
    border-radius: 30px;
}

#items.ytd-grid-renderer {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-wrap: wrap;
    -webkit-flex-wrap: wrap;
    flex-wrap: wrap;
    background: white;
    padding-left: 8px;
    border-radius: 8px;
    padding-top: 11px;
}

ytd-masthead {
    --paper-input-container-input-align: baseline;
    display: block;
    width: 100%;
    background: #00000008;
}

#items.ytd-watch-next-secondary-results-renderer {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    background: white;
    padding-left: 8px;
    border-radius: 11px;
    padding-top: 3px;
}

ytd-video-secondary-info-renderer {
    display: block;
    border-bottom: 1px solid var(--yt-spec-10-percent-layer);
    margin-bottom: 24px;
    padding-bottom: 16px;
    background: white;
    padding: 10px;
    border-radius: 8px;
}

#info.ytd-video-primary-info-renderer {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    background: white;
}

ytd-video-primary-info-renderer {
    --yt-button-icon-size: 36px;
    display: block;
    border-bottom: 1px solid var(--yt-spec-10-percent-layer);
    padding: 20px;
    background: white;
    border-radius: 8px;
}

ytd-comments-header-renderer {
    --paper-input-container-input-align: baseline;
    display: block;
    margin-top: var(--comments-header-renderer-margin-top, 24px);
    margin-bottom: var(--comments-header-renderer-margin-bottom, 32px);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    background: white;
    padding: 10px;
    border-radius: 8px;
}

ytd-comment-thread-renderer {
    display: block;
    margin-bottom: var(--ytd-comment-thread-margin-bottom, 16px);
    background: white;
    padding-left: 10px;
    padding-top: 5px;
    border-radius: 8px;
}

.html5-play-progress,
.ytp-play-progress,.ytp-clip-start-exclude {
/*Полоса*/
    background:  url("https://i.postimg.cc/JnLq6fLJ/image.jpg") !important;
}
/*Полоса*/
.html5-load-progress,
.ytp-load-progress {
    background:  url("https://i.postimg.cc/JnLq6fLJ/image.jpg") !important;
    opacity:0.89;
}
/*гоку*/
.html5-scrubber-button,
.ytp-scrubber-button {
    background: url("https://i.postimg.cc/PrM1TSwb/38x38-ZJFD-1.gif") !important;
    width: 39px !important;
    height:38px !important;
    border: none !important;
    margin-left: -17px !important;
    margin-top: -28.5px !important;
    transform: scale(0.8)!important
;
    -webkit-transform: scale(0.8)!important
;
    -moz-transform: scale(0.8)!important
;
    -ms-transform: scale(0.8)!important
;
    background-repeat:no-repeat!important;
}

.html5-progress-bar-container,
.ytp-progress-bar-container {
    height: 6px !important;
}

.html5-progress-bar,
.ytp-progress-bar {
    margin-top: 12px !important;
}

.html5-progress-list,
.ytp-progress-list,
.video-ads .html5-progress-list.html5-ad-progress-list,
.video-ads .ytp-progress-list.ytp-ad-progress-list {
    height: 6px !important;
}
/*гоку*/
.html5-scrubber-button:hover, .ytp-chrome-controls .ytp-button[aria-pressed]::after, .ytp-scrubber-button:hover {
    background: url("https://i.postimg.cc/PrM1TSwb/38x38-ZJFD-1.gif") !important;    
}

.ytp-clip-start-exclude,.ytp-clip-end-exclude:after{
    content:url("https://i.postimg.cc/JnLq6fLJ/image.jpg")
}

.ytp-clip-start-exclude,.ytp-clip-end-exclude:hover{
    content:url("https://i.postimg.cc/JnLq6fLJ/image.jpg")
}

.ytp-chapter-hover-container.ytp-exp-chapter-hover-container{
    height:6px!important;
}

}
