css:
@-moz-document domain("scarlet-clicks.info") {
.c, .w, #cpmbanner61, #cpmbanner68, #cpmbanner67 {
    display: none !important;
    width: 10px !important;
}
body, .ad-title {
    background: #630318;
    border-color: #630318;
}
.widget-content, .menubarlist li ul li, .header-top {
    background: #E0B8C0;
}

iframe{
    filter: grayscale(1.0) opacity(0.5);
}
}

