css:
@-moz-document url(https://invidious.snopyta.org/) {
/*variables*/
:root {
    --hoverduration: 0.2s;
    --unimportantgrey: #a0a0a0;
}

/*add left border on video description*/
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.pure-g > div.pure-u-1.pure-u-lg-3-5:nth-of-type(2) > div.h-box > div:first-of-type {
    border-left: 1px solid var(--unimportantgrey);
    padding-left: 10px;
    margin-bottom: 30px;
}
/*transitions on subscribe buttons*/
button#subscribe:hover , button#subscribe {
    transition: all 0.15s;
}

/* increase size of video player*/
#player-container {
    padding-left:0px;
    padding-right: 0px;
}
/* change layout of items below video */
#dislikes {
    color: #bc0000;
}
#likes {
    color: green;
}
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.pure-g > div.pure-u-1.pure-u-lg-1-5 > div.h-box > p:last-of-type {
    border-right: 0px !important;
}
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.pure-g > div.pure-u-1.pure-u-lg-1-5 > div.h-box > p:not(:first-of-type) {
    color: var(--unimportantgrey);
    padding: 10px;
    border-right: 1px solid #a0a0a0;
}
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.pure-g > div.pure-u-1.pure-u-lg-1-5:first-of-type > div.h-box {
    display: flex;
    flex-wrap: wrap;
}
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.h-box > h1 {
    padding-bottom: 15px;
    margin-bottom: 30px;
    border-bottom: 1px solid;
}
/*reduce promenence of download button and options*/
form.pure-form.pure-form-stacked[action^="/latest_version"] button.pure-button.pure-button-primary {
    transition: all var(--hoverduration) !important;
}
/*style annotations and watch on youtube button*/
p#annotations a[href^="/watch"],
span#watch-on-youtube a[href^="https://www.youtube.com/"] {
    white-space: nowrap;
    margin-top: 25px !important;
    border: 1px solid;
    border-radius: 5px;
    padding: 10px;
    transition: all var(--hoverduration) !important;
}
span#watch-on-youtube,
p#annotations {
    margin: 20px 10px 10px 0px;
}
/*style watch on youtube button  on hover*/
span#watch-on-youtube a[href^="https://www.youtube.com/"]:hover,
span#watch-on-youtube a[href^="https://www.youtube.com/"]:focus {
    background-color: red;
    color: white;
    transition: all var(--hoverduration) !important;
}
/* audio and video icons on watch pages */
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.h-box > h1 > a[title="Audio mode"][href^="/watch"],
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.h-box > h1 > a[title="Video mode"][href^="/watch"] {
    margin-left: 10px;
    border: 1px solid;
    border-radius: 5px;
    padding: 0px 10px 0px 10px;
    color: var(--unimportantgrey) !important;
    position: relative;
    float: inline-end;
    transition: all var(--hoverduration) !important;
}
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.h-box > h1 > a[title="Audio mode"][href^="/watch"]:hover,
body > div.pure-g > div.pure-u-1.pure-u-md-20-24 > div.h-box > h1 > a[title="Video mode"][href^="/watch"]:hover {
    color: rgb(0, 182, 240) !important;
    transition: color var(--hoverduration) !important;
}

/* this is for the search bar but I couldnt get it working so I'm just leaving it here for the selectors
div.navbar > div.pure-u-1.pure-u-md-12-24.searchbar > form.pure-form[action="/search"] > fieldset > input[type="search"][name="q"][placeholder="search"] {


}
div.navbar > div.pure-u-1.pure-u-md-12-24.searchbar > form.pure-form[action="/search"] > fieldset > input[type="search"][name="q"][placeholder="search"]:hover {
    border: 1px solid;
    border-radius: 5px;
    transition: border 1s;
}
*/
/*prettify user action icons*/
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-8-24.user-field > div > a,
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-8-24.user-field > div > form > a > input[value="Log out"] {
    transition: color var(--hoverduration) !important;
    border: 1px solid !important;
    padding: 8px !important;
    border-radius: 5px !important;
    color: var(--unimportantgrey) !important;
}
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-8-24.user-field > div > a:hover,
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-8-24.user-field > div > form > a > input[value="Log out"]:hover {
    color: rgb(0, 182, 240) !important;
    transition: color var(--hoverduration) !important;
}
/*prettify INVIDIOUS icon*/
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-4-24 > a.index-link.pure-menu-heading[href="/"] {
    color: rgb(0, 182, 240);
    border: 1px solid rgb(0, 182, 240);
    border-radius: 5px;
    padding: 10px;
    transition: all var(--hoverduration) !important;
}
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-4-24 > a.index-link.pure-menu-heading[href^="/"]:hover,
div.navbar.pure-g.h-box > div.pure-u-1.pure-u-md-4-24 > a.index-link.pure-menu-heading[href^="/"]:focus {
    transition: all var(--hoverduration) !important;
    color: white !important;
    background-color: rgb(0, 182, 240);
    border: 1px solid white;
    box-shadow: 1px 1px 11px white;
}
/*reduce emphasis on channels in video boxes*/
div.pure-u-md-1-4 div.h-box > p > b > a[href^="/channel/"]:nth-of-type(1) {
    font-weight: normal !important;
}
div.pure-u-md-1-4 div.h-box > p:nth-of-type(2):not(a):not(.length) {
    border-top: 1px solid;
    padding-top: 15px;
    margin-top: 15px;
}
/*only show length when (any part of the thumbnail) is hovered on*/
div.pure-u-md-1-4 div.h-box a div.thumbnail p.length:not(:hover) {
    color: rgba(255, 255, 255, 0);
    background-color: rgba(0, 0, 0, 0);
}
div.pure-u-md-1-4 div.h-box a div.thumbnail p.length {
    width: 100% !important;
    height: 100%;
    right: 0;
    left: 0;
    top: 0;
    bottom: 0;
    margin: 0px !important;
    color: white;
    font-weight: bold;
}
/* add padding for whole box instead of individual elements */
div.pure-u-md-1-4 > div.h-box {
    padding: 15px !important;
}
/* fix alignment and color of video title */
div.pure-u-md-1-4 > div.h-box > p > a[href^="/watch"] {
    font-weight: bold;
    color: white;
    position: initial !important;
    width: 100%;
    height: 100%;
}
div.pure-u-md-1-4 > div.h-box > p > a[href^="/watch"]:hover {
    color: rgb(0, 182, 240);
}
/*decrease emphasis on channel descriptions, subscriber and video count, date shared, and views*/
div.pure-u-md-1-4 > div.h-box > *:nth-child(2),
div.pure-u-md-1-4 > div.h-box > *:nth-child(3),
div.pure-u-md-1-4 > div.h-box > *:nth-child(4) {
    color: var(--unimportantgrey);
}
/* add border and fix padding/margin on share date and views*/
div.pure-u-md-1-4 > div.h-box > h5.pure-g {
    border-top: 1px solid;
    padding: 15px 0px 0px 0px;
    margin: 15px 0px 0px 0px !important;
}
/* add left border to channel description for clarification */
div.pure-u-md-1-4 > div.h-box > h5:not(.pure-g) {
    border-left: 1px solid/*rgb(0, 106, 140)*/;
    padding: 5px 8px 5px 8px;
    margin-left: 0px !important;
}
/*
idk why tf this is here but it really fucks shit up
center, hr
  {
    display: none;
  }
*
a:hover, a :hover {
    text-decoration: highlight;
    background-color: red !important;
}*/
.pure-g:not(.h-box) h3 {
    margin: 20px 0 10px;
}

.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box {
    border-radius: 4.5px;
    background: #363636;
    padding: 8px;
    margin: 5px;
    box-shadow: 0px 1px 20px rgba(0, 0, 0, 0.3);
    transition: all var(--hoverduration);
}

.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box:hover {
    background: #222;
    transform: scale(1.035);
    transition: all var(--hoverduration) !important;
    box-shadow: 0px 0px 15px rgba(0, 182, 240, .5);
    z-index: 99999;
}
/*channel link container*/
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box p a[href^="/channel/"],
.pure-u-md-1-4 .h-box a[href^="/channel/"] p , #channel-name {
    font-weight: bold;
    background: #000;
    color: white;
    padding: 3px 6px;
    border-radius: 20px;
    transition: all var(--hoverduration) !important;
}
/*channel link container on hover*/
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box p a[href^="/channel/"]:hover,
.pure-u-md-1-4 .h-box a[href^="/channel/"] p:hover , #channel-name:hover {
    transition: all var(--hoverduration) !important;
    background: rgb(0, 182, 240);
}

.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box p > a[href^="/watch"] {
    position: relative;
    left: 7px;
}

.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box > p {
    margin: 8px 0px;
}

.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box h5 {
    margin: 0px 8px 5px;
}
}
