css:
@-moz-document url("https://www.torrentbd.net"), url("https://www.torrentbd.me"), url-prefix("https://www.torrentbd") {
:root {
    --body-bg: #111;
    --body-color: #bbb;
    --modal-color: #c1cdd2;
    --main-bg: #222;
    --nav-bg: rgba(0, 0, 0, 0.85);
    --nav-alt-bg: #f33;
    --border-color: #333;
    --link-color: #fff;
    --link-hover-color: #f33;
    --link-sp1-color: #bbb;
    --link-sp1-hover-color: #f33;
    --link-sp2-color: #c33;
    --link-sp3-color: #f33;
    --btn-1-color: #f33;
    --cmodal-dark-bg: #111;
}
.tamperhead {
    position:sticky;
    top:-1px;
    z-index:9999;
}
.new-torrent-tag {
    background: #f33!important;
}
.new-torrent-tag:hover {
    background: #f11!important;
}
.pagination li {
    line-height: 20px;
}
.pagination li.active {
    color: #111!important;
}
.z-depth-2 {
    box-shadow: 0 0 0 0;
}
#user-sb:hover {
    color: #fff;
}
.blue-grey.darken-2 {
    background-color: #000 !important;
}
.circle {
    border-radius: 2% !important;
    margin-top: 15px !important;
    max-width: 60% !important;
}
.inline-submit-btn {
    color: #fff;
}
.inline-submit-btn:hover {
    color: #ccc;
}
#tampersmile {
    color: #f33;
}
#tampersmile:hover {
    color: #f22;
}
.shout-label.active {
    color: #f33;
}
.green.darken-1 {
    background-color: #f33 !important;
}
.btn-floating.btn-large {
    height: 40px;
    width: 40px;
}
.btn-floating {
    border-radius: 20%;
}
.btn-floating.btn-large i {
    line-height: 40px;
}
#kuddus-trigger {
    height: 40px;
    width: 40px;
    margin-top: 7px;
    border: 3.5px solid #999;
    background-color: #111;
}
#kuddus-trigger i {
    text-shadow: 2px 3px #222;
}
#kuddus-trigger-handle {
    display: none;
}
#kuddus-trigger-container {
    top: 3px;
    right: -7px;
}
.material-icons.orange600 {
    color: #f33;
}
#kuddus-wrapper {
    background: rgba(0, 0, 0, 0.95);
}
.kuddus-title-bar {
    background: #111;
}
#kuddus-close {
    background: #f33;
}
#kuddus-searchtype {
    background: #222;
}
.forum-page-title {
    background: rgba(0, 0, 0, 0.7);
}
.crumb-container, .crumb-container a {
    color: #fff !important;
}
.crumb-container a:hover {
    color: #f33 !important;
}
.pg-intro {
    color: #fff;
}
.red100 {
    color: #f33;
}
body.dark-scheme .trgbtn {
    border-color: #f33 !important;
    color: #f33 !important;
}
body.dark-scheme .instr-trg {
    border-color: #f33 !important;
}
.instr-trg {
    border: 1px solid #f33 !important;
}
.btn:hover, .btn-large:hover {
    background-color: #c22;
    color: #fff;
}
.teal.darken-3 {
    background-color: #060 !important;
}
.blue.darken-2 {
    background-color: #06c !important;
}
.card .card-action a:hover {
    color: #f33;
}
.switch label input[type="checkbox"]:checked + .lever {
    background-color: #533;
}
.switch label input[type="checkbox"]:checked + .lever::after {
    background-color: #c33;
}
.bbc-btn, .bbc-option {
    border: 1px solid #f33 !important;
    color: #f33;
}
.bbc-btn:hover {
    background: #f33;
}
option {
    background: #111 !important;
    color: #f33;
}
button:focus {
    background-color: #a33;
}
.scrollToTop:hover {
    opacity: 1.0;
}
.teal.lighten-2 {
    background-color: #88f !important;
}
.teal.lighten-2:hover {
    background-color: #77f !important;
}
.options .btn-floating i {
    color: #111 !important;
}
.orange.lighten-2 {
    background-color: #fc6 !important;
}
.orange.lighten-2:hover {
    background-color: #c93 !important;
}
body.dark-scheme .cnav-menu-item > a:hover, body.dark-scheme .cnav-menu-item > span:hover {
    background-color: #f33;
}
.section-description, .updated-by-text {
    color: #f33 !important;
}
.forum-repu {
    background: #f33 !important;
}
.white-text {
    color: #111 !important;
}
.mrsr {
    color: #f33 !important;
}
.tampertopsl-btn {
    color: #fff;
    border: 1px solid #f33;
    box-shadow: 0 2px 5px 0 #f332, 0 2px 10px 0 #f332;
    background: none;
    margin-right: 5px;
    margin-top: 6px;
}
input[type="text"]:focus:not([readonly]) + label, input[type="password"]:focus:not([readonly]) + label, input[type="email"]:focus:not([readonly]) + label, input[type="url"]:focus:not([readonly]) + label, input[type="time"]:focus:not([readonly]) + label, input[type="date"]:focus:not([readonly]) + label, input[type="datetime-local"]:focus:not([readonly]) + label, input[type="tel"]:focus:not([readonly]) + label, input[type="number"]:focus:not([readonly]) + label, input[type="search"]:focus:not([readonly]) + label, textarea.materialize-textarea:focus:not([readonly]) + label {
    color: #f33;
}
input[type="text"]:focus:not([readonly]), input[type="password"]:focus:not([readonly]), input[type="email"]:focus:not([readonly]), input[type="url"]:focus:not([readonly]), input[type="time"]:focus:not([readonly]), input[type="date"]:focus:not([readonly]), input[type="datetime-local"]:focus:not([readonly]), input[type="tel"]:focus:not([readonly]), input[type="number"]:focus:not([readonly]), input[type="search"]:focus:not([readonly]), textarea.materialize-textarea:focus:not([readonly]) {
    border-bottom: 1px solid #f33;
    box-shadow: 0 1px 0 0 #f33;
}
[type="checkbox"].filled-in:checked + label::after {
    border: 2px solid #f33;
    background-color: #f33;
}
.red.lighten-2 {
    background-color: #f55 !important;
}
.red.lighten-2:hover {
    background-color: #f33 !important;
}
.tamperfltrsb-btn {
    background: #f33 !important;
}
body.dark-scheme {
    background: #111 !important;
}
body.dark-scheme .subject-header {
    color: #f33;
}
body.dark-scheme .bg-warning {
    background-color: #222;
}
body.dark-scheme hr {
    border: 1px solid #444;
}
body.dark-scheme .bg-success {
    background-color: #333;
}
body.dark-scheme .form-control, body.dark-scheme .list-group-item, body.dark-scheme select, body.dark-scheme .dropdown-menu {
    background: none;
    border-color: #111;
}
.form-control:focus {
    box-shadow: inset 0 1px 1px #000d, 0 0 8px #000d;
}
.label-success {
    color: #000;
}
.btn-info {
    background-color: #f33;
    border-color: #f33;
}
.btn-info:hover {
    border-color: #c22;
}
.btn-warning {
    color: #000;
    font-weight: 700;
}
.btn-warning:hover {
    color: #000;
}
.btn-success {
    color: #000;
    font-weight: 700;
}
.btn-success:hover {
    color: #000;
    background: #398439 !important;
}
[type="checkbox"]:checked + label::before {
    border-right: 2px solid #f33;
    border-bottom: 2px solid #f33;
}
table.striped > tbody > tr:nth-child(2n+1) {
    background-color: #333;
}
.ttcp .tclabel {
    color: #f33;
}
#kuddus-more-categories {
    color: #f33;
    border: 1px solid #f33;
}
.tclabel:hover {
    color: #f33;
}
.cmodal {
    background: rgba(0, 0, 0, 0.95);
}
.cmodal-header {
    background: #111;
}
.forum-cat-heading {
    background: #000 !important;
}
.btn-links {
    border: 2px solid #d33 !important;
}
.btn-links:hover {
    background: #f33 !important;
    color: white;
}
.tabs .tab a {
    color: #fff !important;
}
.tabs .tab a:hover {
    color: #ccc !important;
}
.tabs .indicator {
    background-color: #fff !important;
}
.tab-sortable {
    color: #f33;
}
.tab-sortable:hover {
    color: #c33;
}
.side-nav li:hover, .side-nav li:active {
    background: #f33;
}
.side-nav li.active {
    background: #c33;
}
.btn-clear {
    border: 1px solid #f33 !important;
    color: #f33 !important;
}
.btn-clear:hover, .btn-clear:focus {
    color: #fff !important;
    background: #f33 !important;
}
.tradiopill[type="radio"] + label {
    border: 1px solid #f33;
}
.tradiopill[type="radio"]:checked + label {
    background: #f33;
}
#kuddus-results-container a {
    color: var(--link-sp1-color);
}
#kuddus-results-container a:hover {
    color: var(--link-sp1-hover-color);
}
.border-teal {
    border: 1px solid #f33 !important;
}
.short-links {
    background: #833 !important;
}
.short-link-counter {
    background: #f33 !important;
}
.sub-h6 {
    color: #eee !important;
    background: #833 !important;
    border: 1px solid #f33 !important;
}
.tp-progress {
    background: #f33 !important;
}
.icon-input-box input {
    background: #633 !important;
}
.icon-input-box i {
    background: #b33 !important;
}
.main-header, .sub-header {
    background: #000 !important;
}
.main-header--icon, .sub-header--title {
    color: #fff !important;
}
.icon-input-box input[type="text"]:focus {
    border-color: #f33 !important;
    box-shadow: 0 0 0 2px #f338 !important;
}
.picker__date-display {
    background-color: #f33;
}
.picker__weekday-display {
    background-color: #833;
}
.picker__close, .picker__today {
    color: #f33;
}
.picker__header {
    margin-bottom: .75em;
}
.picker__box {
    background: #222;
    border: 1px solid #111;
}
.picker__day.picker__day--today {
    color: #f33;
}
button.picker__today:focus, button.picker__clear:focus, button.picker__close:focus {
    background-color: #633;
}
.picker__nav--prev, .picker__nav--next {
    margin-top: .25em;
}
.picker__nav--prev:hover, .picker__nav--next:hover {
    background: #0000;
}
.picker__nav--prev:hover::before {
    border-right: 0.75em solid #f33;
}
.picker__nav--next:hover::before {
    border-left: 0.75em solid #f33;
}
.select-field-custom {
    background: #633 !important;
}
.select-field-custom::after {
    background: #b33 !important;
}
.select-field-custom:hover::after {
    color: #fff !important;
}
[type="radio"].with-gap:checked + label::before {
    border: 2px solid #f33 !important;
}
[type="radio"].with-gap:checked + label::after {
    border: 2px solid #f33 !important;
    background-color: #f33 !important;
}
.friend-card {
    background: #3225;
    border: 1px solid #b33 !important;
}
.light-blue.darken-3 {
    background-color: #f33 !important;
}
.picker__select--month, .picker__select--year {
    background-color: #111 !important;
}
.friend-card--delete {
    color: #f33 !important;
}
.shoutbox-text {
    color: #f33;
}
.progress {
    background-color: #622;
}
.progress .determinate {
    background-color: #f33;
}
.pr-action-container .btn-floating {
    background: var(--btn-1-color) !important;
}
.pr-action-container .btn-floating:hover {
    background: #c22 !important;
}
#msg-search {
    border-bottom: 1px solid #f33 !important;
}
.listitem {
    background: #222 !important;
    border-bottom: 1px solid #111 !important;
}
.listitem:hover {
    background: #333 !important;
}
.message-body {
    background: #222 !important;
}
.message-subject, .backBtn {
    color: #f33 !important;
}
.replyBtn {
    background: #f33 !important;
}
#message-thread-delete-btn {
    background-color: #822 !important;
    border: 2px solid #c22;
}
#message-thread-delete-btn:hover {
    background-color: #f33 !important;
}
.teal.darken-1 {
    background-color: #f33 !important;
}
.tradiopill.ell[type="radio"] + label {
    border-right: 1px solid #f33;
}
.gratitude legend, .stats {
    color: #f33;
}
.tgaction {
    color: #f33;
    border: 1px solid #f33;
}
.repu {
    background: #f33;
}
.topsl-btn {
    border:1px solid #f33 !important;
}
.news-content h5 {
    color: #f33 !important;
}
.cnp-container {
    background: #622;
}
.cnp-progress {
    background: #f33;
}
.ft-lock-indicator {
    border: 2px solid #f33;
    color: #f33;
}
.fixed-pos {
    top: 58px;
}
}
