/* ==UserStyle==
@name           unshort.link - carbon black
@namespace      github.com/openstyles/stylus
@version        1.5.0
@description    A carbon-black style for unshor.-link
@author         Thorsten Willert
==/UserStyle== */
@-moz-document domain("unshort.link") {

    * {
        font-family: Tahoma, Arial, Helvetica !important;
    }

    body {
        background-color: rgb(32, 32, 32);
        background-image: linear-gradient(to right, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0) 20%, rgba(0, 0, 0, 0) 80%, rgba(0, 0, 0, 1)), linear-gradient(45deg, black 25%, transparent 25%, transparent 75%, black 75%, black), linear-gradient(45deg, black 25%, transparent 25%, transparent 75%, black 75%, black), linear-gradient(to bottom, rgb(8, 8, 8), rgb(32, 32, 32));
        background-size: 100% 100%, 10px 10px, 10px 10px, 10px 5px;
        background-position: 0px 0px, 0px 0px, 5px 5px, 0px 0px;
    }

    #buttonRedirect {
        filter: invert();
        font-weight: bold;
    }

    a:hover {
        color: #fd0;
    }
    
    #unshortDirectButton {
        border-color: black !important;
    }
    
    #unshortDirectButton:hover,
    #unshortDirectButton:focus,
    #unshortDirectButton:active
    {
        border-color: #fd0 !important;
    }
    
    .logo {
        filter: grayscale();
        mix-blend-mode: difference;
    }

    #buttonRedirect,
    #unshortDirectButton,
    #headerLinks {
        color: white !important;
        background: linear-gradient(to bottom, rgba(125,126,125,0.5) 0%,rgba(14,14,14,0.5) 100%) !important;
    }
    
     .btn-primary:focus, .btn-primary:active,
    #redirectLinkTextArea{
        background-color: rgba(255,255,255,0.5) !important;
        border: 1px solid rgba(255,255,255,0.9) !important;
    }
}
