/* ==UserStyle==
@name           unshort.link - light
@namespace      github.com/openstyles/stylus
@version        1.5.0
@description    unshort.link 
@author         Thorsten Willert
==/UserStyle== */
@-moz-document domain("unshort.link") {

    * {
        font-family: Tahoma, Arial, Helvetica !important;
        color: #777 !important;
    }

    body {
        background: #fff;
    }

    .footer-links {
        filter: invert();
    }

    i[class^="icon"] {
        filter: drop-shadow(5px 5px 10px rgba(0, 0, 0, 0.3));
    }

    #buttonRedirect {

        font-weight: bold;
    }

    .logo {

        filter: grayscale() drop-shadow(5px 5px 10px rgba(0, 0, 0, 0.3));
    }
    #headerLinks {
        filter: grayscale();
    }

    #urlDirect,#redirectLinkTextArea {
        border-radius: 6px !important;
        background: #ffffff !important;
        box-shadow: inset 5px 5px 10px #d9d9d9,
        inset -5px -5px 10px #ffffff !important;
    }


    #unshortDirectButton,
    #buttonRedirect,
    #headerLinks
        {
        border: 1px solid silver;
        border-radius: 6px !important;
        background: #ffffff !important;
        box-shadow: 5px 5px 10px #d9d9d9,
        -5px -5px 10px #ffffff !important;
    }


    a:hover {
        color: #fd0 !important;
    }

    #unshortDirectButton:hover,
    #unshortDirectButton:focus,
    #unshortDirectButton:active {
        border-color: #fd0 !important;
    }
    
}
