css:
@-moz-document domain("toky.chat") {
#header, #footer, .Question_tandc_0, .visitorAvatar, .AppHeader_title_0, .AnswerInboxHeader_connect_0, .AnswerInboxHeader_tandc_0 {
    display: none;
}

#all.desktop {
    width: auto
}

#all {
    border: none;
    height: calc(100%); 
}

.MenuTop_back_0, .ChatInput_icon_0 { 
    color: #90A4AE
}

.triangle-color {
    border-color: #cf1f2e transparent transparent transparent !important
}

.main-background-color {
    background-color: #cf1f2e !important
}

.MenuTop_menus_0 .MenuTop_menu_0:nth-child(1) {
    background-color: #fff0 !important;
}
.MenuTop_menus_0 .MenuTop_menu_0.MenuTop_current_0 {
    background-color: #fff !important;
    color: #000 !important;
}

.MenuTop_menus_0 .MenuTop_menu_0:nth-child(3n) {
     background-color: #cf1f2e !important;
}

.MenuTop_menus_0 .MenuTop_menu_0.MenuTop_current_0:nth-child(3n) {
    background-color: #fff !important;
    color: #000 !important;
}

.important-text-color, .important-border-color {
    border-color: #cf1f2e !important;
    color: #cf1f2e !important;
}

.MenuTop_backText_0, .AppLoader_loader_0 { 
    color: #90A4AE;
}

* {
    font-family: Roboto;
}

.GlobalPopups_anotherVisitorAnswered_0, .GlobalPopups_infos_0 {
    color: #cf1f2e !important;
}

.ChatBubble_bubbleContainer_0.main-background-color {
    background-color: #cf1f2e !important;
    font-size: 13px
}

.AppNickname_nickname_0 {
    font-size: 13px;
    font-weight: bold;
}

.MenuTop_text_0 {
    font-family: Roboto;
    font-size: 13px;
    font-weight: bold;
}
body {
    background-color: #cf1f2e;
}

.ChatInput_icons_0 {
    padding-top: 3px;
}

.Question_container_0 {
    height: 135px;
}

.AppPopup_buttons_0 div {
    background-color: #424242
}

.Question_insideContainer_0 {
    background: url(https://i.imgur.com/y1eIoAQ.jpg);
    background-repeat: no-repeat;
    background-size: 143px 81px;
    background-position: 50% 30%;
}



.Question_text_0 {
    padding-top:105px;
    padding-right:0px;
    padding-left:0px;
    padding-bottom:10px;
}
}
