css:
@-moz-document domain("qq.com") {
.app_content {
    max-width: unset !important;
}
.app_content .readerTopBar {
    position: fixed;
    bottom: 0;
    right: 0;
    z-index: 91;
    border-top: 1px solid rgba(255, 255, 255, 0.4);
    border-left: 1px solid rgba(255, 255, 255, 0.4);
    border-top-left-radius: 5px;
    /*     transition:all .1s ease-in-out; */
    transition: unset;
}
.wr_whiteTheme .app_content .readerTopBar {
    border-top: 1px solid rgba(0, 0, 0, 0.4);
    border-left: 1px solid rgba(0, 0, 0, 0.4);
}
.app_content .readerChapterContent {
    margin-left: 20px;
    margin-right: 20px;
}
.app_content .readerChapterContent .renderTargetContainer .wr_canvasContainer {
    width: 100% !important
}
.readerControls .download {
    display: none
}
#routerView {
    position: relative
}
#routerView .readerControls {
    margin-left: unset;
    left: unset;
    right: 120px;
    bottom: 13px;
    z-index: 91;
    display: flex;
    flex-direction: row;
    width: unset;
    overflow: hidden;
}
.readerTopBar_actions {
    position: absolute;
    right: 80px;
    width: 48px;
    overflow: hidden;
    transition: all 0.1s linear;
}
.readerTopBar_actions a {
    transition: all 0.1s linear;
}
#routerView .readerNotePanel, 
#routerView .readerCatalog {
    left: unset;
    right: 0px;
    height: 80%;
    top: unset;
    bottom: 71px;
    z-index: 92;
    max-width: 1000px;
    width: 100%;
    border-top: 1px solid rgba(255, 255, 255, 0.4);
    border-left: 1px solid rgba(255, 255, 255, 0.4);
    border-top-left-radius: 10px;
    box-shadow: unset;
}
.wr_whiteTheme #routerView .readerNotePanel,
.wr_whiteTheme #routerView .readerCatalog{
    right: 0px;    
    border-left: 1px solid rgba(0, 0, 0, 0.4);
    border-top: 1px solid rgba(0, 0, 0, 0.4);
}
.readerControls_fontSize,
.readerControls .readerControls_item {
    border-radius: unset;
    margin-top: 0px !important;
    background: none;
    box-shadow: unset !important;
}
.wr_whiteTheme .readerControls_fontSize,
.wr_whiteTheme .readerControls_item {
    background: none;
}
.readerTopBar .readerTopBar_title {
    display: flex;
    flex-direction: column;
    height: 60px;
    justify-content: center
}
.readerTopBar .readerTopBar_title .readerTopBar_title_link {
    font-size: 20px;
    margin: 8px 0;
}
.readerTopBar .readerTopBar_title .readerTopBar_title_chapter {
    font-size: 12px
}
/* .readerTopBar_right .menu_container {
    display: none
} */
.readerTopBar_right {
    width: 48px !important;
}
#readerReviewDetailPanel{
    padding: 24px;
    bottom: 90px;
    z-index: 94;
    position: fixed;
    right: -1px;
    border: 1px solid black;
    width: calc(100% - 48px);
    border-radius: unset;
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px
}
.wr_whiteTheme #readerReviewDetailPanel{
    background-color: #fff !important;
    border: 1px solid rgba(0, 0, 0, 0.4);
}
#readerReviewDetailPanel .content{
    color: black
}
.readerWriteReviewPanel {
    bottom: 70px;
    z-index: 91;
    right: 1px;
    padding: 0;
}
.readerWriteReviewPanel_bg {
    width: calc(100% + 3px);
    border-radius: 0 !important;
    background-color: rgb(31, 32, 34) !important;
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: 0;
    border-top-left-radius: 10px !important;
}

.wr_whiteTheme .readerWriteReviewPanel_bg {
    background-color: #fff !important;
    border: 1px solid rgba(0, 0, 0, 0.4);
    border-bottom: 0;
}
.wr_whiteTheme .readerTopBar_right {
    background: none;
}
.wr_whiteTheme .writeReview_private_button {
    background-color: #000000ba !important;
    border: 1px solid;
}
/* .wr_whiteTheme .writeReview_private_button span{
    color: black !important;
    
} */
.menu_option_container {
    top: -90px !important;
    max-height: 140px !important;
    right: 10px !important;
}
.navBar {
    height: 60px
}
.navBar .navBar_border {
    border: unset;
}
.navBar .navBar_border .navBar_inner {
    width: 100%;
    max-width: calc(100% - 80px);
    margin: 0 40px;
    padding: unset
}
.navBar .menu_option_container {
    top: 42px !important;
    max-height: 140px !important;
    right: 0px !important;
}
.shelf_download_app {
    display: none
}
.shelf_header {
    margin-left: 40px
}
.shelf_list {
    margin-left: unset;
    margin-right: 40px;
}
.navBar_separator {
    display: none;
}
.navBar_link {
    display: none
}
.navBar_input {
    max-width: 250px
}

#routerView .shelf_header {
    margin-right: 40px;
    margin-top: 20px;
}
#routerView .shelf_header H2,
#routerView .shelf_header div {
    text-align: right
}
@media (max-width: 1365px) {
    .readerWriteReviewPanel,
    #readerReviewDetailPanel{
       max-width: calc(760px - 48px);
    }
    #routerView .readerNotePanel, 
    #routerView .readerCatalog {
        max-width: 760px;
    }
}
@media (max-width: 1023px) {
    .readerWriteReviewPanel, 
    #readerReviewDetailPanel{
       max-width: calc(560px - 48px);
    }
    #routerView .readerNotePanel, 
    #routerView .readerCatalog {
        max-width: 560px;
    }
}
@media (max-width: 767px) {
    #routerView .readerControls {
        display: none
    }
    #routerView .readerNotePanel, 
    #routerView .readerCatalog {
        bottom: 56px;
        max-width: 100%;
    }
    
    #readerReviewDetailPanel{
       max-width: calc(100% - 48px);
    }
    .readerWriteReviewPanel {
        max-width: 100%;
    }
}
@media (max-width: 720px) {
    .navBar_input {
        max-width: unset;
        height: 40px;
        font-size: 15px;
        background-position: 12px 10px;
    }
}
}
