@-moz-document url-prefix('https://bytedance.feishu.cn/docs/') {
	:root {
		--fontColor: #d0d0d0;
		--lineColor: #9a9a9a;
		--bgColor: #f8f8f8;
		--linkColor: #469ae4;
	}
	.navigation-bar, .find-replace-box__container, .find-replace-box__input, .comment-panel__textarea, .mention-pop__time, .fs-mode-control-bar {
		background-color: #383838;
	}
	.note-title__input, .suite-path-btn.one-path, .note-title__time, .find-replace-box__input, .dialog-header__2xs9AgNx, .global-like-pc__detail-item-username, .lwiki-path-item, .line-menu__btn-text, .sheetbox-hd-content, .suite-path-list-item, .suite-paths-item-delete:before, span.ellipsis, .iwtvPm, #editbar-text-highlight .cp-dropdown .toolbar-highlight-panel .group-title, #editbar-text-highlight .cp-dropdown .toolbar-highlight-panel .clear-highlight-btn, .hdcYTz, .eFUvnL, .dropdown-select .selected-items-container {
		color: var(--fontColor);
	}
	.suite-header__user-avatar>.navigation-bar__line, .note-title__info-separator, .navigation-bar__line, .iAcjWB {
		background-color: var(--lineColor);
	}
	.sc-gzVnrw>svg>path, .suite-path-btn>svg>g>path:first-child, button[data-selector="ppt-btn"]>svg>path, .note-title__search>svg>path, .more-btn>svg>path, .note-title__create>svg>path, .line-menu__style-btn svg path, .etherpad-container.suspension-comment .suspension-comment-area .comment-btn svg, .editorbar-icon svg path, .fs-mode-control-bar__exit > svg > path {
		fill: var(--bgColor);
	}
	.suite-path-btn>svg>g>path:last-child {
		fill: #504444;
	}
	body, .etherpad-container-sub-wrapper {
		background-color:#484848;
		color: var(--fontColor);
	}
	.note-user__cursor-color, .navigation-bar.navigation-bar__suite, .doc-comment__toggle-container .doc-comment__list-header, .doc-comment__toggle-container.doc-comment .doc-comment_background, .innerdocbody .ace-table tbody>tr>td, .innerdocbody .ace-table, .tb-scrollable-content .tb-scrollable-shadow-right.tb-scrollable-shadow-enabled, .tb-scrollable-content .tb-scrollable-shadow-left.tb-scrollable-shadow-enabled, .global-comment__title:after, .find-replace-box__container, .global-comment__textarea, .doc-comment .doc-comment__panel-item, .mention-pop, .mention-popover, .mention-pop__time, .content-inner__3IVEXlPI, #editbar-text .editbar-text-more .cp-dropdown-menu, .content-inner__2XhNhokM, .fs-mode-control-bar, .gSmwAH, .ckXwye:not(:last-child), .translate-settings {
		border-color: #383838;
	}
	.maindocbody>div:first-child span {
		color: var(--fontColor)!important;
	}
	.comment-list-item__content, .comment-list-item__author, .global-comment .comment-list-item__author, #outerdocbody .maindocbody, .innerdocbody .blockquote:not(.list-code), .section-nav-container .full-entry-title {
		color:var(--fontColor);
	}
	.comment-list-item__time {
		color: #b1b1b1;
	}
	.doc-comment__toggle-container.doc-comment .doc-comment_background, .doc-comment__toggle-container .doc-comment__list-header, .doc-comment .doc-comment__panel-item, .section-nav-container .section-nav.showing-full, .mention-popover, .dialog-content__2eZqZ0kU, .line-menu hr, .history-body, .ssrWaterMark {
		background-color:#484848;
	}
	.global-like-count .sep-line {
		background:var(--lineColor) !important;
	}
	.innerdocbody .list-code code, .innerdocbody .blockquote:not(.list-code) {
		background-color: #3e3e3e;
		border-color: #313131;
	}
	.note-users-dropdown-btn {
		background-color:#000;
	}
	.innerdocbody .horizontal-line hr {
		background-image: linear-gradient(#333, #333);
	}
	.editorbar-icon, .content-inner__3IVEXlPI, #editbar-text .menu, #editbar-text .editbar-text-more .cp-dropdown-menu, .content-inner__2XhNhokM, .iwtvPm, .toolbar-highlight-panel {
		background-color:#373c43 !important;
	}
	.selection-popup, .tablebox-content, .sheetbox-content, .toolbar-highlight-panel, .toolbar-highlight-btn {
		border:none !important;
	}
	#editbar-text > ul > div > button > span > svg > g > path, #zh-CN > div:nth-child(17) > div.popup__6JA8Hb43 > div > div > div.lwiki-path > div:nth-child(1) > div > svg > path {
		fill: var(--bgColor);
	}
	.selection-popup {
		background-color: transparent;
	}
	.editorbar-icon.active:not(.editorbar-btn-highlight) svg {
		background: rgba(51, 112, 255, .5);
	}
	.history-body>div, .history-item {
		border-color: #383838 !important;
	}
	.history-item-b:after {
		background-image: linear-gradient(hsla(0, 0%, 100%, 0), #363636);
	}
	.editorbar-icon svg:hover {
		background-color: #555 !important;
	}
	.innerdocbody .blockquote:not(.list-code) span.mention {
		background: none;
	}
	.rangecode-bomb-container {
		padding: 0 10px;
	}
	.innerdocbody.adit-container a, #editorcontainerbox ol[class^=list-number] li:before, .innerdocbody ol[class^=list-number] li:before, .section-nav-container .full-entry.active .full-entry-title, .section-nav-container .full-entry:hover>.full-entry-title {
		color: var(--linkColor);
	}
	span.mention>a:before {
		filter: brightness(1.48);
	}
