css:
@-moz-document domain(sky.shiiyu.moe){
  a {
    color: #07d5f9;
}

.external-app-name {
    color: #07d5f9;
}

#goto_target_username {
    background-color: #3BB9FF;
}

#goto_target_username:hover {
    background-color: #07d5f9;
}

.additional-player-stat {
    background-color: #145ac6;
}
.additional-player-stat:hover {
    background-color: #1490c6;
}

.skill-progress-bar {
    background-color: #3BB9FF;
}

.skill-icon {
    background-color: #3BB9FF;
}

.skill-bar::before {
    background-color: #3BB9FF;
}

#nav_bar_line {
    background-color: #3BB9FF;
}

.nav-item::after {
    background-color: #3BB9FF;
}

.stat-header span {
    border-bottom: 4px solid #3BB9FF;
}

.inventory-header-line {
    background-color: #3BB9FF;
}

#inventory_header .inventory-tab::after {
    background-color: #3BB9FF;
}

.sea-creature-name {
    border-bottom: 2px solid #2698d8;
}

.narrow-info-header {
    border-bottom: 2px solid #2698d8;
}

.kills-deaths-container .show-all {
    color: #07d5f9;
}

.tippy-tooltip[data-placement^="top"] > .tippy-arrow {
    border-top-color: rgb(66, 81, 191, 9);
}

.tippy-tooltip[data-placement^="bottom"] > .tippy-arrow {
    border-bottom-color: rgb(66, 81, 191, 9);
}
.info-header {
    background-color: #0BAFCA;
}
.pride-flag.logo {
    background: url(https://i.imgur.com/4SFZGdj.png);
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-size: auto;
    background-size: auto 100%;
    background-position: center;
    background-repeat: no-repeat;
    width: 25px;
    height: 25px;
    margin-top: 12px;
}
html {
    background-color: #000;
    background: url(https://i.imgur.com/qjsmQ4B.png) no-repeat center center fixed;
}
#bg_blur {
    background: url(https://i.imgur.com/yVqoyvZ.png) no-repeat center center fixed;
    background-color: rgba(0, 0, 0, 0);
}
#enter_player_box_wrapper {
    background: url(https://i.imgur.com/yVqoyvZ.png) no-repeat center center fixed;
    background-color: rgba(0, 0, 0, 0);
}
#error_title {
     background-color: #1487ddfa
}
#error_box_wrapper {
    background: url(https://i.imgur.com/yVqoyvZ.png) no-repeat center center fixed;
}
[data-tippy-content]:hover:after {
    color: #00fffb
}
.top-profile {
    background: url(https://i.imgur.com/yVqoyvZ.png) no-repeat center center fixed;
}
.switch_packs_button{
    background-color: rgb(10, 107, 153)
}
.switch_packs_button:hover{
    background-color: rgb(14, 141, 202)
}
.kills-deaths-container .narrow-info-content{
   scrollbar-color: #14e1e0 transparent;
}
}
