@-moz-document url-prefix("https://e-schooling.de/e-learning/main/start.php") {

:root {
  --clock-color: #ffffff;
  --link-hover-color: #0072ff;
  --link-color: #3e5f8a;
  --text-color: #737373;
  --name-color: #990000;
  --hader-hover-color: #272626;
  --hader-color: #1a1a1a;
  --background: url(https://wallpaperaccess.com/full/1450969.jpg);
}

h2 {
 font:bold 24px "Century Schoolbook",Georgia,Times,serif;
 color: var(--name-color);
 line-height:100%;
 margin:.4em 0 .2em 0;
 letter-spacing:-1px;
}
#stripe {
 position:relative;
 margin:0 auto;
 text-align:left;
 background-color:#ffffff;
 width:840px;
 padding-left:20px;
 padding-right:20px;
 height:0%
}
#background {
 z-index:-10;
 position:fixed;
 width:100%;
 height:100%;
 background: var(--background);
}
#content {
 margin-left:auto;
 margin-right:auto;
 margin-bottom:0;
 clear:both;
 width:840px;
 padding-top: 23 !important;
 line-height:150%;
 top:0;
 position:absolute;
 background: none;
 height:0%;
 color: var(--text-color);
}
#headerFirstColumn
{
    background-color: var(--hader-color);
    color: var(--name-color);
}
.memu-root
{
 background-color:  var(--hader-color);
}
#status
{
    background-color: var(--hader-color);
    color: #ffffff;
}
#headerClock
{
    color: var(--clock-color) !important;
    background-color:  var(--hader-color);
}
#headerStatus
{
    color: #ffffff;
    background-color:  var(--hader-color);
}
#headerAlert
{
    color: #ffffff;
    background-color: var(--hader-color);
}
a
{
    color: var(--link-color);
}
a:hover
{
 color: var(--link-hover-color);
}
.HeaderTable td
{
    background-color:  var(--hader-color);
    border-bottom: 0px solid  var(--hader-color);
    border-top: 0px solid  var(--hader-color);
}
.memu a 
{
    background-color: var(--hader-color);
}
.memu .memu-separator
{
     background-color: var(--hader-color);
}
.memu li:hover>a
{
	background-color:var(--hader-hover-color);
}

}

@-moz-document url-prefix("https://e-schooling.de/login/login.php"), url-prefix("https://e-schooling.de/e-learning/login/login.php") {
:root
{
    --login-border-color: #2ecc71;
    --login-hover-color: #2ecc71;
    --border-select-color: #2ecc71;
    --border-color: #3498db;
    --warn-text-color: #000000;
    --warn-head-color: #000000;
    --text-color: #737373;
	--background: url(https://wallpaperaccess.com/full/1450969.jpg);
}

#location
{
    color: #ffffff;
}
h3
{
    color: rgba(255,255,255,0);
    margin: 0px 0px 0px;
    text-shadow: 0px 0px 0px rgba(255,255,255,0);
}
html
{
    background: var(--background);
}
body.login
{
	background: rgba(255,255,255,0);
}
div.login
{
    background:var(--dwads);
}
a, body, table, input
{
    color: rgba(255,255,255,0);
}
#warn
{
    color: var(--warn-text-color) !important;
}
#warn h3
{
    color: var(--warn-head-color) !important;
}
#warn input
{
    color: var(--warn-text-color);
}



#login
{
    border:0;
    background: #1a1a1a;
    margin: 20px;
    text-align: center;
    border: 2px solid var(--login-border-color);
    padding: 14px 10px;
    width: 220px;
    outline: none;
    border-radius: 24px;
    transition: 0.25s;
    color: white;;

}
#login:hover
{
    background: var(--login-hover-color);
}
#school
{
	color: #000000;
    border:0;
    background: #1a1a1a;
    margin: 20px;
    text-align: center;
    border: 2px solid var(--border-color);
    padding: 14px 10px;
    width: 200px;
    outline: none;
    border-radius: 24px;
    transition: 0.25s;
    color: white;

}
#location
{
	color: #000000;
    border:0;
    background: #1a1a1a;
    margin: 20px;
    text-align: center;
    border: 2px solid var(--border-color);
    padding: 14px 10px;
    width: 200px;
    outline: none;
    border-radius: 24px;
    transition: 0.25s;
    color: white;

}
#loginForm input[type="button"]
{
    border:0;
    background: #1a1a1a;
    margin: 20px;
    text-align: center;
    border: 2px solid red;
    padding: 14px 10px;
    width: 250px;
    outline: none;
    border-radius: 24px;
    transition: 0.25s;
    color: red;
}
#loginForm input[type = "text"], #loginForm input[type = "password"]
{
    border:0;
    background: #1a1a1a;
    margin: 20px;
    text-align: center;
    border: 2px solid var(--border-color);
    padding: 14px 10px;
    width: 200px;
    outline: none;
    border-radius: 24px;
    transition: 0.25s;
    color: white;

}
#loginForm input[type = "text"]:focus, #loginForm input[type = "password"]:focus
{
    border-color: var(--border-select-color);
}
}

