css:
@-moz-document domain(dropmark.com) {
   body {
      background-color:#323232;
  }
  .item-preview {
      box-shadow: 0 4px 10px 0 rgba(0, 0, 0, 0.4)
  }
  .item-preview:hover {
      box-shadow: 0 3px 10px 0 rgba(0, 0, 0, 0.6)
  }
  .dropdown .filter-dropdown__select, .dashboard-collection-toggles .toggle, .pagination-button, .cn-bar__tag {
      border: 1px solid #1D1D1D;
      background: #373737;
  }
  .cn-bar__tag {
      color: #737373;
      color: rgba(255, 255, 255, 0.300);
  }
  .cn-bar__tag--medium {
      color: #BEBEBE;
      color: rgba(255, 255, 255, 0.700);
  }
  .showing-highlights .highlighted-toggle {
      border: 1px solid #20B7B8;
      background: #373737;
      color: #20B7B8;
  }
  .primary-header {
      background: #383838;
      border-bottom: 1px solid #444444;
  }
  .nav-item {
      border-right: 1px solid #1D1D1D;
  }
  .page-title, .section-title, .collection-title, .collection-link {
      color: white;
  }
  .item-name-label {
      color: #BCBFC2;
  }
  .nav-sidebar {
      background: #383838;
  }
  .is-list .item {
      border-bottom: 1px dotted #444444;
  }
  .collection-inner {
      background: #383838;
      background: rgba(56, 56, 56, 1.000);
      box-shadow: 1px 1px 0 rgba(32, 35, 37, .08), 3px 3px 0 rgba(31, 35, 37, 0.3), 4px 4px 0 rgba(32, 35, 37, .08), 6px 6px 0 rgba(31, 35, 37, 0.9), 7px 7px 1px rgba(32, 35, 37, .08);
      margin-bottom: 7px;
  }
  .collection-inner {
      background: #383838;
      box-shadow: 1px 1px 0 rgba(32, 35, 37, .08), 3px 3px 0 rgba(31, 35, 37, 0.7), 4px 4px 0 rgba(32, 35, 37, .08), 6px 6px 0 rgba(31, 35, 37, 0.5), 7px 7px 1px rgba(32, 35, 37, .08);
      margin-bottom: 7px;
  }
  .is-bookmarklet .item-name, .is-bookmarklet .item-name-field, .is-grid .item-name, .is-grid .item-name-field, .is-scroll .item-name, .is-scroll .item-name-field {
      background: none;
  }
}
