css:
@-moz-document domain("tosdr.org") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

.modal
{
  background: #3d3d3f;
}

.bgw,

.card-inline:nth-child(2),
.panel-default>.panel-heading,

.table-striped>tbody>tr:nth-of-type(odd),

#index-html .hero-unit,
.hero-unit, #bottom,

.panel,
.card,

.jumbotron
{
    background: #363639;
}

.navbar-tosdr,

.card-inline:nth-child(1),
.panel-default>.panel-heading+.panel-collapse>.panel-body,

input, textarea,

.navbar-fixed-top .navbar-inner,
code,

.form-search .input-prepend .btn,

.collapse.in,
.card .card-service,
.card-badge
{
    background: #2e2e2f !important;
}

.modal-backdrop,

.card-inline:nth-child(odd)
{
    background: #2e2e2f;
}

.badge
{
    background-color: #2e2e2f;
}

.label-warning, .badge-warning {
    background-color: #f89406;
}

.D, .badge-warning, .badge-mediocre {
    background-color: #D66F2C;
}

.list-group-item-warning
{
  background: #6a561a;
}
.list-group-item-secondary
{
  background: #1a636a;
}
.list-group-item-danger
{
  background: #6a251a;
}
.list-group-item-success
{
  background: #1a6a1a;
}

.list-group-item-warning:hover
{
  background: #6a561a5c !important;
}
.list-group-item-secondary:hover
{
  background: #1a636a5c !important;
}
.list-group-item-danger:hover
{
  background: #6a251a5c !important;
}
.list-group-item-success:hover
{
  background: #1a6a1a5c !important;
}


body, h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6,
input, textarea, li,

.navbar-tosdr-left .navbar-tosdr-title,

.masthead-links li a,

.btn-secondary a,

.card-badge
{
  color: #ccc !important;
}

.list-group-item a
{
  color: #ccc !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

.form-search .input-prepend .btn
{
  border-right: 2px solid #3d3d3f !important;
}

#index-html .hero-unit h1 img,
#inthenews a img,

[class^="icon-"], [class*=" icon-"],
.jumbotron img,

#news-section\ mt-5
{
    filter: invert(1) hue-rotate(195deg) saturate(5) brightness(1.5);
    mix-blend-mode: lighten;
}

.badge i
{
    filter: brightness(1);
}
}
