css:
@-moz-document regexp("http://acm.*hdu.edu.cn/.*")
{
/*---全局*/ 
body
{
	overflow-x:hidden !important;
	margin:0;
	font-size:12px;
	box-shadow: 0 5px 15px rgba(50,50,93,.1),0 5px 8px rgba(0,0,0,.07)!important;
}
body > table 
{
	width:100%;
}
body>table>tbody>tr:nth-child(2)>td>table
{
	min-width:900px;
	max-width:1100px;
	width:80%;
	margin:auto;
}
/*---页码栏*/ 
body>table>tbody>tr:nth-last-child(3)>td, p.footer_link 
{
	min-width:900px;
	max-width:1100px;
	width:80%;
	margin:auto;
	display:block;
}
body > table > tbody > tr:nth-child(5) > td > p
{
	border-top: none !important;
	outline:none !important;
	cursor: pointer;
	line-height: 1.25;
	position: relative;
	margin-left: -.0625rem;
	padding: .5rem .75rem;
	border: .0625rem solid #dee2e6;
	font-size: 15px;
	font-weight: unset;
	display: flex;
	min-width: 50px;
	height: 36px;
	margin: 0 3px;
	border-radius: 100px!important;
	color: #fff;
	border-color: #5e72e4;
	background-image: linear-gradient(-90deg, #29bdd9 0%, #276ace 100%);
	box-shadow: 0 5px 15px rgba(50,50,93,.35),0 5px 8px rgba(0,0,0,.07)!important;
	border-color:#28a0d5;
	width:1100px !important;
	align-items: center;
	justify-content: center;
	transition:all .3s;
}
/*---输入框,选择器*/ 
input,select
{
	border-image:linear-gradient(135deg,#fb00ca, #0012ea) 3 10!important;
	font-size: 14px;
	background-color: transparent;
	color:#007bff; 
}
/*---页码栏页码*/
body>table>tbody>tr:nth-last-child(3)>td a, p.footer_link a
{
	font-size:12px !important;
	color:black;
}
/*---滚动栏*/
::-webkit-scrollbar
{
    width: 7px;
    height: 10px;
    background-color: rgba(0, 0, 0, 0);
}
::-webkit-scrollbar-track
{
    background-color: rgba(94,114, 238, 0.3);
}

::-webkit-scrollbar-thumb
{
    background-color: rgba(0, 0, 0, 0.2);
}
::-webkit-scrollbar-thumb:hover
{
    background-color: #5e72e4;
} 
/*---顶栏*/
body > table > tbody > tr:nth-child(2) > td
{
	border: none !important;
	background-image: linear-gradient(to right, #ff9569 0%, #e92758 100%);
	border-color: #555abf;
}
/*---题目颜色*/
a
{
	color:#00ffa3;
	font-family:Arial;
}
body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child a
{
	color:#f0f0f0;
	font-size:13px;
	margin-top:3px;
	padding:0 !important;
}
body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child a:hover
{
	color:white;
}
body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child td form table
{
	color:gray;
}
/*---去图*/ 
body>table>tbody>tr:first-child 
{
	display:none !important;
}
body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:first-child
{
	display:none !important;
}
/*---table_header*/
.table_header 
{
	background-image: linear-gradient(120deg, #a6c0fe 0%, #f68084 100%);
	background-color: #0f0f0f !important;
}
/*---Search和Sign in*/
.button40 
{
	outline:none !important;
	cursor: pointer;
	line-height: 1.25;
	position: static;
	margin-left: -.0625rem;
	border: .0625rem solid #dee2e6;
	background-color: #fff;
	color:#5e72e4 !important;
	font-weight:bold;
	font-size:11px !important;
	min-width: 20px;
	max_width: 100px;
	height: 20px;
	margin: 0 3px;
	border-radius: 100px!important;
	justify-content: center;
	transition:all .3s;
	border-image:none!important;
}
/*---提交界面*/
#submitform > table > tbody > tr:nth-child(4) > td > input.text40 
{
	border-weight:2px;
	border-image:linear-gradient(135deg,#fb00ca, #0012ea) 3 10!important;
}
#submitform > table > tbody > tr:nth-child(4) > td > span
{
	border-weight:2px;
	border-image:linear-gradient(135deg,#fb00ca, #0012ea) 3 10!important;
}
.code 
{
	background-color: white !important;
	border: black 1px solid;
	font-size: 16px;
	font-family: Consolas, Courier New, Courier !important;
	text-align: left;
	padding:1px 1px 1px 1px !important;
	border-width:3px 3px 3px 3px!important;
	border-image:linear-gradient(120deg, #92fe9d, #00c9ff) 37 100;
}
/*---Bestcode颜色*/
body > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td:nth-child(4) > a:nth-child(1)
{
	color:yellow !important;
}
/*---邮件数颜色*/ 
body > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td:nth-child(5) > div > b > a:nth-child(2) > font
{
	color:yellow !important;
}
/*---编号颜色*/ 
body > table > tbody > tr:nth-child(6) > td > table > tbody > tr:nth-child(1n)
{
	color:#ffbe00;
}
/*---顶栏字体颜色*/
body > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td:nth-child(1) > a:nth-child(1)
{
	color:#f0f0f0;
	font-size:13px;
	margin-top:3px;
	padding:0 !important;
}
/*---底栏*/
body>table>tbody>tr:last-child table
{
	width: 100%;
	border-width: 0px 0 0 0 !important;
	background-image: linear-gradient(to right, #eea2a2 0%, #bbc1bf 19%, #57c6e1 42%, #b49fda 79%, #7ac5d8 100%) !important;
	font-size: 12px;
	margin-top:30px;
}
/*---题目栏*/
body>table>tbody>tr:nth-last-child(2)>td
{
	min-width:900px;
	max-width:1100px;
	width:80%;
	margin:auto;
	display:block;
}
body > table > tbody > tr:nth-child(6) > td > table > tbody > tr:nth-child(even)
{
	background-color:rgba(215,235,255,0.62);
}
/*---底栏*/
.footer_link
{
	 font-size:13px;
}
/*---搜索栏的In*/
body > table > tbody > tr:nth-child(5) > td > form > table > tbody > tr > td:nth-child(3)
{
	color:#1A5CC8 !important;
}
}
@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_showproblem.php.*"), regexp("http://acm.*hdu.edu.cn/showproblem.php.*")
{
/*---标题*/ 
h1
{
	font-size:38px;
	font-family:Open Sans;
	background-size:cover;
	background-image: linear-gradient(to right, #b8cbb8 0%, #b8cbb8 0%, #b465da 0%, #cf6cc9 33%, #ee609c 66%, #ee609c 100%) !important;
	background-origin: border-box;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;

}
/*---时空显示*/
body > table > tbody > tr:nth-child(4) > td > font > b > span
{
	color:#0bea0b !important;
}
/*---顶*/
div.panel_title
{
	height:45px !important; 
	background-size:contain;
}
/*---文本*/ 
div.panel_content
{
	font-size:16px !important;
	background-size:contain;
}
/*---底*/
div.panel_bottom
{
	background-size:contain;
}
}
@-moz-document regexp("http://acm.*hdu.edu.cn/status.php.*")
{
/*---实时评测界面微调*/
#fixed_table > left
{
	color:yellow;
}
#fixed_table > left > span:nth-child(4) > select
{
	border:none !important;
}
#fixed_table > left > span:nth-child(5) > select
{
	border:none !important;
}
#fixed_table > left > span:nth-child(4)
{
	border-image:linear-gradient(135deg,#fb00ca, #0012ea) 3 10!important;
}
#fixed_table > left > span:nth-child(5)
{
	border-image:linear-gradient(135deg,#fb00ca, #0012ea) 3 10!important;
}
/*---更改了透明度*/ 
#fixed_table > table > tbody > tr:nth-child(even)
{
	background-color:rgba(215,235,255,0.62) !important;
}
/*---标题颜色微调*/ 
h1
{
	background-image:linear-gradient(120deg,#0081ff 60%,#1A5CC8);
	background-size:cover;
	background-origin: border-box;
	-webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
/*---Run ID颜色修改使其更易于与背景区分*/
#fixed_table > table > tbody 
{
	color:#e9e7ef !important;
}
}
/*---自定义设置*/ 
@-moz-document regexp("http://acm.*hdu.edu.cn/.*")
{
	body
	{
		background-image:url(/*[[wjr]]*/);
		background-repeat:no-repeat;
		background-size:cover; 
	}
}
