css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rutracker.org"), domain("pornolab.net")
{
/*=================================================*/
/*   Cream  |   Aqua  |  Green  |   Red   */
/*  #282828 | #2C2C36 | #2A2A2C | #2F353A */
/*  #222222 | #23232D | #212122 | #2b3034 */
/*  #191919 | #1D1D24 | #19191A | #262a2e */
/*  #B9B384 | #77BABC | #63b97f | #ff7b5d */
/*  #908B67 | #5B8E90 | #4B8B60 | #B35843 */
:root {
    --primary: #B9B384;
    --secondary: #908B67;
    /*--special1: #77BABC;*/
    --special1: darkorange;
    --special2: #4B8B60;
    --special3: #5B8E90;
    --special4: #63b97f;
    --dark1: #191919;
    --dark2: #222222;
    --dark3: #282828;
    --dark4: #333333;
    --dark5: #393939;
    --dark6: #494949;
    --dark7: #696969;
    --light0: #FFFFFF;
    --light1: #DDDDDD;
    --light2: #CCCCCC;
    --light3: #BBBBBB;
    --light4: #AAAAAA;
    --light5: #999999;
    --light6: #888888;
    --alert-info: gold;
    --visited: dimgray;
}
/* reverse fix */
body {
    transform: scaleX(1) !important;
}
/* reverse fix end */
/* various fixes */
#logo {
    display: none;
}
.vMiddle {
    display: none;
}
.bypass-alert {
    display: none;
}
/* various fixes end */
body {
    background-color: var(--dark1) !important;
    color: var(--light4) !important;
}
#body_container {
    background-color: var(--dark1) !important;
}
#page_container {
    background-color: var(--dark1) !important;
}
#main-nav {
    background-color: var(--dark2);
    min-height: 21px;
    border-color: var(--dark4);
    border-style: solid;
    border-width: 2px 0;
    padding: 2px 10px;
    margin: 0;
}
#main-nav a {
    color: var(--light6);
}
.site-nav a {
    color: var(--light6) !important;
}
.site-nav a:hover,
.site-nav a:active {
    color: var(--primary) !important;
}
.topmenu {
    background-color: var(--dark1);
    border-color: var(--dark5);
}
#main_content {
    border: solid var(--light1);
}
#main_content_wrap {
    border: solid var(--light2);
}
#main_content,
#main_content_wrap {
    border-color: var(--dark2);
    border-width: 0px 1px 1px;
}

/* news section */
.news_title {
    background-repeat: repeat-x;
}
#latest_news {
    background-color: var(--dark3);
    padding: 4px;
}
#latest_news a {
    color: var(--light2);
}
#latest_news .new a {
    color: var(--special4);
}
/* news section end */
#forums_top_links {
    padding: 2px;
    margin: 2px;
}
.logged-in-as-wrap,
.gen,
.med,
.small,
.gensmall,
#sidebar1 h3,
#idx-sidebar2 h3,
#latest_news h3 {
    color: var(--primary);
}
.forumlink a {
    color: var(--primary) !important;
}
a:link {
    color: var(--light3);
}
a:visited {
    color: var(--light6);
}
a:hover,
a:active,
a:focus {
    color: var(--special1) !important;
}
fieldset {
    border-color: var(--dark5);
}
#sidebar1 a {
    color: var(--light6);
}
.cat_title,
.forumline th,
.forumline td,
.forums th,
.forums td,
.topic th,
.topic td {
    border-color: var(--dark6) !important;
}
.cat_title {
    background-color: var(--dark3);
    background-image: none;
}
.cat_title a {
    color: var(--light2) !important;
}
.row1,
.row1 td {
    background-color: var(--dark2);
}
#forums_wrap > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) {
    display: none;
}
#timezone {
    color: var(--light6);
}
.modal {
    background-color: var(--dark3);
}
/* sub options */
.menu-sub table th {
    background-color: var(--dark5);
}
.menu-sub table td {
    background-color: var(--dark2);
}
.menu-sub label:hover {
    background: var(--special2);
    color: var(--special1);
}
/* Search page */
.row4,
.row4 td {
    background-color: var(--dark2);
}
.maintitle,
.pagetitle {
    color: var(--light2);
}
#fsn-menu-a {
    color: var(--light2);
}
legend {
    color: var(--secondary);
}
.forumline th {
    color: var(--light2);
    background-color: var(--dark5);
    background-image: none;
}
.cat,
td.cat,
td.catTitle,
td.catHead,
td.catBottom {
    background-color: var(--dark3);
    background-image: none;
    color: var(--light2);
}
div.t-tags span {
    background-color: var(--dark5);
    color: var(--light4);
    text-shadow: var(--light6);
}
/* highlight hover */
tr.hl-tr:hover td,
tr.tCenter:hover td {
    background-color: var(--dark5) !important;
}
/* highlight hover end */
a:hover .brackets-pair {
    color: var(--special1) !important;
}
.topicAnnounce,
.topicPoll {
    color: var(--special4) !important;
}
span.brackets-pair {
    color: var(--light4);
}
.buttons input,
select,
input,
textarea,
.news_date,
.topmenu input[type="text"],
.topmenu input[type="password"],
input.mainoption,
input.main,
input.liteoption,
input.lite {
    color: var(--light4);
    background-color: var(--dark5);
    border-color: var(--dark7);
}
optgroup option {
    color: var(--light4);
    background-color: var(--dark5);
}
option:hover {
    background-color: var(--special2);
}
optgroup {
    color: var(--light4);
    background: var(--dark2);
}
.has_sf {
    color: var(--light4);
    background: var(--dark3);
}
/* topic page */
.row5,
.row5 td {
    background-color: var(--dark1);
}
.row2,
.row2 td {
    background-color: var(--dark3);
}
.row3 {
    background-color: var(--dark2);
}
.nick a {
    color: var(--light0) !important;
}
.nick-author a {
    color: var(--primary) !important;
}
.signature a:hover {
    color: var(--special2) !important;
}
.poster_info em {
    color: #777;
}
.poster_info p {
    color: var(--light6);
}
.posted_since {
    color: var(--light5);
}
/* peers list */
table.peers th {
    background-color: var(--dark5);
    color: var(--light3);
}
.p-body tr {
    background-color: var(--dark2);
    color: var(--light5);
}
.p-body tr:hover {
    background: var(--special2);
    color: var(--dark2);
}
/* quote and spoilers */
.sp-wrap {
    background-color: var(--dark2);
}
.c-body,
.q,
.sp-wrap,
.sp-head,
.sp-body {
    border-color: var(--dark7);
}
.sp-head span,
.sp-head {
    color: var(--light4);
}
.sp-body {
    background-color: var(--dark3);
}
.q {
    background-color: var(--dark5);
    color: var(--light2);
}
.q .q {
    background-color: var(--dark6);
    color: var(--light3);
}
.q .q .q {
    background-color: var(--dark7);
    color: var(--light4);
}
/* preview file list */
#tor-fl-wrap {
    background-color: var(--dark3);
}
#tor-fl-wrap td {
    background-color: var(--dark3);
}
#tor-filelist {
    background-color: var(--dark4);
    border-color: var(--light4);
}
#tor-filelist s {
    color: var(--special1);
}
/* text-editor */
.ped-editor-buttons .buttons-row input[type="button"] {
    background-image: linear-gradient(var(--dark1) 5%, var(--dark3) 100%);
    color: var(--light4)
}
.ped-editor-buttons .buttons-row input[type="button"] {
    text-shadow: 0px 1px 0px rgba(22, 22, 22, 0.7);
}
/* message keyboard */
.b80,
.b30,
.b100,
.b300 {
    background-color: var(--dark5);
}
/* profile */
.txtb,
.txtb:link,
.txtb:visited,
.txtb:hover {
    color: var(--special3);
}
.thHead {
    background-color: var(--dark5);
    background-image: none;
}
table.ratio th {
    color: var(--light3);
}
table.message td {
    background-color: var(--dark3);
}

#thx-list b {
    color: forestgreen
}
#thx-list i {
    color: var(--light4);
}
.seed {
    color: forestgreen;
    font-weight: bolder
}
#logged-in-username {
    color: forestgreen !important;
    font-size: 12px;
}
.log-out-icon,
.vf-topic-icon-cell > img {
    filter: invert(100%);
}
#search-menu {
    width: 105px;
}
ul.inlined.middot-separated > li + li::before {
    color: chocolate;
}
.menu-a a {
    background-color: var(--dark3)
}
.menu-a a:hover {
    background: var(--dark6)
}
.a-like {
    color: darkcyan !important
}
.menu-split a:hover,
.a-like:hover {
    color: darkorange !important;
}
.ta-inf2 {
    background: var(--alert-info);
}

img.icon2 {
    filter: invert(100%);
}

a.torTopic:visited {
    color: var(--visited);
}

td.topicSep {
    color: darkcyan
}

.pad_2 > a > img,
td > a > img {
    filter: invert(1)
}
}
