css:
@-moz-document domain("game3rb.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 3px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 3px;
             box-shadow: 2px 0px 3px rgba(0, 0, 0, .9) !important;
             background-color: #ff0036;
        }

#menuatas, #nav ul ul {
    background: #111;
}
#logo {
	filter: invert(1);
}
#nav ul ul li a {
	color: #aaa;
}
#nav ul ul li:hover {
	background: #5a5a5a;
}
#nav li:hover ul, #nav li:hover .g1-link-toggle {
	border-color: transparent transparent #0e0e0e;
}
.current-menu-item>a, #nav li a:hover {
    color: #1a1a1a !important;
}
.barsearsh-clear {
    background: rgba(0, 0, 0, .95);
}
.post>h1 strong, .post>h1 a:hover, .post h2 strong, .post h2 a:hover {
	color: #ccc;
}
.btnsearshh {
    background: rgba(255, 255, 255, .7);
    color: #000;
}
.fa-search:before {
	color: #000;
}
.searchform>div:before {
    content: "\f002";
    font-family: FontAwesome;
    top: 0;
    bottom: 0;
    padding: 11px 15px;
    position: absolute;
    font-size: 20px;
}
.searchform>div:after {
	display: none;
}
.h2title, .relhead, .komhead {
	border-color: #333;
}
.comments .comment-block {
	background: #0f1010;
}
.comment-content {
	color: #777;
}
#social-top i {
	color: #000;
}
center a button[style="font-size: 19px;padding:16px"] {
	display: none;
}
#wpdcom .ql-editor {
	border-color: #252525;
}
#wpdcom .wpd-thread-head, #wpdcom .wpd-form-head, #wpdcom .wpdiscuz-subscribe-form-button input[type=submit], #wpdcom .wpd_label__check i.wpdicon-off {
	border-color: #4a4a4a;
}
#wpdcom .wpd-sbs-toggle i.fa-caret-down {
	color: #999;
}
#wpdcom.wpd-dark .wpdiscuz-item.wpd-field-select select.wpdiscuz_select, #wpdcom.wpd-dark select {
	background-color: #666 !important;
	border-color: #444 !important;
	color: #fff !important;
}
#wpdcom .wpdiscuz-subscribe-form-email input[type=email] {
	background-color: #1a1a1a;
	color: #fff;
	border: 1px solid #444;
}
#wpdcom .ql-container {
	background-color: #666;
	border-color: #444;
}
#wpdcom .ql-toolbar {
	border-color: #444;
}
#wpdcom .wpd-form .wpd-avatar img, #wpdcom .wpd-form .wpd-avatar img.avatar {
	background-color: #1a1a1a;
	border-color: #4a4a4a;
}
#wpdcom .wpd-form-row .wpdiscuz-item input[type="text"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="email"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="url"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="color"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="date"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="datetime"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="datetime-local"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="month"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="number"], #wpdcom .wpd-form-row .wpdiscuz-item input[type="time"], #wpdcom textarea, #wpdcom select {
	background-color: #666;
	border-color: #444;
	color: #fff;
}
#wpdcom.wpd-dark .wpd-form-row .wpdiscuz-item .wpd-field-icon {
	color: #aaa;
}
#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-header {
	background-color: #252525;
}
#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-footer .wpd-vote-up, #wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-footer .wpd-vote-down, #wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-footer .wpd-reply-button {
	border-color: #5a5a5a;
}
#wpdcom .wpd-comment-footer .wpd-reply-button:hover {
	color: #aaa;
}
#wpdcom .wpd-thread-filter .wpd-filter {
	border-color: #666;
}
#wpdcom .wpd-thread-filter .wpdf-hottest {
    color: #fc5844;
    border-bottom-color: #fc5844;
}
#wpdcom .wpd-thread-filter .wpdf-reacted {
    color: #ffa600;
    border-bottom-color: #ffa600;
}
#wpdcom .wpdiscuz-sort-buttons {
	background-color: #1a1a1a;
	border-color: #4a4a4a;
}
#wpdcom .wpdiscuz-sort-buttons .wpdiscuz-sort-button:hover {
	background-color: #222;
}
#wpdcom.wpd-dark .wpd-comment .wpd-avatar img, #wpdcom.wpd-dark .wpd-comment .wpd-avatar img.avatar {
	background-color: #616161;
}
#wpdcom.wpd-layout-3 .wpd-comment .wpd-avatar img {
	border-color: #444;
}
#nav li a[href="https://www.game3rb.com/p/"] {
	display: none;
}
#wpdcom .ql-editor > * {
	color: #fff;
}
#wpdcom .wpdiscuz-spoiler-wrap {
	background-color: #333;
	border-color: #aaa;
}
#wpdcom.wpd-dark.wpd-layout-3 .wpd-comment .wpd-comment-footer .wpd-tool-wrap {
	background-color: transparent;
}
}

