css:
@-moz-document url-prefix("https://student.bmstu.ru/") {
/*Окно входа*/
/*---------------------------------------------------------------------------------------------*/
/*Окно входа*/
.pronto-login {
    overflow: auto!important;
    position: relative;
    height: 100%;
    z-index: 10;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://sun9-46.userapi.com/CpJILKYTqgyl0N7eedMNRlz9r2z8Q8KaaKff7w/qGbVGyQP-Vw.jpg);
}

/*Надписи - Логин и Пароль*/
.pronto-login__type {
    display: table-cell;
    z-index: 1;
    padding: 9px 10px 9px 0;
    color: #fff;
    text-align: left;
    font-weight: 700;
    vertical-align: middle;
}

/*Копирайт*/
.pronto-login__copyright {
    margin-right: 20px;
    color: #fff;
    line-height: 25px;
    font-size: 13px;
}

/*Ссылка снизу*/
.pronto-login__link-button {
    cursor: pointer;
    margin-left: 4px;
    color: #fff;
    text-decoration: underline;
    text-shadow: none;
    font-size: 15px;
    line-height: 27px;
}
/*---------------------------------------------------------------------------------------------*/

/*Меню - Почта*/
/*---------------------------------------------------------------------------------------------*/

/*Контейнер контента*/
.pronto-container__inner {
    height: 100%;
    background: url(https://sun9-46.userapi.com/CpJILKYTqgyl0N7eedMNRlz9r2z8Q8KaaKff7w/qGbVGyQP-Vw.jpg);
}

/*Меню по середине*/
.pronto-mail-list_empty {
    background: none;
}

/*Меню по середине и слева*/
.pronto-mainlist {
    background: none;
    -webkit-border-radius: 3px;
    -webkit-background-clip: padding-box;
    -moz-border-radius: 3px;
    -moz-background-clip: padding;
    border-radius: 3px;
    background-clip: padding-box;
}

/*Меню справа*/
.pronto-content, .pronto-content_full_screen {
    background: none; 
}

/*Тоже меню справа*/
.pronto-placeholder {
    text-align: center;
    text-shadow: rgba(255,255,255,.6)0 1px 0;
    background: none;
}

/*Поиск с белым фоном*/
.pronto-folder {
    text-align: left;
    background: #fff;
}

/*Надпись - Поиск завершен*/
.pronto-mail-list__search-progress-text {
    color: #fff;
    font-size: 14px;
}

/*Надпись - Folder is empty*/
.pronto-mail-list_empty-label {
    color: #fff;
    font-size: 28px;
}

/*Надпись - Сообщение не выбрано*/
.pronto-placeholder__title {
    color: #fff;
    font-size: 28px;
}

/*Поиск при открытом письме*/
.cg-search-outer-container {
    width: 100%;
    display: -webkit-flex;
    display: -moz-flex;
    display: -ms-flexbox;
    display: -ms-flex;
    display: flex;
    height: 43px;
    background: #fff;
}

/*---------------------------------------------------------------------------------------------*/

/*Меню - Контакты*/
/*---------------------------------------------------------------------------------------------*/

/*Меню справа*/
.pronto-content__list {
    background: none;
    -webkit-border-radius: 3px;
    -webkit-background-clip: padding-box;
    -moz-border-radius: 3px;
    -moz-background-clip: padding;
    border-radius: 3px;
    background-clip: padding-box;
}

/*Надпись - Пусто*/
.pronto-content__item-notfound {
    color: #fff;
    text-align: center;
    /*text-shadow: none;*/
    font-size: 20px;
}
/*---------------------------------------------------------------------------------------------*/

/*Меню - Заметки*/
/*---------------------------------------------------------------------------------------------*/

/*Список по середине*/
.pronto-notes-list_empty {
    background: none;
}

/*Надпись - Список пуст*/
.pronto-notes-list_empty-label {
    color: #fff;
    font-size: 28px;
}

/*---------------------------------------------------------------------------------------------*/

/*Меню - Файлы*/
/*---------------------------------------------------------------------------------------------*/

/*Фон Мои Файлы*/
.pronto-files-content-header-path {
    position: relative;
    display: table;
    width: 100%;
    height: 52px;
    padding-left: 8px;
    background: #fff;
    border-bottom: 1px solid black;
}

/*Надпись - Мои файлы*/
.pronto-files-path__label, .pronto-files-path__label .pronto-files-path__link:hover, .pronto-files-path__label .pronto-files-path__link:active, .pronto-files-path__label .pronto-files-path__link:visited {
    color: #525c66;
}

/*Текст в списке*/
.pronto-textfield__label {
    color: #fff;
    font-size: 14px;
}

/*Заголовок таблицы*/
.pronto-files-content-header__separator {
    border-right: none;
    /*border-right: 1px solid black;*/
}

.pronto-files-content-header__row {
    text-decoration: none;
    font-size: 14px;
    background: #fff;
}

.pronto-textfield__label {
    color: #525c66;
    font-size: 14px;
}


/*Сам список*/
.pronto-files-content-header__cell-link, .pronto-files-content-header__cell {
    border-top: none;
    border-bottom: none;
}

.pronto-files__item-holder-line {
    border-top: none;
    text-decoration: none;
}

/*---------------------------------------------------------------------------------------------*/

/*Меню - Настройки*/
/*---------------------------------------------------------------------------------------------*/
/*Контейнер содержащий список в правах доступа*/
.pronto-prefs-acl__container {
    padding: 20px;
    background: #fff;
}
/*---------------------------------------------------------------------------------------------*/

}
