css:
@-moz-document domain("zos.strikearena.ru") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 10px rgba(0, 0, 0, .6);
             background-color: rgba(231,76,60,0.8);
        }

#content, body {
	background: #0e0e0e;
	color: #fff;
}
h1, h2, h3, h4, h5, h6 {
	color: #bfbfbf;
}
.bg-grey-50 {
	background: #111 !important;
}
.forum {
	background: #0f0f0f;
	border-color: #141414;
}
.forum .forum-group a, .forum .forum-title a {
	color: #fff;
}
.forum .forum-group {
	border-color: #141414;
	color: #a3a3a3;
}
.forum .forum-group .forum-activity div span {
	color: #a9a9a9;
}
.border-grey-200 {
	border-color: #111 !important;
}
.breadcrumb>li>a {
	color: #a1a1a1;
}
.breadcrumb {
	color: #999;
}
.forum-post {
	background: #0f0f0f;
	border-color: #141414;
}
a {
	color: #c1c1c1;
}
.post {
	border-color: #111;
}
.forum-post .forum-footer {
	border-color: #141414;
}
.forum-post .forum-footer .post-meta {
    color: rgba(255, 255, 255, .6);
}
.forum-post .forum-user div span {
	color: #999;
}
.forum-post .forum-user .avatar .label {
	border-color: #0e0e0e;
}
.border-grey-300 {
	border-color: #101010 !important;
}
.panel-default>.panel-heading {
	background: #0e0e0e;
	color: rgba(255, 255, 255, .6);
}
.panel, .panel-default>.panel-footer {
	background: #0e0e0e;
}
.form-control {
	background: #1b1b1b !important;
	border-color: #181818;
	color: #adadad;
}
.form-control:hover, .form-control:active, .form-control:focus {
	border-color: #333;
}
.input-icon-left i {
	color: #999;
}
.separator span {
	background: #0e0e0e;
}
.checkbox {
	background: #0000;
}
.panel-login .panel-body a {
    color: #90939b;
}
.ipsSteps {
    border-color: #0e0e0e;
    background: #0e0f11
}
.ipsBox {
    background: #121313;
}
.ipsBox_container {
	background: #0e0e0e;
	border-color: #202224;
}
hr {
	border-color: #111;
}
div#s_s_c_back2, div#s_s_c_backdesc, div#kc_sample_image_div, div#s_s_c_border {
	background: #0e0e0e !important;
	border-color: #0e0e0e !important;
}
.input_text, .ipsTagBox_wrapper {
    border-color: #444 #313131 #111 #313131;
    background: #0e0e0e;
}
.row2, .post_block.row2 {
	background: #0f0f0f;
}
.ipsType_pagetitle, .ipsType_subtitle {
	color: #c2c2c2;
}
.url {
	color: #fff;
}
.ipsType_sectiontitle {
	color: #b9b9b9;
}
.ipsType_sectiontitle {
	border-color: #1c1c1c;
}
.desc, .desc.blend_links a, p.posted_info {
	color: #999;
}
#board_stats .value {
	background: #121212;
	color: #bababa;
}
.forum .forum-group .forum-activity div span a {
	color: #969696;
}
.btn-default {
	background: #111;
	color: #bababa;
}
.btn-default {
    border-color: rgba(255, 255, 255, .1)!important;
}
.btn-default.active, .btn-default:active, .open>.dropdown-toggle.btn-default {
	background: #262626 !important;
	color: #ccc;
	border-color: #5d5d5d !important;	
}
.dropdown-menu>.active>a, .dropdown-menu>.active>a:focus, .dropdown-menu>.active>a:hover {
    color: #bbb;
    background-color: #151515b3;
}
.dropdown-menu {
	background: #111;
}
.dropdown-menu>li>a {
    color: rgba(255, 255, 255, .7);
}
.statistics {
	border-color: #282828;
}
[src='https://zos.strikearena.ru/forums/public/style_images/zos/icon_users.png'] {
	filter: invert(100);
}
blockquote, .blockquote-reverse {
	background: #151515;
}
blockquote {
	border-left-color: #222;
}
.ipsSteps ul li:nth-of-type(2) {
	filter: invert(100)
}
#s_s_c_desc > div:nth-of-type(2) {
	color: #aaa;
}
#DIOcNccPQxjQsgivHeQjcHdLfOp, div#s_s_c_border {
	border-color: #222 !important;
}
.checkbox label {
    color: #888c93;
}
}

@-moz-document domain("vk.com") {
[dir] .wcommunity_wrap {
	background: #0e0e0e;
}
[dir] .wcommunity_header {
	border-color: #111;
}
.wcommunity_subscribers {
	color: #a5a5a5;
}
}
