css:
@-moz-document url-prefix("http://best-drakon") {
body {
    font-size: 12px;
    background: #7B8CEA;
}
.content {
    margin-top: 0;
}
.cp-art {
    height: 0;
    margin-bottom: 0;
}
img {
    opacity: 0.5
}
.wrap {
    background-color: inherit;
    background-image: none;
    min-height: 0;
}
}

@-moz-document domain("aviso.bz") {
.roll-banner_200x300, .roll-banner__body, .roll-banner__link, .roll-banner__link img, .context-ad, .serf-stat, .wall-ad, .baner {
    display:none;
}
body {
    background: #A9BDD6;
    font-weight: bold;
}
frame, iframe {
    filter: grayscale(1.0) opacity(0.5);
}
}

@-moz-document domain("socpublic.com") {
.baner {
    display:none;
}
body, .page-content, .page-sidebar, .portlet.box > .portlet-body {
    background-color: hsl(200, 50%, 80%);
    color: hsl(200, 70%, 20%);
    border-color: hsl(200, 70%, 50%);
    font-size: 1.2em;
    filter: invert(0.9);
}
frame, iframe {
    filter: grayscale(1.0) opacity(0.5);
}
}

@-moz-document domain("drift.biz"), domain("ferma.gg") {
frame, iframe {
    filter: grayscale(1.0) opacity(0.5);
}
.dt-root, img {
    filter: invert(1.0) contrast(0.5);
    font-size: 1.2em;
}
}

@-moz-document domain("instagram777.ru") {
body, img {
    filter: invert(1.0) contrast(0.5);
    font-size: 1.2em;
    background-color: #614C38;
}

#user_balance {    font-size: 2em;}
.col-md-9 {    font-size: 0.7em;}
}
