@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* TRANSFORM ITEMS */

#styles-container {margin: 0px -2px -2px -2px !important;}

#styles {margin: -2px 0px 0px 0px !important;}

#userstyles-heading {display: none;}

#copy-style-info {display: none !important;}

#new-style {display: none !important;}

#extensionsManager spacer, #stylish-manage-standalone spacer,

#stylish spacer {display:none !important;}

#styles { -moz-appearance:none !important; background: #ddeeff 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAaCAYAAAB2BDbRAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADlJREFUeNpiYGBgWMwEJO6DiNcg4heI+A1noRE/sbDQCITeP8xAghlEMIIIBob///8zgCSQCIAAAwDkxxVEpQP1YAAAAABJRU5ErkJggg==") repeat-x 0px -14px !important;}

#styles-container #styles [anonid='heading'] {background: transparent 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAaCAYAAAB2BDbRAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADlJREFUeNpiYGBgWMwEJO6DiNcg4heI+A1noRE/sbDQCITeP8xAghlEMIIIBob///8zgCSQCIAAAwDkxxVEpQP1YAAAAABJRU5ErkJggg==") repeat-x 0px -14px !important;}




#styles-container richlistitem {
-moz-appearance: none !important;
outline: none !important;
margin: 1px !important;
padding: 0px !important;
background: transparent 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAYAAABMDlehAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAEhJREFUeNokitsNgDAMA+3svxDrsAGUtEiNGmryc/LjEBGHzRmnZWY3AE1w1SE8day1/u0278PLI3kJu5KvUhiNie1BSuETYAAUKy3mq81zeAAAAABJRU5ErkJggg==") repeat-x top !important;
opacity: 1.0 !important;
}

#styles-container richlistitem:not([selected="true"]) {
min-height: 22px !important;
max-height: 100px !important;
height: auto !important;
min-width: 128px !important;
max-width: 500px !important;
width: auto !important;
}

#styles-container richlistitem[selected="true"] {
min-height: 28px !important;
max-height: 900px !important;
height: auto !important;
min-width: 128px !important;
max-width: 500px !important;
width: auto !important;
margin: 1px 1px 1px 1px !important;
padding: 0px !important;
background: transparent 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAaCAYAAAB2BDbRAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADlJREFUeNpiYGBgWMwEJO6DiNcg4heI+A1noRE/sbDQCITeP8xAghlEMIIIBob///8zgCSQCIAAAwDkxxVEpQP1YAAAAABJRU5ErkJggg==") repeat-x 0px -8px !important;
}










/*=========================[CUSTOMIZE]============================*/



#styles-container richlistitem {



/*------------------------------------------------------------------
[5] ITEMS COLORS & BORDERS

Below you can change the COLORS, BORDERS & OPACITY of:
default ITEMS (5a1, 5a2, 5a3),
non-compatible ITEMS (5b1, 5b2, 5b3),
plugins ITEMS (5c1, 5c2, 5c3).
Also you can change the Image Preview BG COLOR (5d).
------------------------------------------------------------------*/



/*-------------------------------------------
[5a1] ITEMS COLOR & BORDER (DEFAULT)
-------------------------------------------*/
border: solid 1px #888888 !important;
background-color: #bbccdd !important;
opacity: 1.0 !important;}
/*-----------------------------------------*/



#styles-container richlistitem:not([selected="true"]):hover {



/*-------------------------------------------
[5a2] ITEMS COLOR & BORDER (HOVER)
-------------------------------------------*/
border: solid 1px #999999 !important;
background-color: #ccddee !important;
opacity: 1.0 !important;}
/*-----------------------------------------*/



#styles-container richlistitem[selected="true"] {



/*-------------------------------------------
[5a3] ITEMS COLOR & BORDER (SELECTED)
-------------------------------------------*/
border: solid 1px #555555 !important;
background-color: #778899 !important;
opacity: 1.0 !important;}
/*-----------------------------------------*/



#styles-container, #styles #extensionsView richlistitem .addonName,
#styles-container, #styles #extensionsView richlistitem .addonVersion {



/*-------------------------------------------
[7d1] TEXT SETTINGS: ADDON LABEL (DEFAULT)

Changes the ADDON NAME & VERSION TEXT.
-------------------------------------------*/
color: #000000 !important;
font-size: 10px !important;
font-weight: bold !important;
opacity: 1.0 !important;
margin-top: 1px !important;}
/*-----------------------------------------*/



#styles-container, #styles #extensionsView richlistitem[selected="true"]
 .addonName,
#styles-container, #styles #extensionsView richlistitem[selected="true"]
 .addonVersion {



/*-------------------------------------------
[7d2] TEXT SETTINGS: ADDON LABEL (SELECTED)

Changes the color when selected.
-------------------------------------------*/
color: #ffffff !important;}
/*-----------------------------------------*/





/* END */
