css:
/*******************************/
/* codeME.de v3.0 skin         */
/* Date: 23/02/2009            */
/* Made by: rei85              */
/*******************************/

@-moz-document domain("hattrick.org") {


#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
        /*background: url(page/top.png) bottom left no-repeat !important;*/
}



#footer a
{
        color: white !important; 
}

#ticker
{
       
        width: 318px !important;
	position: absolute !important;
	top: 20px !important;
	left: 318px !important;
}

#ticker div
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codestar.png) no-repeat !important;
        padding-left: 13px !important;
}


#ticker div a
{
	padding-bottom: 2px !important;
	color: #ffffff !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}

#shortcuts
{
	margin-top: 4px !important;
	margin-right:-1px !important;
}

#teamLinks
{
        color: #ffffff !important;
        margin-top: 0px !important;
	margin-left: 7px !important;
}

div#teamLinks a
{
        color: #000000 !important;
}

/*Users online - week - date - time*/

div[id="online"] 
{
        color: #ff0000 !important; 
        float:right!important;
        margin-right:1pt!important;
        margin-top:15pt!important;
}
div[id="time"]
{
        color: #ff0000 !important;
        float:right!important;
        margin-right:3pt!important;
        margin-top:15pt!important;
}

/*Page background*/
body
{ 
  background: #000000 url(http://www.codeme.de/templates/codeme_v2/images/bg_body_2.gif) fixed !important;
}

/*centre title*/
h1 
{
        color: #808080 !important;

        border-bottom: solid thin darkgrey !important;

        background: #eeeeee !important;
}

/*all boxHead*/
h2 
{
        /*color: white !important;

        border-bottom: solid thin #eeeeee !important;

        background: #c70000 !important;*/
}

/*centre news*/
.boxBody h2 
{ 
        color: #c70000 !important;
        border-bottom: solid 1px #c70000 !important;
}



/*-----------boxHeads-----------*/
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeMEtr.png) no-repeat top right !important;
        margin: 0;
	padding: 0;
}


.subMenuBox .boxHead
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeMEtr.png) no-repeat top right !important;
}


.sidebarBox .boxHead .boxLeft, .subMenuBox .boxHead .boxLeft, .main .boxHead .boxLeft
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeMEtl.png) top left no-repeat !important;
}


.sidebarBox .boxFooter,.subMenuBox .boxFooter,.main .boxFooter
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/brcode.png) top right no-repeat !important;
        margin: 0;
        padding: 0;
}

.sidebarBox .boxFooter .boxLeft,.subMenuBox .boxFooter .boxLeft,.main .boxFooter .boxLeft
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/blcode.png) top left no-repeat !important;
}

div.sidebarBoxCollapsed > div.boxLeft h2 
{ 
        background: none top left no-repeat !important;
}
 
/* Press, GB, Team events */
.main .mainBox h2 
{
	border-bottom: solid 1px #c70000 !important;
	background-color: #fff !important;
	color: #c70000 !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}


/*Central header paths*/

h2 a 
{
        color: #ffffff !important;
}

h2 a:hover 
{
        color:#ff6e6e !important;
}


/*News and team events date*/

.date
{
	color: #000000 !important;
	padding-right: 8px;
	font-weight: 400;
}

/*Left Menu titles*/
h3 
{
        color: #c70000 !important;

        border-bottom: solid thin #808080 !important;

        background: #eeeeee !important;
}





/*General Links*/
a
{
        color: #de4747 !important;
	text-decoration: none !important;
}

.sidebarBox a
{
        color: #000000 !important;
        text-decoration: underline !important;
}


/*----subMenu and sidebar links----*/
.subMenuBox a, .subMenuBox a
{
	text-decoration: none !important;
	color: black !important;
}

/*Bot teams link*/

div a[class="shy"] 
{
        color: gray !important;
}


/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}

/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}


/* central page background */
#content
{
        background-image: none !important;
        background-color: #808080 !important;

}

/*Image header (banner)*/
#header 
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeME3banner2.png) !important;
	height: 103px !important;
	padding-top: 42px !important;
}

/*--------Top menu settings--------*/
#menu
{
	height: 20px !important;
	margin-top: 5px !important;
        /*bottom: 3px; !important;*/
        background: none !important;
}

#menu a
{
	line-height: 22px !important;  
        color: #ffffff !important;
        /*-moz-border-radius: 2% !important;*/        
        /*background: #808080 !important;*/
}

div#menu a:hover 
{
        color:#FFFFFF !important; background: #de3559 !important;
}

a:hover
{
	color: #821414 !important;
}

/*bottom white line*/
div[id="bottom"]
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codefooter.png) !important;
}


/*---------Forum---------*/
.cfHeader
{
        background:  #920000 !important;
}

#cfPollHeader
{
	background: #920000 !important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
}

.cfWrapper
{
	border: solid 1px #c70000 !important;
	margin: 0 0 12px 0;
}

.cfHeader a
{
	color: #ffffff !important;
	font-weight: bold;
	text-decoration: none;
}

.cfWrapper a
{
	text-decoration: none;
        /*color: white !important;*/
}

.cfUserInfo a
{
        /*color: #de4747 !important;*/
}

.cfMessage a
{
	/*color: #de4747 !important;*/
}

.folderHeader a:link 
{
	color: #821414 !important;
	font-weight: bold !important;
}

.cfFooter a
{ 
        color: black !important;
}


.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/cross_small.png) no-repeat !important;
}

.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tick_small.png) !important;
}

.folderItem div.unignore:hover
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tickGreen_small.png) !important;
}

/*Ads section*/

div[class="insertHeader"]
{
        background: #920000 !important;
}

div[class="insertFooter"]
{
        background: #ffffff !important;
}




/*Forum Paging*/

img.first
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonFirst.png') no-repeat !important;
}

img.prev
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonPreviuos.png') no-repeat !important;
}

img.next
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonNext.png') no-repeat !important;
}

img.last
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonLast.png') no-repeat !important;
}

img.disabledFirst
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonFirstgray.png') no-repeat !important;
}

img.disabledLast
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonLastgray.png') no-repeat !important;
}


img.disabledPrev
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonPrevgray.png') no-repeat !important;
}

img.disabledNext
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonNextgray.png') no-repeat !important;
}

.ForumPager img.prev
{
background:transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codePreviousPage.png') no-repeat !important;
}

.ForumPager img.next
{
background:transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeNextPage.png') no-repeat !important;
}


input.prevSeries#ctl00_CPMain_imgButLeagueLeft
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonPreviuos.png') no-repeat !important;
}



input.nextSeries#ctl00_CPMain_imgButLeagueRight
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonNext.png') no-repeat !important;
}

input.rgPageNext
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/buttonNext.png') no-repeat !important;
}

input.upSeries#ctl00_CPMain_imgButLeagueUp
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeseriesup.png') no-repeat !important;
}



input.downSeries#ctl00_CPMain_imgButLeagueDown
{
background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeseriesdown.png') no-repeat !important;
}







/*Formatting buttons*/

img.f_quote1{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codequote1.png') no-repeat !important;}
img.f_quote2{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codequote2.png') no-repeat !important;}
img.f_bold{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codebold.png') no-repeat !important;}
img.f_italic{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeitalic.png') no-repeat !important;}
img.f_ul{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeul.png') no-repeat !important;}
img.f_hr{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codehr.png') no-repeat !important;}
img.f_player{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeplayer.png') no-repeat !important;}
img.f_team{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeteam.png') no-repeat !important;}
img.f_match{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codematch.png') no-repeat !important;}
img.f_fed{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codefed.png') no-repeat !important;}
img.f_message{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codemessage.png') no-repeat !important;}
img.f_series{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeseries.png') no-repeat !important;}
img.f_www{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codewww.png') no-repeat !important;}
img.ft_br{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codebr.png') no-repeat !important;}
img.ft_uid{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeft_uid.png') no-repeat !important;}
img.ft_kit{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeft_kit.png') no-repeat !important;}
img.ft_aid{background: transparent url('http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/codeft_aid.png') no-repeat !important;}
.alert,.edit,.kitEdit{border:solid 1px #920000 !important;background-color:#e9d3d3 !important;}

/*shortcuts*/

img.scNewMail 
{
	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;
}

/* HT-LIVE */


.main .liveHighlight
{
       color: red !important;
       font-weight:700 !important;
}

.main .liveReport .liveHomeEvent .liveEvent div 
{
       background: #ffdddd !important;
       border-left: 3px solid #920000 !important;
}

.main .liveReport .liveAwayEvent .liveEvent div 
{
       background: #fffcdd !important;
       border-left: 3px solid #666;
}


/* MatchLineup */
.name a
{
	color: #277f31 !important;
}

/*Foxtrick pop-up menu*/

span.myht1:hover div.myht2 {
display: inline;
position: absolute;
left: 20px;
background-color: #fff !important;
border: solid 1px #de4747 !important;
padding: 0px;
z-index: 999
}

div.playerInfo {
overflow: visible !important;
}

span.myht1 table>tr>td>a {
font-weight: normal !important;
text-decoration: underline !important;
color: #de4747 !important;
}

span.myht1 table>tr>td:hover {
background-color:#ccc !important;
}

}
