css:
@-moz-document url-prefix("https://www.redemerca.com/") {
#navbar-principal{
    background-color: #333333 !important;
}
#navbar-principal a{
    color: #ffffff !important;
}
.preloader {
    background-color: #333333 !important;  
}
.nav-item .btn.btn-primary.btn-sm.shadow-sm.btn-animated.btn-animated-x.rounded-pill span{
    color: #ffffff !important;
}
#navbar-principal a:hover{
    color: #718096 !important;
}
.dropdown-menu.dropdown-menu-xl.p-0.rounded.show .dropdown-body.bg-dropdown-secondary.h-100.rounded, .dropdown-menu.dropdown-menu-xl.p-0.rounded .dropdown-body.bg-dropdown-secondary.h-100.rounded{
    background-color: #2c2c2c !important;    
}
.container-fluid section:not(.bg-dark){
    background-color: #333333 !important;
}
h1, h2, h3, h5, h4, h6, .h6{
    color: #ffffff !important;
}
p:not(.lead){
    color: #718096 !important;
}
.shape-container svg{
    fill: #333333;
}
.col-lg-3.col-sm-6 .card-body{
    background-color: #2c2c2c !important;
}
.card-body{
    border-radius: 25px;
}
.card.shadow-lg{
    background-color: #2c2c2c;
}
.card-body.p-2.position-relative.delimiter-bottom{
    background-color: #171347 !important;
}
.card.shadow-lg.rounded-lg.border-0 #dtHogares, .card.shadow-lg.rounded-lg.border-0 #dtComercios{
    color: #2c2c2c !important;
}
.w-50.position-absolute.bottom-0.right-n8.rotate-180 svg{
    fill: #2c2c2c !important;
}
.card.card-overlay.card-hover-overlay.hover-shadow-lg.hover-translate-y-n10{
    border-color: #2c2c2c !important;
}
.overlay-actions.w-100.card-footer.mt-auto.d-flex.justify-content-between.align-items-center{
    border: #2c2c2c;
    background-color: #2c2c2c;
}
.card.card-pricing.text-center.px-3.shadow.hover-scale-110:first-child(){
    border: #2c2c2c;
    background-color: #2c2c2c;
}
#dtHogares + .counter-extra, #dtComercios + .counter-extra, .px-4.py-5.text-center h6.text-white{
    color: #2c2c2c !important;
}
.px-4.py-5.text-center .text-dark{
    color: #ffffff !important
}
.h6.text-white{
    color: #fff !important;
}
.card.card-pricing.bg-primary.text-center.px-3.shadow.hover-scale-110{
    color: #ffffff !important;
}
.footer{
    background-color: #2c2c2c !important;
}
.row.justify-content-center .col-lg-4.col-md:nth-child(1) .card.card-pricing.text-center.px-3.shadow.hover-scale-110{
    background-color: #333333 !important;
}
.container-fluid.bg-gray-100{
    background-color: #2c2c2c !important;
}
#DepartConten{
    background-color: #2c2c2c !important;
}
.card-img-top.img-fluid{
    border-radius: 25px;
}
.form-control.form-control-sm{
    background-color: #2c2c2c;
    border-color: #2c2c2c;
    color: gray;
}
.card-body.p-1 .text-center.text-dark.h6.mb-0{
    color: #ffffff !important
}
.card{
    border-color: #2c2c2c;
}
.card-img-top.img-fluid{
    margin-top: 1em;
}
.py-2 .text-center.mb-0.pt-1, small{
    color: gray !important;
}
#basic-addon1{
    background-color: #333333;
    border-color: #2c2c2c;
}
#CuerpoCarrito{
    background-color: #333333;
}
#CuerpoCarrito input{
    background-color: #2c2c2c;
    border-color: #2c2c2c;
}
.input-group-prepend button{
    background-color: #1a5e98;
    border-color: #1a5e98;
}
.modal-footer{
    background-color: #2c2c2c !important;
}
input, textarea{
    color: #e6e6e6 !important;
}
textarea{
    background-color: #333333 !important;
    border-color: #333333 !important;
}
.input-group.input-group-merge.rounded-pill input, .input-group-text{
    background-color: #2c2c2c !important;
    border-color: #2c2c2c !important;
}
#CuerpoCarrito .col-12.mb-1{
    color: #ffffff !important;
}
.input-group.input-group-sm.input-group-merge.rounded-pill.mb-3 input{
    background-color: #2c2c2c !important;
    border-color: #2c2c2c;
}
.btn-inner--icon svg{
    color: #e1f5ef;
}
.modal-content{
    background-color: #333333;
}
img.rounded{
    border-radius: 25px;
}
#DescripDetProd .ml-2.text-dark{
    font-weight: 800;
    font-size: 18px;
}
.alert-heading{
    color: #e1f5ef !important;
}
#accordion-1 .card{
    background-color: #333333;
}
#list-SubDpto-1 a, #list-SubDpto-2 a, #list-SubDpto-3 a, #list-SubDpto-4 a, #list-SubDpto-5 a, #list-SubDpto-6 a, #list-SubDpto-7 a, #list-SubDpto-8 a{
    background-color: #333333;
    border-color: gray;
    color: #718096;;
}
#ProductosConten .card-body.p-1{
    background-color: #333333;
    border-radius: 8px;
}
.container-fluid.pb-8, .mt-md-3.text-center{
    background-color: #333333;
    margin-top: 0em !important;
}
#text-Over .mt-md-3.text-center{
    background-color: transparent !important;
}
.container-fluid.pb-8 .card-body, form .card{
    background-color: #2c2c2c;
}
.dropdown-menu.dropdown-menu-xl.ml-lg-10.p-0, .dropdown-body.bg-dropdown-secondary.h-100, .list-group.list-group-flush.px-lg-4, .list-group-item.list-group-item-action{
     background-color: #2c2c2c;
}
.dropdown-menu.dropdown-menu-xl.p-0.show{
    color: #000000 !important;
}
form[action="/Pagos/TipoPago"] i{
    color: #000000 !important;
}
label[for="customRadio3"] br{
    display: none;
} 
form[action="/Pagos/TipoPago"] label{
    color: #ffffff !important;
}
form[action="/Pagos/RegistroPagoMovil"], .row.mx-3.shadow-lg.rounded.bg-light-secondary, form[action="/Pagos/Agregar"]{
    background-color: #333333 !important;
    margin-top: 0px !important;
}
.col-12.col-lg-4.bg-secondary.rounded-left .row.p-2{
    background-color: #333333 !important;
}
.col-12.col-lg-8.bg-translucent-secondary.rounded-right .form-group.row{
    background-color: #333333 !important;
}
.col-12.col-lg-8.bg-translucent-secondary.rounded-right{
    background-color: #333333 !important;
}
form[action="/Pagos/RegistroPagoMovil"] label{
    color: #ffffff !important;
}
.input-group.input-group-merge.input-group-sm.rounded-pill.mb-3 select, label[for="Archivo"]{
    background-color: #2c2c2c;
    border-color: #2c2c2c;
}
form[action="/Pagos/Agregar"] label{
    color: #ffffff !important;
}
.card.bg-light.shadow-sm{
     border-radius: 30px !important;
}
.card.bg-light.shadow-sm .card-body.py-0{
    border-radius: 25px !important;
}
.col-12.col-md-6.p-0{
    background-color: #2c2c2c !important;
    border-radius: 25px !important;
}
.text-dark.h5.d-none.d-sm-none.d-md-block.text-uppercase, .text-dark.h5.text-uppercase{
    color: #ffffff !important;
}
.list-group.list-group-flush span.text-dark, .fas.fa-book{
    color: #000000 !important;
}
#DocsPendientes{
    background-color: #333333 !important;
}
.h5.text-muted.mb-0, .col-12.pt-3.text-center i{
    color: #ffffff !important;
}
form[action="/Pagos/Zelle"]{
    margin-top: 0px !important;
}
.col.col-12.col-lg-4.bg-secondary.rounded-left .col-12 label{
    color: #ffffff !important;
}
form[action="/Pagos/TransferenciaInternacional"]{
    margin-top: 0em !important;
}
form[action="/Pagos/TransferenciaInternacional"] label{
    color: #ffffff !important;
    margin-top: 0em;
}
#MssAlertProd{
    color: #ffffff !important;
}
.bg-section-secondary.mt-2{
    background-color: #333333 !important;
    margin-top: 0px !important;
}
.bg-section-secondary.mt-2 label{ 
    color: #718096 !important;
}
[name="SubDpto_Name"]{
    color: #1a5e98 !important;
    font-size: 26px !important;
    font-weight: 700;
}
.card-body.p-1 p.text-center.text-dark.h6.mb-0{
    font-size: 18px !important;
}
}
