css:
@-moz-document domain("time.is") {

/*時計のフォント変更*/
#clock {
  font-family: "arial black",
  Arial,
  "Hiragino Kaku Gothic ProN",
  "Hiragino Sans",
  Meiryo,
  sans-serif;
}


/*時計の背景と位置*/
#time_section {
    /*背景色, https://grabient.com などで適宜変更*/
  background-color: #FFE53B;
  background-image: linear-gradient(147deg, #bb45ff -18%, #9d1414 74%);

  height:101%;
  width:101%;
  position: fixed!important;
  margin:auto!important;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color:#eee!important;
  text-shadow: 0 0 3px rgba(255,255,255,1);
  display: flex;
  align-items: center;
}


/*ロゴを消す*/
#top {
  display:none;
}


/*時計をクリックすると時計だけになる仕様だったのを常に適用 & 無駄な要素をhiddenからnoneに*/
.showall #qbox{display:none}
#navbg,.map.showall #navbg,.showall #navbg,body #navbg:hover,.factspage #clock0_bg{background:#eee}
.showall .tr{visibility:hidden}
.showall #navbg:hover .tr,.showall .tr:hover{display:none;}
.showall #navbg:hover .tr{opacity:1}
.showall section,
.showall .tr,.showall .susdiv,.hide,.just .tr,.factspage #lC,.prnt{display:none}
.showall #timewindow .tr{display:block;display:none;}
.showall #navbg,.just div#navbg,.infotable tr.nohover:hover,.d .infotable tr.nohover:hover,.susdiv a:hover{background:none}
.showall #menupositioner{z-index:-1;position:absolute;display:none;}
.showall.menu_open #menupositioner,.showall.menu_closing #menupositioner
{visibility:visible;position:relative;z-index:-1}

.simplify #qbox{display:none}
#navbg,.map.simplify #navbg,.simplify #navbg,body #navbg:hover,.factspage #clock0_bg{background:#eee}
.simplify .tr{visibility:hidden}
.simplify #navbg:hover .tr,.simplify .tr:hover{display:none;}
.simplify #navbg:hover .tr{opacity:1}
.simplify section,
.simplify .tr,.simplify .susdiv,.hide,.just .tr,.factspage #lC,.prnt{display:none}
.simplify #timewindow .tr{display:block;display:none;}
.simplify #navbg,.just div#navbg,.infotable tr.nohover:hover,.d .infotable tr.nohover:hover,.susdiv a:hover{background:none}
.simplify #menupositioner{z-index:-1;position:absolute;display:none;}
.simplify.menu_open #menupositioner,.simplify.menu_closing #menupositioner
{visibility:visible;position:relative;z-index:-1}
/*常に適用終*/


}
