css:
@-moz-document url-prefix("https://nitro.powerhrg.com/runway") {
  html {
      background-color: #000000;
  }

  nav.primary {
      background-color: #2b2c31 !important;
      border-right-color: #494949 !important;
  }

  li.add-a-team-dropdown {
      border-top-color: #dddddd;
  }

  [class^="pb_body_kit"] {
      color: #dddddd !important;
  }

  span.carat {
      color: #dddddd !important;
  }

  li.active a {
      background-color: #b3b3b3 !important;
  }
  .nav > li > a:hover, div.drawer nav.primary .navigation-options > li > a:hover, .nav > li > a:focus, div.drawer nav.primary .navigation-options > li > a:focus {
      background-color: #494949 !important;
  }

  ul li.pb_item_kit {
      background-color: #494949 !important;
  }

  div.search-result-display span {
      color: #86b9ff !important;
  }

  mark {
      background-color: #86b9ff;
  }

  header.team-index-header h1 {
      color: #dddddd !important;
  }

  ol.team-index .team-card {
      background-color: #2b2c31 !important;
      border-color: #494949;
  }

  ol.team-index .team-card li {
      color: #dddddd;
  }

  .team-dropdown li a{
      color: #dddddd !important;
  }

  section.team-settings {
      background-color: #2b2c31;
  }

  section.team-settings div.pb_list_kit_ li.pb_item_kit {
      background-color: #2b2c31 !important;
  }

  section.team-settings li.pb_item_kit h3 {
      color: #86b9ff !important;
  }

  section.team-settings input.text_input {
      background-color: #2b2c31;
      border-color: #b3b3b3;
      color: #dddddd;
  }

  .swal2-modal {
      background-color: #2b2c31;
  }

  .swal2-title, .swal2-content {
      color: #dddddd !important;
  }


  main, .backlogs-show {
      background-color: #000000;
  }

  div.active-sprint header.active-sprint-header h1,
  div.backlogs-show header.backlogs-show-header h1,
  main div.active-sprint h2,
  .backlog-filter-condensed .backlog-filter-condensed-details .backlog-filter-condensed-title {
      color: #dddddd !important;
  }

  div.active-sprint-column {
      background-color: #2b2c31 !important;
  }

  div.active-sprint-column::before {
      background: linear-gradient(90deg, rgba(56,0,142,1) 0%, rgba(0,19,200,1) 20%, rgba(0,161,152,1) 100%) !important;
  }

  .active-sprint .active-sprint-columns .active-sprint-column .active-sprint-column-header {
      border-bottom: 1px solid #000000;
  }

  span.active-sprint-column-status-name {
      color: #dddddd;
  }

  .active-sprint .active-sprint-columns .active-sprint-column .active-sprint-column-cards li.backlog-item-card {
      border: 1px solid #494949;
      background: #000000 !important;
  }

  section.active-sprint-columns div.active-sprint-column ol.active-sprint-column-cards li.backlog-item-card div.backlog-item-card-description div.backlog-item-card-title {
      color: #dddddd !important;
  }

  section.active-sprint-columns div.active-sprint-column ol.active-sprint-column-cards li.backlog-item-card div.backlog-item-card-description span.backlog-item-card-id {
      color: #dddddd !important;
  }

  .active-sprint .active-sprint-columns .active-sprint-column .active-sprint-column-cards .backlog-item-card::after {
      background-color: #494949;
  }

  [class^="pb_badge_kit"] {
      border-color: #494949 !important;
  }

  img.avatar, div.monogram-avatar {
      border-color: #494949 !important;
      color: #000000 !important;
  }

  .active-sprint .active-sprint-columns .active-sprint-column .active-sprint-column-cards .backlog-item-card span.backlog-item-card-estimate {
      background-color: #2b2c31 !important;
      color: #dddddd !important;
      border-color: #494949 !important;
  }

  .active-sprint .active-sprint-columns .active-sprint-column .active-sprint-column-backlog-item-count {
      background-color: #2b2c31 !important;
      color: #dddddd !important;
      border-color: #494949 !important;
  }




  button.add-sprint-button, [data-nitro-search-filter-toggle=""] {
      background-color: #b3b3b3 !important;
      border-color: #494949 !important;
  }

  li.backlog-item-condensed {
      background-color: #2b2c31 !important;
      border-color: #494949;
  }

  li.backlog-item-condensed:hover {
      background-color: #494949 !important;

  }

  .backlog-item-condensed .backlog-item-condensed-details span.backlog-item-condensed-id {
      color: #dddddd !important;
  }

  .backlog-item-condensed-title {
      color: #86b9ff;
  }

  li.backlog-item-condensed div.backlog-item-condensed-meta div.backlog-item-condensed-estimate{
      background-color: #000000 !important;
      color: #dddddd !important;
  }



  div.modal-content {
      box-shadow: 0 0 150px 75px #0a2243;
  }

  div.loading-state, div.modal-content {
      background-color: #000000 !important;
  }

  svg.fa-spinner, p.loading {
      color: #b3b3b3 !important;
  }

  ol.active {
      background-color: #2b2c31 !important;
  }

  .backlog-item-modal header.backlog-item-modal-header {
      border-bottom-color: #494949 !important;
  }

  h1.backlog-item-modal-title {
      color: #dddddd !important;
  }

  div.pb_circle_icon_button_kit button{
      background-color: #2b2c31 !important;
      color: #86b9ff !important;
  }

  div.backlog-item-modal-actions.show {
      background-color: #494949 !important;
      color: #dddddd;
      border-color: #2b2c31 !important;
  }

  [class*="pb_list_kit"] li {
      border-bottom-color: #2b2c31 !important;
  }

  .backlog-item-modal div.backlog-item-modal-content-and-nav nav.backlog-item-modal-navigation {
      border-right-color: #494949 !important;
  }

  section#details dl.backlog-item-modal-attributes div.backlog-item-modal-attribute-group dd.backlog-item-modal-attribute {
      color: #dddddd !important;
  }

  ol.backlog-item-modal-assignee-names li {
      color: #dddddd !important;
  }

  li.persisted-file-attachment {
      background-color: #2b2c31 !important;
      border-color: #494949 !important;
  }

  li.persisted-file-attachment p span {
      color: #86b9ff !important;
  }

  li.persisted-file-attachment p span.persisted-file-attachment-meta-info {
      color: #dddddd !important;
  }
  
  pre {
      background-color: #2b2c31 !important;
      border-color: #494949;
  }

  figure.active-sprint-column-indicator {
      background-color: #2b2c31 !important;
  }



  div.trix-content {
      color: #dddddd !important;
  }

  div.trix-dialog {
      background-color: #b3b3b3 !important;
  }

  input.trix-input, input.trix-button {
      background-color: #b3b3b3 !important;
      border-color: #494949;
  }

  span.author-name {
      color: #dddddd !important;
  }

  span.select2-container {
      background-color: #2b2c31;
      border: 1px solid #494949 !important;
  }

  li.select2-selection__choice {
      background-color: #dddddd !important;
      border-color: #b3b3b3 !important;
  }

  span.select2-selection__choice__remove {
      border-color: #b3b3b3 !important;
  }

  .select2-results {
      color: #dddddd !important;
  }
  
  trix-toolbar div.trix-button-row,
  trix-editor {
    background-color: #000000 !important;
	border-color: #494949 !important;
    color: #dddddd !important;
  }

  trix-toolbar div.trix-button-row span button {
      background-color: #b3b3b3;
      border-top: none;
      border-bottom: none;
      border-color: #494949 !important;    
  }

  trix-editor.comment-field,
  trix-editor.description-field{
      color: #dddddd;
      border-color: #494949;
  }

  div.backlog-item-modal-form textarea,
  div.backlog-item-modal-form input,
  div.backlog-item-modal-form select {
      background: none !important;
      background-color: #2b2c31 !important;
      border: 1px solid #494949 !important;
      color: #dddddd !important;
  }

  button.cancel-button {
      background-color: #b3b3b3;
      border-color: #494949 !important;
  }


  li.backlog-item-modal-action.active a {
      background-color: #2b2c31 !important;
      color: #dddddd !important;
  }

  ol.backlog-item-history-list li.history-record div.history-record-details dl.history-record-attribute-list div.history-record-attribute span.history-record-attribute-label-and-value dt.history-record-attribute-label,
  ol.backlog-item-history-list li.history-record div.history-record-details dl.history-record-attribute-list div.history-record-attribute span.history-record-attribute-label-and-value dd.history-record-attribute-value,
  ol.backlog-item-history-list li.history-record div.history-record-details dl.history-record-attribute-list div.history-record-attribute span.history-record-attribute-label-and-value dd details.history-record-description-expander summary.history-record-description-summary,
  ol.backlog-item-history-list li.history-record div.history-record-details dl.history-record-attribute-list div.history-record-attribute span.history-record-attribute-label-and-value dd details.history-record-description-expander div.history-record-description-value {
      color: #dddddd !important;
  }

  summary::marker {
      color: #dddddd;
  }

  li.history-record {
      border-bottom-color: #dddddd !important;
  }


  span.sprint-dropdown-label {
      color: #dddddd;
  }

  div.dropdown.closed-sprints a {
      background-color: #b3b3b3;
      border-color: #494949 !important;
  }

  ol.dropdown-menu {
      background-color: #494949;
      border-color: #494949;
  }
}
