css:
@-moz-document domain("v3rmillion.net") {
/* Ads System */
ins {
    display: none !important;
}
div#sharingPlace {
    display: none !important;
}

/* Top Image logo */
#logo img {
    max-height: 45px;
    margin-left: -100%;
    visibility: hidden;
}

/* General Stuff */

body {
    height: 100%;
    background-image: url(https://imgur.com/pjdaSSM.png) !important;
    background-attachment: fixed !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-size: cover;
    background-color: #101010;
    overflow-y: scroll;
}

#bridge {
    background: #508eff3d;
    height: 45px;
    border-bottom: 5px solid #66e1fb99;
}

#wrapper {
    background: #0a0a0a00;
    padding: 20px 0;
}

.navigation {
    border-radius: 14px;
    background: #28a2e83b;
    border: 0px;
    margin: auto auto 15px;
}
#container {
    width: 90%;
    text-align: left;
    line-height: 1.4;
    margin: 0 auto;
    margin-left: 30px;
}
#panel {
    float: right;
    margin-right: 75px;
}
.ddm_anchor {
    position: relative;
    float: left;
    height: 45px;
    margin-right: -150px;
}
ul.panel_links {
    list-style: none;
    float: right;
    padding: 0;
    margin: 0;
    margin-left: -200px;
}

#content {
    background: linear-gradient(360deg, rgba(2,0,36,0) 0%, rgba(0,0,0,0) 0%, rgba(255,76,0,0.2665441176470589) 0%, rgba(230,132,67,0.28335084033613445) 0%, rgba(0,142,255,0.227328431372549) 100%);
    border: 0px solid #2a2a2a;
    border-radius: 14px;
    padding: 20px;
    padding-bottom: 0;
    margin-bottom: 20px;
    overflow: hidden;
}

table {
    color: #73ead4;
    font-size: 16px;
}

.trow1, .trow2 {
    background: #20202000;
    border-bottom: 1px solid #ea99568f;
}

.tborder {
    background: #0000003d;
    margin: auto;
}

.thead {
    background: linear-gradient(360deg, rgba(2,0,36,0) 0%, rgb(0 0 0 / 0%) 0%, rgba(255,76,0,0.2665441176470589) 0%, rgba(230,132,67,0.28335084033613445) 0%, rgba(0,142,255,0.227328431372549) 20%);
    padding: 10px;
}

.tcat {
    background: #ef91423d;
    padding: 6px 10px;
    font-size: 12px;
}

.tfoot {
    padding: 10px;
    background: #62b7f336;
    color: #e0e0e0;
}

#footer {
    display: none;
}

.profile_header {
    background: #29ffff1a;
}

ul.bottommenu {
    display: inline-block;
    vertical-align: top;
    background: #20202000;
}

.cRem a:visited {
    color: #5ee6a7 !important;
}

.cRem a:link {
    color: #49f1e9 !important;
}
.trow_sep {
    background: #80f7fd33;
    color: #94c8ec;
    padding: 6px;
}

.reputation_positive {
    color: #87e0ef;
}

.reputation_negative {
    color: #ff6464;
}

fieldset {
    padding: 12px;
    border: 0px;
    margin: 0;
}

.panel_buttons {
    z-index: 9999;
    display: none;
    position: absolute;
    top: 45px;
    background: #63d5ef21;
    width: 180px;
    overflow: hidden;
    margin: 0;
    padding: 10px 0;
    border-top: 5px solid #66e8fb9c;
    text-align: left;
}

ul.menu li ul {
    z-index: 9999;
    position: absolute;
    background: #63d5ef21;
    padding: 10px 0;
    border-top: 5px solid #66e8fb9c;
}

a.button:link, a.button:visited, a.button:active {
    background: #00ffd030;
    display: inline-block;
    padding: 4px 8px;
    margin: 2px 2px 6px;
    border: 1px solid #cd181800;
}

.ddm_anchor:hover, .ddm_anchor a:hover {
    background: #6af9f90f;
}

ul.menu li:hover a, ul.menu li a:hover {
    background: #6af9f90f;
}

ul.menu li ul li a:hover {
    background: rgb(106 249 249);
    color: #fff;
}

ul.menu li ul li a:link, ul.menu li ul li a:visited {
    background: #2a2a2a00;
    padding: 10px 15px;
}

.inline_row a {
    color: #7fc8ead6;
    font-size: 90%;
}
.inline_row a:visited {
    color: #e09e26a1;
    font-size: 90%;
}
.forum_on {
    color: #18accd;
}

.sub_control {
    background-color: #20202000;
    border: 1px solid #d3d3d3;
    border-radius: 12px;
    padding: 5px 10px;
}
.sub_control:hover {
    background-color: #5ad7f95c;
    text-decoration: none;
    -moz-transition: background-color 1s ease-in;
    -o-transition: background-color 1s ease-in;
    -webkit-transition: background-color 0.1s ease-in;
}

a:hover, a:active {
    color: #66e1fb;
    text-decoration: none;
    -moz-transition: color 1s ease-in;
    -o-transition: color 1s ease-in;
    -webkit-transition: color 0.1s ease-in;
}

.pagination a {
    background: #00caff40;
    border: 1px solid #40404000;
    color: #fff;
}
.pagination a:hover {
    background: #f9cd7c73;
    color: #fff;
    border-color: #40404000;
    text-decoration: none;
}
.pagination .pagination_current {
    background: #73ddf58c;
    color: #fff;
    border: 1px solid #40404000;
    border-radius: 6px;
}

button, input.button {
    background: #e6733061;
    border: 1px solid #cd181800;
    padding: 5px 8px;
    color: #fff;
}
input.textbox {
    background: #00000078;
    border: 1px solid #2a2a2a00;
    padding: 5px;
}

select {
    background: #10101052;
    border: 1px solid #2a2a2a00;
    padding: 5px;
    font-size: 14px;
}


/* Reputation & Thread */

._plus {
    background-color: #1a1a1a00;
    color: #5ba8ff;
    border: 1px solid #5ba8ff00;
}
._minus {
    background-color: #1a1a1a00;
    color: #ff5b5b;
    border: 1px solid #5ba8ff00;
}

.thread_head {
    background: #00ffe71a !important;
    border-bottom: 5px solid #2fbfd46b !important;
}
hr {
    border: none;
    width: 100.50%;
    margin-left: -4px;
    height: 3px;
    background-color: transparent;
    border-bottom: solid thin #4ec4f1;
}

.post .post_head {
    font-size: 11px;
    padding: 10px;
    border-top: 15px solid #ffffff00;
}
.post .post_author {
    border-bottom: 3px solid #f5864766;
    background: #58ecf31f;
    padding: 5px;
}
.post_controls {
    margin-top: 3px;
    background: #64c2ef1f;
    border-bottom: 1px solid #00bbffb3;
    padding: 5px;
}
.post .post_author div.author_statistics {
    float: right;
    font-size: 14px;
    background-color: transparent;
    color: #5dffff !important;
    border-radius: 6px !important;
    line-height: 1.3;
}
.postlink {
    background-color: #20202000;
    border: solid thin #cd181800;
}
.postbit_buttons>a:link, .postbit_buttons>a:hover, .postbit_buttons>a:visited, .postbit_buttons>a:active {
    background: #00caff40;
    border: 1px solid #cd181800;
}
form #message {
    width: 500px;
    background-color: #00000045;
}

.buttons {
    background-color: #00ffd030;
    border: 1px solid #da2c2c00;
    border-radius: 3px;
}
.buttons:hover {
    background-color: #4ceaff66;
    border-radius: 6px;
    color: #fff;
}

button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons>a, a.button {
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background-color: #00000045;
    border-color: #00000000;
}
blockquote cite span {
    float: right;
    font-weight: 400;
    font-size: 12px;
    /* color: #16beff; */
    color: #ffad16 !important;
}
.hiddenQuote {
    max-height: 300px;
    overflow: hidden;
    text-overflow: ellipsis;
    background: #77d9ff26 !important;
    background: -moz-linear-gradient(top,rgba(16,16,16,1) 0%,rgba(16,16,16,1) 87%,rgba(61,61,61,1) 100%);
    background: -webkit-linear-gradient(top,rgb(16 16 16 / 0%) 0%,rgb(16 16 16, 0) 87%,rgb(61 61 61, 0) 100%);
    background: linear-gradient(to bottom,rgb(0 0 0) 0%,rgb(16 16 16) 87%,rgb(255 255 255) 100%);
    cursor: pointer;
}

/* Classic theme edits */
.post.classic .post_author {
    border: 1px solid #ddd0 !important;
    margin: 0 1% 15px 0;
    float: left;
}
.post .post_author div.author_avatar img {
    padding: 5px;
    border: 1px solid #2a2a2a00 !important;
    background: #1a1a1a00 !important;
}

/* Notification box */
th.thead {
    background: #009292ad;
}
td.tfoot.smalltext {
    background-color: #009292ad;
}
tbody.alert {
    background: #000000db;
}
.post .post_head span.post_date {
    color: #16beff;
}

Style Attribute {
    margin-right: 5px;
    background-color: #20202000 !important;
    display: inline-block;
    min-width: 40px;
    border-radius: 3px;
    text-align: center;
    padding: 1px 5px 1px 5px;
}

/* Other Stuff x2 */

.spoiler_header {
    background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(0,0,0,0) 0%, rgba(255,76,0,0.15029761904761907) 0%, rgba(230,132,67,0.19791666666666663) 31%, rgba(0,142,255,0.19511554621848737) 79%) !important;
    border-top: 1px solid #59e3f93b;
    border-bottom: 1px solid #ff960026;
}
.spoiler_body {
    padding: 4px;
    border: 1px solid #a0a0a000 !important;
    border-top: 0;
    color: #cccccc;
}

div[style*="background-color: #202020;"] {
    background-color: unset !important;
}

/* Like Counter */

.tooltiptext {
    text-align: center;
    padding: 4px 0;
    border-radius: 6px;
    visibility: hidden;
    color: #77eafdbd !important;
    width: 110px;
    background-color: #10101047;
    position: absolute;
    margin-top: 18px;
    z-index: 10;
}

/* Top alert */

button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons>a, a.button {
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    color: white !important;
    border-radius: 6px;
    background-color: #34abf13b !important;
    border-color: #00000000 !important;
}
.red_alert:hover {
    -moz-transition: background-color 0.5s ease-in;
    -o-transition: background-color 0.4s ease-in;
    -webkit-transition: background-color 0.3s ease-in;
    background-color: #d266114d !Important;
}

.red_alert a:link, .red_alert a:visited, .red_alert a:hover, .red_alert a:active {
    color: #00dcff !important;
}

/*
Style Attribute {
    color: #71cfe4;
}*/

span[style*="color: #CD1818;"] {
    color: #5de4e4 !Important;
}

/* Like system */
div#negative.likebar {
    background-color: #ff6868db !important;
    height: 100%;
    float: left;
}
div#positive.likebar {
    background-color: #75ff75a1 !important;
    height: 100%;
    float: left;
}
.likebar .tooltiptext {
    text-align: center;
    padding: 5px 0;
    border-radius: 2px;
    visibility: hidden;
    color: #6dd06d !important;
    background-color: #10101075;
    border-radius: 6px;
    position: absolute;
    margin-top: 15px;
    z-index: 10;
}

/* buddy ignore/add list and other related stuff */
.select2-container-multi .select2-choices {
    height: auto !important;
    padding: 0 5px 0 0;
    position: relative;
    border: 1px solid #0000;
    background-color: #6eedf74a !important;
    border-radius: 6px;
    background-image: linear-gradient(to bottom,#eee0 1%,#fff0 15%);
}
.select2-default {
    color: #91d6d6!important;
}
.select2-container-multi .select2-choices .select2-search-field input.select2-active {
    background: transparent !important;
}
.select2-drop-active {
    border: 1px solid #00000000 !important;
    border-top: none;
    border-radius: 6px;
}
.select2-drop {
    width: 100%;
    margin-top: -1px;
    position: absolute;
    top: 100%;
    background: #62daf540 !important;
    color: #FFF;
    border: 0px solid #aaa;
}
.select2-results .select2-highlighted {
    background: #d2d2d259 !important;
    color: #88e9ff;
}
.select2-container-multi .select2-choices .select2-search-field input {
    padding: 5px;
    margin: 1px 0;
    font-family: sans-serif;
    font-size: 100%;
    color: #86d2ef !important;
    outline: 0;
    border: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
    background: 0 0!important;
}
.select2-container-multi .select2-choices .select2-search-choice {
    background-image: unset !important;
    padding: 3px 5px 3px 18px;
    margin: 3px 0 3px 5px;
    position: relative;
    line-height: 14px;
    /* color: #fff; */
    font-style: normal;
    cursor: default;
    border: 2.5px solid transparent;
    border-radius: 5px;
    -webkit-box-shadow: none;
    box-shadow: none;
    background-clip: padding-box;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #ffbc6f4a !important;
    background-image: unset !important;
}
.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-ajax-error, .select2-results .select2-selection-limit {
    background: #7ccfff38 !important;
    display: list-item;
    padding-left: 5px;
}
.select2-search input {
    width: 100%;
    height: auto !important;
    min-height: 26px;
    color: #77e7f9 !important;
    padding: 4px 20px 4px 5px;
    background-color: #6eedf74a !important;
    margin: 0;
    outline: 0;
    font-family: sans-serif;
    font-size: 1em;
    border: 1px solid #aaa0;
    border-radius: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
    background: unset !important;
}
.select2-container .select2-choice .select2-arrow {
    display: inline-block;
    width: 18px;
    height: 100%;
    border-left: 1px solid transparent;
    border-radius: 0 4px 4px 0;
    background-clip: padding-box;
    background: transparent !important;
}
.select2-dropdown-open .select2-choice {
    border-bottom-color: transparent;
    -webkit-box-shadow: 0 1px 0 #fff0 inset !important;
    box-shadow: 0 1px 0 #fff0 inset !important;
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
    background-color: #6eedf74a !important;
    background: unset !important;
}
.select2-container .select2-choice {
    display: block;
    height: 26px;
    padding: 0 0 0 8px;
    overflow: hidden;
    position: relative;
    border: 1px solid #42f9f585 !important;
    /* color: #FFF; */
    white-space: nowrap;
    line-height: 26px;
    color: #444;
    text-decoration: none;
    border-radius: 4px;
    background: unset !important;
    background-clip: padding-box;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.select2-search input.select2-active {
    background: transparent !important;
}

.post .post_head span.edited_post a {
    color: #00b8ff;
}
.post .post_head span.edited_post {
    font-size: 10px;
    color: #d4d4d4;
}

.popup_menu .popup_item {
    background: transparent;
    color: #fff;
}
.popup_menu .popup_item:hover {
    background: #7afff929;
    color: #ffffff;
}

.usercp_container {
    margin: 5px;
    padding: 8px;
    border: 1px solid #52b3ffa3;
}

tbody#boardstats_e {
    color: #ffcd8f;
}
}

