css:
@-moz-document domain("blaseball.com") {
body,
.open-sans-font{
  font-family: 'Open Sans', sans-serif;
}
    .Main,
    .Main-Body,
    .Main-Body-Intro,
    .Header-Logo
    {
        background: #e9e9e9 !important;
        color: #fff;
    }
    .Vote-Form-Inputs-Amount,
    .Vote-Form {
        background: #e9e9e9 !important;
        color: #000 !important;
    }
    .About-Ticket,
    .About-QuoteAuthor,
    .About-Ticket :hover,
    .About-Ticket:hover,
    .Header-Logo, 
    .Header-Social-Patreon-Icon,
    .Header-Social-Follow-Inner,
    .Stubs-All,
    .Stubs-Body {
        color: #000;
    }

    /*Login and Welcome Page*/
    
    /*Welcome Page Text Color*/
    .Main-Body-Intro{
    color: #000
    }
    
    /*Welcome Page Ticket Icon*/
    .About-Ticket{
    filter: hue-rotate(110deg) drop-shadow(1px 1px 5px)
    }
    
    /*About Lower Text Background*/
    .About-Body-Container{
    background: #f7a8b8 !important;
    filter: drop-shadow(2px 2px 2px #000);
    }

    /*FAQ Page*/
    .FAQ,
    .FAQ-Content,
    .FAQ-Question,
    .FAQ-Question-Header{
        color: #000;
        font-family: sans-serif !important
    }
    
    /*Header Era Name*/
    .Header-Logo-Era {
        filter: saturate(200%) drop-shadow(0px 0px 1px #000)
    }
    
    /*Header Era Subtitle*/
    .Header-Logo-SubEra {
        filter: saturate(200%) drop-shadow(0px 0px 1px #000)
    }

    /*Main site navbar background and shadow*/
    .Navigation,
    .Navigation-Header,
    .Navigation-Header-SubEra {
        color: #000;
        background: #f7a8b8;
        border-radius: 5px;
        padding: 10px;
        filter: drop-shadow(1px 1px 2px #000)
    }

    /*Main site navbar buttons, ticker background, site title beta tag, postseason nav button*/
    .Navigation-CurrencyButton,
    .Navigation-Button,
    .Header-Logo-Tag,
    .PlayoffSetup-Navigation-Button,
    .Decrees-Submit-Button,
    .Decrees-Option-Button,
    .Blessings-Option-Button,
    .Blessings-Submit-Button{
        background: #55cdfc;
        color: #000;
        border-style: solid;
        border-color: #fff !important;
        border-width: 2px !important;
        filter: drop-shadow(0px 0px 1px #000)
    }
    
    /*Ticker Body*/
    .EventTicker-Display{
        background: #55cdfc;
        color: #000;
        filter: drop-shadow(0px 0px 1px #000)
    }

    /*Ticker items*/
    .EventTicker-Item {
        background: #55cdfc;
        color: #000;
    }

    /*Navbar team emoji and idol buttons, separate to maintain original background colors*/
    .Navigation-IdolizedPlayer,
    .Navigation-FavoriteTeamEmoji {
        border-style: solid;
        border-color: #fff !important;
        border-width: 2px !important;
        filter: drop-shadow(0px 0px 1px #000) saturate(200%)
    }


    
    /*Site header socials, patreon, FAQ buttons*/
    .Header-Social-Patreon-Inner,
    .Header-Social-Follow-Inner,
    .Header-Social-Faq-Inner {
        color: #000;
        background: #f7a8b8;
        filter: drop-shadow(1px 1px 2px #000)
    }
 
    /*Hover for header icons*/
    .Header-Social-Patreon-Icon:hover,
    .Header-Social-Follow-Inner a:hover,
    .Header-Social-Faq-Icon:hover{
        color: #55cdfc;
        filter: drop-shadow(0px 0px 1px #000);
    }
    
    /*Socials element lower triangle*/
    .Header-Social-Follow-Triangle {
        border-top-color: #cc8c99;
        filter: drop-shadow(1px 1px 2px #000)
    }

    /*Main nav dropdown*/
    .Navigation-Dropdown-Content{
            background: #f7a8b8;
            filter: drop-shadow(1px 1px 2px #000)
    }

    /*Hall of Flame*/
    
    /*Hall of Flame Header Text*/
    .HallOfFlame-Header
    {
    color: #f7a8b8;
    text-shadow: 0px 0px 3px #000, 0px 0px 5px #f00;
    font-weight: bold;
    }

    /*Hall of Flame Leaderboard Position Number*/
    .HallOfFlame-Player-Position{
    color: #f7a8b8;
    text-shadow: 1px 1px 2px #970101, -1px 0px 2px #000;
    font-weight: bold;
    }
    /*Hall of Flame Header Skull Icons*/
    .HallOfFlame-Header-Deceased-Icon {
    filter: hue-rotate(320deg) saturate(40%) brightness(140%) contrast(160%) drop-shadow(1px 1px 3px #000);
    }
    
    /*Hall of Flame Subheader*/
    .HallOfFlame-Description{
    color: #000;
    font-family: sans-serif;
    font-weight: bold;
    }

    /*Hall of Flame Player Containers*/
    .HallOfFlame-Player{
    color: #000;
    padding: 5px;
    filter: drop-shadow(0px 0px 3px);
    background: #fff !important;
    margin: 5px
    }
    .HallOfFlame-Player:hover{
    background-color: #ffcbcb !important;
    padding: 5px;
    filter: drop-shadow(0px 0px 5px #000);
    text-shadow: 0px 0px 2px #000;
    margin: 5px;
    color: #000
    }
    
    /*Hall of Flame Player Tribute Count*/
    .HallOfFlame-Player-Tributes{
    text-shadow: 1px 1px 1px #5282ac, -0px -0px 2px #36688e;
    color: #55cdfc;
    font-family: monospace;
    font-weight: bold;
    font-size: 22px
    }

    /*End of Season Pages*/
    .GodsDay-Header,
    .PlayoffsEnd-Body,
    .Recap-Header,
    .Recap-Name,
    .Recap-ResultsHeader {
        color:#000
    }
    
    /*Playoff Bracket Container*/
    /*The wildcard match*/
    .PlayoffSetup-Matchup:nth-child(2){
        background-color: #55cdfc;
        color: #000;
        filter: drop-shadow(-2px 2px 2px #000);
    }
    
    /*The rest of the matches*/
    .PlayoffSetup-Matchup{
        background-color: #f7a8b8;
        color: #000;
        filter: drop-shadow(-2px 2px 2px #000);
    }
    .Blessings-Option-SubheaderHyperlink{
    color: #b77d80;
    text-shadow: 1px 1px 0px #a7eff0 ;
    }

    /*Playoff Bracket Team Icons*/
    .PlayoffSetup-MatchupTeam-Icon{
        filter: drop-shadow(0px 0px 2px #000);
    }

    .BigDeal-All,
    .Leaderboard-Player[class^="Leaderboard-Shelled"] {
        filter: hue-rotate(290deg) brightness(200%) saturate(80%) drop-shadow(1px 1px 2px #000);
    }

    .Navigation-CurrencyButton:hover,
    .Navigation-Button:hover {
        border-color: #000;
        color: #000;
    }
    .Navigation-Button.Navigation-Button-Current {
        color: #fff;
        background: #000;
    }
    .Navigation-Bar-Menu {
        color: #000;
    }
    .Navigation-Bar-Menu-Toggle {
        background: #c8c8c8;
    }
    .Navigation-Bar-Menu-Toggle:after {
        border-top-color: #000;
    }
    .Navigation-Bar-Menu-List {
        background: #9e9e9e;
    }
    .Navigation-Bar-Menu-List .Navigation-CurrencyButton,
    .Navigation-Bar-Menu-List .Navigation-Button {
        background: rgba(207, 207, 207, .64);
    }
    /* fix SVG resizing issue */
    .Navigation-Bar-Menu-List svg {
        min-width: 1em;
    }
    .Countdown {
        color: #fff;
    }
    .Countdown > .Countdown-Number {
        color: #fff;
        background: #fff;
    }
    .Main-Footer-Link,
    .Main-Footer-Advertise-Inner,
    .Main-Footer-Advertise-Inner{
        color: #000 !important;
        border: 1px solid #000;
        background: #fff;
        margin: 5px;
        border-radius: 20px !important
    }
.Main-Footer-Advertise-Icon{
        color: #000 !important;
        background: #fff;
        margin: 5px;   
}
    .Main-Footer-Link:hover {
        color: #b0b0b0;
    }
    .btn.btn-success {
        border-color: transparent;
    }
    .About-Body-Container {
        background: rgba(255, 0, 0, .64);
    }
    .Hiatus-Body a {
        color: #ffe000;
    }
    .BigDeal-Icon {
        color: #f00;
    }
    .Stubs-Body,
    .Standings-Division-Header {
        text-align: center;
        font-weight: bold;
        
    }
    .Standings-Team:nth-child(2n+1) {
        background: #ffcbcb;
        color: #000;
        filter: drop-shadow(0px 0px 3px #000) !important;
        margin: 3px
    }
    .Standings-Team:nth-child(2n+2) {
        background: #a9f9f0;
        color: #000;
        filter: drop-shadow(0px 0px 3px #000) !important;
        margin: 3px
    }
    .Standings-Team:hover {
        background: #a9f9f0;
    }
    .Standings-League,
    .Standings-Division-Header {
        color: #000;
    }
    .Standings-Team-Name {
        filter: drop-shadow(1px 0px 1.2px #000) saturate(50%) !important;
    }
    .Standings-Team-Standings {
        filter: brightness(40%) saturate(300%);
    }
    .Shop-Description,
    .Offseason-Description,
    .Offseason-Header {
        color: #000;
    }
    .Shop-Item,
    .Decrees-Option,
    .Blessings-Option {
        background: #c0edff;
        color: #000;
        border: 1px solid #000;
        filter: drop-shadow(3px 3px 2px #000)
    }
    .Decrees-Option .Decrees-Option-Icon,
    .Blessings-Option .Blessings-Option-Icon {
        fill: #000;
    }
    .Decrees-Option-Button,
    .Blessings-Option-Button {
        background: #000;
        color: #fff;
    }
    .Decrees-Option:hover,
    .Blessings-Option:hover {
        color: #222;
    }
    .Decrees-Option:hover .Decrees-Option-Icon {
        fill: #222;
    }
    .TheBook-RedactGroup,
    .TheBook-Redacted {

        background: hsla(348, 83%, 81%, .65) !important;
        display: inline-block
    }
    .Decrees-Option.Decrees-Option-Current,
    .Blessings-Option.Blessings-Option-Current {
        background: #000;
        color: #fff;
        filter: none;
        -webkit-filter: none;
    }
    .Decrees-Option.Decrees-Option-Current .Decrees-Option-Icon {
        fill: #fff;
    }
    .Decrees-Option.Decrees-Option-Current .Decrees-Option-Button,
    .Blessings-Option.Blessings-Option-Current .Blessings-Option-Button {
        background: #fff;
        color: #000;
    }
    .Advertisement-SiteHeader,
    .Advertisement-SiteHeader-Callout:hover,
    .DailySchedule-Body,
    .GodsDay-Body {
        color: #000;
    }
    .DailySchedule-Header {
        color: #000;
    }
    .DailySchedule-Number {
        background-color: #000;
        color: #fff;
        border-radius: 5px;
    }
    .Countdown,
    .Countdown-Number {
        color: #000 !important;
        border-radius: 5px;
    }
    .Advertisement-SiteHeader-Callout {
        color: #000;
    }
    .GameWidget {
        background: none;
    }
    .GameWidget-PlayerStatusIcon {
        filter: invert(100%);
    }

    .GameWidget-Upcoming-Info,
    .GameWidget-Header-Wrapper,
    .GameWidget-Display-Body .GameWidget-Log,
    .Recap-Decree-Container,
    .Recap-Blessing-Container {
        background: #c0edff;
        border: 1px solid #000;
        border-radius: 7px;
        color: #000;
        filter: drop-shadow(3px 3px 2px #000);
        font-family: sans-serif !important;
    }
    .GameWidget-Outcome {
        background: #fff;
        color: #000;
        border: 1px solid #000;
        filter: drop-shadow(3px 3px 2px #000);
        font-family: sans-serif !important;
        font-weight: bold;
    }
    .Leaderboard-Description,
    .Leaderboard-List,
    .Shop-Item-Link,
    .Shop-Header,
    .Shop-Description,
    .Decrees-Header,
    .Decrees-Description,
    .Blessings-Header,
    .Blessings-Description,
    .TheBook-All {
        color: #000;
    }
    .Leaderboard-Player-Container {
        background-color: #fff0;
        border-radius: 5px;
        padding: 15px;
        }
    .Leaderboard-Player {
    filter: drop-shadow(0px 0px 3px);
    background: #fff !important;
    margin: 5px
    }
    

    /*Bulletin board containers*/
    .Bulletin-Item {
        background-color: rgb(255, 213, 221) !important;
        color: #000;
        border: 1px solid #000;
        filter: drop-shadow(3px 3px 2px #000);
    }
    
    /*Bulletin Fonts*/
    
    /*Main font*/
    .Bulletin-Item-Contents-Text{
        color:#000;
        font-family: sans-serif !important
    }
    /*Italics*/
    .Bulletin-Item-Contents-Text-Soft{
        color: #000 !important;
        text-shadow: 1px 1px 3px #fff
    }
    /*Links*/
    .Bulletin-Serif{
        color: #07acec !important;
        font-family: sans-serif !important;
        text-shadow: 1px 1px 3px #fff
    }


    .Shop-Item {
        background-color: #c0edff !important;
        filter: drop-shadow(3px 3px 2px #000)
    }
    .react-toast-notifications__toast {
        filter: drop-shadow(5px 5px 2px #f7a8b8);
    }
    .react-toast-notifications__toast__icon-wrapper {
        background: #55cdfc !important;
    }
    .GameWidget-Display-Visual,
    .GameWidget-Upcoming-Info {
        background: #fff;
        border: 1px solid #000;
        color: #000;
        filter: drop-shadow(3px 3px 2px #000)
    }
    .GameWidget-Log,
    .GameWidget-Upcoming-Bets,
    .Decrees-Option,
    .GameWidget-Log {
        font-family: sans-serif !important;
        background: rgb(255, 213, 221);
        border: 1px solid #000;
        color: #000;
        filter: drop-shadow(3px 3px 2px #000);
        }
    .GameWidget-Header {
        background: rgb(247, 168, 184);
        font-weight: bold;
        border-bottom: 1px solid
    }
    .GameWidget-Log-Header,
    .GameWidget-Upcoming-Header {
        background: rgb(85, 205, 252);
        font-weight: bold;
        border-bottom: 1px solid
    }
    .GameWidget-UpcomingBet {
        background: #fff;
        border: 1px solid #000;
        margin: 10px;
        font-family: sans-serif;
    }
    .WeatherIcon {
    margin: -1px;
    }
    .GameWidget-Upcoming-Label,
    .GameWidget-Upcoming-Weather {
        color: #000;
    }
    .GameWidget-Button,
    .Shop-Item-Button {
        background: #55cdfc !important;
        color: #000 !important;
        border: 1px solid #000 !important;
        margin-bottom: 9px;
        filter: drop-shadow(0px 0px 2px #000)
    }
    .GameWidget-Button:hover,
    .Shop-Item-Button:hover {
        background: #f7a8b8;
        color: #fff;
        border: 1px solid #fff !important;
        }
    .GameWidget-Upcoming-Favorites-Team,
    .GameWidget-ScoreName,
    .GameWidget-WinChance,
    .GameWidget-WinChance-WithBet,
    .GameWidget-Outcome-Callout {
        filter: saturate(200%) brightness(60%);
        font-weight: 400;
    }
    .GameWidget-PlayerLineNameWrapper,
    .GameWidget-Upcoming-Pitchers-Wrapper {
        filter: saturate(200%) brightness(100%) drop-shadow(2px 2px 1px #000);
    }
    .Player-Status {
    background-color: rgba(225, 225, 225, 0.64) !important
    }
    .Player-Deceased-Icon{
    filter: invert(100%)
    }
    .GameWidget-PlayerLineName {
    color: #fff;
    }
    .GameWidget-ScoreRecord,
    .Recap-Decree-Votes,
    .Recap-Blessing-Votes,
    .Vote-Header,
    .form-label,
    .form-control {
        color: #000 !important;
    }
    .Vote-Ticket-Icon {
        color: #fff !important;
        filter: invert(100%);
    }
    .GameWidget-Log-Content,
    .GameWidget-Outcome-Blurb {
        font-family: sans-serif;
    }
    .GameWidget-ScoreTeamColorBar {
        border: 1px solid #000
    }
    .GameWidget-ScoreLine {
        margin-right: 3px
    }
    .GameWidget-Status {
        background-color: #55cdfc;
        color: #fff;
        padding: 10px;
        margin: -1px;
        margin-right: -4px;
        border-radius: 0px;
    }
    .GameWidget-Status--Upcoming {
        background: #55cdfc;
        color: #000;
        margin: -1px
    }
    .GameWidget-Outs-Label,
    .GameWidget-PlayerStatusLabel {
        color: #000;
    }
    .GameWidget-Outs-Dots {
        color: #000;
    }
    .GameWidget-Bases svg .st0 {
        stroke: #000;
    }
    .GameWidget-Bases.first #base1,
    .GameWidget-Bases.third #base3 {
        fill: #f7a8b8;
        filter: drop-shadow(3px 3px 4px #000);
    }
    .GameWidget-Bases.second #base2,
    .GameWidget-Bases.second #base4{
        fill: #55cdfc !important;
        filter: drop-shadow(3px 3px 4px #000);
    }
    .GameWidget-ScoreBet {
        color: #000;
        border: 1px solid #000;
        border-radius: 15px
    }
    .GameWidget-ScoreBet-Bet {
        background: #f7a8b8;
    }
    .GameWidget-ScoreBet-Triangle {
        border-left-color: #fff;
    }
    .GameWidget-ScoreBet-Winnings {
        background: #55cdfc;
        color: #000
    }
    .OminousRedLine{
        filter: hue-rotate(310deg) brightness(400%);
    }
    .Main-Footer-Advertise-Inner,
    .Main-Footer-Advertise-Icon {
        color: #fff;
        border-radius: 5px;
    }
    .Main-Footer-Logo,
    .Modal-Background {
        background: rgba(0, 0, 0, .6)
    }
    .Modal--Generic {
        border-color: #000;
        background: #e9e9e9;
        color: #000;
        border-radius: 10px;
    }
    .Modal-Close {
        color: #000;
    }
    .Modal.Auth .Auth-Input {
        border-color: #000;
        color: #000;
    }
    .Modal.Auth .Auth-Submit,
    .Modal.Auth .Auth-SocialAuth {
        background: #000;
        color: #fff;
    }
    .Modal.Auth .Auth-Input::placeholder {
        color: #000;
    }
    #root .Bet,
    #root .Team-Info {
        color: #000;
    }
    #root .Bet select {
        background: rgba(164, 164, 164, .64);
        color: #000;
    }
    #root .Bet-Form-Inputs-Amount {
        background: rgba(208, 208, 208, .64);
        border-radius: 5px;
    }
    #root .Bet-Form-Inputs-Amount input[type="number"] {
        color: #000;
    }
    #root .Bet-Form-Inputs-Amount-MaxBet,
    #root .Team-Subheader {
        color: #000 !important;
        background: #55cdfc;
        border: 1px solid #000 !important;
        border-radius: 5px;
    }
    #root .Bet-Form-Inputs-Amount-MaxBet:hover,
    #root .Team-Subheader {
        color: #fff;
        background: #f7a8b8;
        border: 1px solid #fff;
    }
    #root .Bet-Form-Teams {
        filter: brightness(150%) saturate(170%) drop-shadow(0px 0px 3px #000);
        text-shadow: 1px 1px 0px #000 !important;
    }
    .Bet-Form-Team-Percentage{
    color: #fff
    }
    #root .Bet-Submit,
    .Vote-Submit,
    .Vote-Form-Inputs-Amount-MaxVote:hover {
        color: #000 !important;
        background: #f7a8b8 !important;
        border: 2px solid #000 !important
    }
    #root .Bet-Submit:hover,
    .Vote-Submit:hover,
    .Vote-Form-Inputs-Amount-MaxVote {
        color: #fff !important;
        background: #55cdfc !important;
        border: 2px solid #fff !important;
    }
    #root .Player-Info-Line {
        color: #000;
    }
    #root .Player-Info-Bio,
    #root .Player-Info-Stats {
        background: none;
    }
    #root .Player-Info-Line:nth-child(2n) {
        background: none;
    }
    #root .Player-Info-Line:nth-child(2n+1) {
        background: rgba(225, 225, 225, .64);
    }
    #root .Player-Info-Items-Box {
        background: rgba(208, 208, 208, .64);
    }
    #root .Team-Player-Line {
        color: #000;
    }
    #root .Team-Attributes,
    #root .Player-Attributes {
        background: #e9e9e9;
        color: #000
    }
    #root .Team-Standing {
        color: #000
    }
    #root .Team-Player-Line:nth-child(2n+1) {
        background: rgba(225, 225, 225, .64);
    }
    #root .Team-Player-Vibe-Neutral,
    #root .Player-Vibe-Neutral {
        color: #888;
    }
    #root .Team-Player-Vibe-Quality,
    #root .Player-Vibe-Quality {
        color: #81a767;
    }
    #root .Team-Player-Vibe-Excellent,
    #root .Player-Vibe-Excellent {
        color: #56ad54;
    }
}
