css:
@-moz-document domain(psdqatar.com) {
  /*Created By: Aaron*/
html{
	scroll-behavior: smooth;
}
::selection {
  background: #a8fcff; /* WebKit/Blink Browsers */
}
::-moz-selection {
  background: #a8fcff; /* Gecko Browsers */
}
Within the selection s * {
  margin: 0px;
  width: auto;
  left: auto;
  right: auto;
  top: auto;
  bottom: auto;
}

::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: rgb(26, 26, 26);
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(45deg, #ffffff, #ffffff);
    border-radius: 5px;
    box-shadow: 0 5px 50px 0 #ffffff inset, 0 5px 50px 0 #ffffff;
}

body {
	background: #3F3F3F;
}

.head {
	background: rgb(32, 32, 32);
}

.menu {
	background: rgb(32, 32, 32);
}

.info img {
	border: 1px solid rgb(255 255 255);
}

.myinfohead{
	background:rgb(31 31 31);
}

.myinfo{
	background:#353938;	
}

#adv a:hover{
		color:rgb(134 134 134);
}

#uname a {
	color: rgb(255 255 255);
}
  
#uname a:hover{
	color: wheat;
	letter-spacing: 1px;
	border-bottom:1px solid wheat;
}

#tamsoy{
		color:rgba(220,220,220,0.9);
}

#active{
	color: rgb(234 226 172);
	border-bottom:2px solid rgb(234 226 172);	
}

.links a:hover{
	color: rgb(207 199 145);
    border-bottom: 2px solid rgb(207 195 145);
}

#subtitle{
	color: rgb(255 255 255);
	color: rgb(242 239 215);
}

#periodicallink{
  	color: #ffffff;
}

#room {
	color: wheat;
}

.forcomment input[type=submit]:hover{
	background:rgb(255 255 255);
	border:1px solid rgb(0 0 0);
	color:rgb(0 0 0);
	border-radius:0px 0px 0px 0px;
}

#viewall:hover{
color:#615b52;
	text-decoration:underline;
	transition:0.3s;
}

