css:
@-moz-document url-prefix("https://eldarya.fandom.com/pl/f/")
  {
      /* Pozwoliłam sobie zmienić tło na półprzezroczyste */
.post-details-desktop
      {
          background-color:rgba(0,0,0,0.5);
        	color:#c5c5c5;
      }
.post-details__load-more-button
      {
          background-color:rgba(0,0,0,0.5);
        	color:#c5c5c5;
      }
.reply__indicator
	  {
          display:none;
      }
.reply__username
      {
         color:#c5c5c5;
      }
.post-attribution__username
     {
         color:#c5c5c5;
     }
.post-details-desktop__right
     {
          display:none;
     }

/* Okienko 'dodaj odpowiedź' i samo okno odpowiedzi - tło, czcionka */ 
.desktop-reply-create-entry, .desktop-reply-create-wrapper
{
    background-color: #0F111E;
}

.desktop-reply-create-entry__text, .rich-text-editor__content
{
     color: #BDBFD0;
}

/* Separatory */ 
.reply, .desktop-post-form__actions {
    border-top: 1px solid rgba(190, 209, 207, 0.3);
}

/* Obramowanie paska odpowiedzi i ikonek */ 
.desktop-reply-create-wrapper, .rich-text-editor__toolbar__icons-container

{
    border: 1px solid rgba(190, 209, 207, 0.3);
}
  }
