css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sajha.com") {

/* This entire chunk of section has only one purpose. To find that sneaky lil table element and make it dissapear */

body>table>tbody>tr>td>div#initialmain>table>tbody>tr>td>div>a.toggleopacity>object#nn>img,

body >table> tbody> tr> td[valign=top]> table >tbody> tr> td> object,

body>table>tbody>tr>td>div>a.toggleopacity>img,

body>table>tbody>tr>td>table>tbody>tr>td>div>a.toggleopacity>img,

body> table> tbody> tr> td:first-child + td >table> tbody> tr >td:first-child + td >a[class=toggleopacity] >div,

body> table> tbody >tr >td:first-child + td >table >tbody >tr td:first-child + td +td> a[class=toggleopacity] >div,

body >table >tbody> tr> td:first-child + td >table >tbody >tr >td:first-child + td + td +td >a[class=toggleopacity] >div,

body >table >tbody> tr[valign=top] >td:first-child> a[class=toggleopacity] >div,

body >table >tbody >tr[valign=top] >td:first-child +td >a[class=toggleopacity] >div,

body >table> tbody> tr[valign=top] >td:first-child +td +td >a[class=toggleopacity]> div,

body >table> tbody >tr[valign=top]> td:first-child + td + td + td >a[class=toggleopacity] >div,

body >table >tbody> tr[valign=top] >td >table >tbody >tr[valign=top] >td[valign=top]> div >div.cheader,

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top]>div#initialmain>p>iframe,



body >table> tbody> tr[valign=top]> td >table> tbody >tr[valign=top]> td[valign=top]> div#Layer11,

body> table> tbody >tr[valign=top]> td> table> tbody> tr[valign=top] >td[valign=top]> p,



body >table> tbody> tr[valign=top]> td >table> tbody >tr[valign=top] > td[valign=top]> table >tbody >tr > td > a > div,

body >table> tbody> tr[valign=top]> td > div[style=padding],

body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > div[align=right],

body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > iframe,


body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > div[align=center],



body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > table > tbody > tr[valign=top] > td:first-child,



body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > table > tbody > tr[valign=top] > td:first-child + td > div#midcolumn > div[align=center],

body>table>tbody>tr[valign=top]>td[valign=top]:first-child,

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr > td:first-child + td + td > p,



body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr > td:first-child + td + td > div.container > div.cheader,


body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr > td:first-child + td + td > div.container > div#videodiv,

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr > td:first-child + td + td > a > img,


body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr > td:first-child + td + td > div[align=center],
div#divthread ~ div[align=center],

/* New ADS, sajha acting clever? They changed some of the tables and divs, but i got em all for ya! Peace to opensource! */

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr[valign=top]>td:first-child +td +td> div > a.toggleopacity,

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr[valign=top]>td:first-child +td +td> a.toggleopacity,

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr[valign=top]>td:first-child +td +td>fieldset.container>div#videodiv





{

display:none !important;

}

/* End wild table selector chase. */

/* Would love ideas on what to do with this? It selects the part which says, you are signed in as [username] */


body > table > tbody > tr:first-child + tr  > td > table > tbody > tr, body>table> tbody> tr[valign=top]> td >table> tbody> tr[valign=top]> td[valign=top]> font{
border: 1px solid olive;
}
/*End username section */

/*This is the search bar */
body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > table > tbody > tr[valign=top] > td:first-child + td > div#midcolumn > form[name=search] {
position:absolute;
top:0;
right:10px;
font-size: 0.8em;
}
/*When you click on the search bar, it somehow manages to metamorph into this ugly lil thing */
body >table> tbody> tr[valign=top]> td:first-child + td > div#initialmain > table > tbody > tr[valign=top] > td:first-child + td > div#midcolumn > form[name=search] > div.container > div.cheader {
font-size: 0.7em;
position: absolute;
top:0;
right:10px;
width: 180px;
max-height: 20px;
}

/*End search bar */

/*The two selectors show you Users online and the pic */
body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > font[face=tahoma]{
position:absolute;
top:0;
right:400px;
}

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > img[align=left] {
position:absolute;
top:0;
right:640px;
}

/*End Users Online*/

/* Code to make the threads on homepage a bit 'fatter' */

body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr >td:first-child + td > div#midcolumn{

padding: 20px;
}

/* Increase height of the thoughtbox */
body>table>tbody>tr[valign=top]>td:first-child + td[valign=top] > div#initialmain > table >tbody >tr[valign=top]>td:first-child +td +td>fieldset.container>div#thoughtsdiv {
min-height: 1300px;
}


/*End CSS for homepage */


/*BEGIN CSS for threads*/
/* Remove the grey color from thread bars. */
div#divthread>table>tbody>tr[bgcolor*=cccccc]>td {
background: white !important;

}

/* Align the username text to the right */
div#divthread>table>tbody>tr[bgcolor*=cccccc]>td>div.gen {
float:right;
}
/* Scre the userpic, we dont need that */

div#divthread>table>tbody>tr[bgcolor*=cccccc]>td>div>img.userpic {
display:none;
}

/* make the threads look nicer */
div#divthread {
max-width: 900px;
margin: auto;
}



}
