css:
@-moz-document url-prefix("https://boardgamearena.com/archive"), regexp("https://boardgamearena.com/.*raceforthegalaxy.*") {
.card_wrapper {
  margin: 2.5px;
}

#game_play_area .whiteblock {
    padding: 5px;
}
#game_play_area .whiteblock h3 {
    margin-bottom: 5px;
}

#player_hand, #player_hand_orb, #player_hand_art, #explore_set {
    margin: 2.5px;
}

@media screen and (max-width: 365px) {
    #game_play_area .whiteblock {
        padding: 2px;
    }
    #game_play_area .whiteblock h3 {
        margin-bottom: 2px;
    }
}
}
