css:
/* i really want this to be global */
/* Vivaldi - Base domain highlighter (light theme) */
.theme-light .UrlFragment--Highlight {
    color: #000 !important;
}
.theme-light .UrlFragment--Lowlight,
.theme-light .UrlFragment-Link UrlFragment--Lowlight {
    color: #999 !important;
}
/* Addressbar background color */
.theme-light .SearchField, .UrlBar-AddressField {
    border: 0px !important;
    background-color: var(--colorBgLight) !important;
}
