css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("board.ogame.nl") {
    /* Make that the forum will use the full screen */
    #page {
        max-width: 95% !important;
    }

    /* Delete top bar that is just a waste of space */
    /*div.mainHeadline {
        display: none !important;
    }*/
    
    /* Delete icons before subforums, improves readability */
    h5 img[src="icon/ogameold/boardS.png"], h5 img[src="icon/ogameold/boardNewS.png"] {
        display: none !important;
    }

    /* Delete ugly infobar */
    div.info {
        display: none !important;
    }

    /* Disable folder icons before breadcrumbs */
    img[src="icon/indexS.png"], img[src="icon/categoryS.png"], img[src="icon/boardS.png"] {
        display: none !important;
    }

    /* Disable green arrow icon before topic titles on the main page */
    div.boardlistInner div.containerContent div.boardlistLastPost div.containerIconSmall {
        display: none !important;
    }
    
    /* Disable menu icons */
    div#mainMenu div ul li a img {
        display: none !important;
    }

    /* Disable background */
    body {
        background-image: none !important;
    }
    
    /* Improve thread icon logic */
    td.columnIcon img[src="icon/threadM.png"], td.columnIcon img[src="icon/threadClosedM.png"] {
        display: none !important;
    }

    /* Delete the space between two posts */
    div.message {
        margin-bottom: 0px !important;
    }
    /* Make the borders black instead of white */
    div.signature, p.editNote {
        border-top: 1px solid #000000 !important;
    }
    div.messageHeader {
        border-bottom: 1px solid #000000 !important;
    }
    /* Remove the lowest border */
    div.messageFooterRight {
        border-top: 0 !important;
    }

    /* Remove the image before a quote */
    blockquote.quoteBox div.quoteHeader h3 img {
        display: none !important;
    }

    /* Make that the quote block looks a bit more like the old one */
    blockquote.quoteBox div.quoteHeader {
        border-bottom: 1px solid #000000 !important;
        padding-left: 8px !important;
        margin-left: -15px !important;
        margin-top: -7px !important;
        margin-right: -15px !important;
    }
    
    /* Improve difference between read and unread subforums */
    h5 a {
        color: #939EB4 !important;
    }
    h5 a.new {
        color: #C3CEE4 !important;
    }
    /* Remove the number of posts behind a forum */
    h5 a.new span, h4.boardTitle a.new span, h3.boardTitle a.new span {
        display: none !important;
    }
    
    div.containerContentSmall p:first-child {
        color: #FFFFFF !important;
        font-weight: 900 !important;
    }
    
    /* Remove italics in breadcrumbs */
    .breadCrumbs li, .breadCrumbs li a, .breadCrumbs li a span {
        font-style: normal !important;
        text-decoration: none !important;
    }

    /* Clear the topic links */
    td.columnTopic div.topic a {
        color: #939EB4 !important;
        font-weight: normal !important;
    }
    td.columnTopic div.new a {
        color: #C3CEE4 !important;
        font-weight: bold !important;
    }
    .prefix strong {
        color: #FFFFFF !important;
        font-weight: bold !important;
    }
    td.columnTopic div.topic a:hover {
        color: #FFFFFF !important;
    }
    
    /* Remove annoying space between subforums on topic page */
    ul#boardlist li.border {
        margin-bottom: 0px !important;
        padding-bottom: 0px !important;
        margin-top: 0px !important;
        padding-top: 0px !important;
    }
    div.boardlistInner {
        margin-bottom: 0px !important;
    }

    .mainHeadline .headlineContainer h2 a {
        color: #AAAAAA !important;
    }
    .message .messageContentInner h3 span {
        display: none;
    }
}
