css:
@-moz-document url-prefix("https://minehut.com/dashboard"), url-prefix("https://minehut.com/market"), url-prefix("https://minehut.com/shop"), url-prefix("https://minehut.com/support"), url-prefix("https://minehut.com/profile"), url-prefix("https://minehut.com/servers"), url-prefix("https://minehut.com/login") {
.v-toolbar__content, .v-toolbar__extension { background: #2C2F33 !important; }

.theme--dark.v-application { background: #23272A; }

.theme--dark.v-card { background: #2C2F33; }

.v-application .primary {
background-color: #7289DA !important;
  border-color: #7289DA !important;
}

.theme--dark.v-chip:not(.v-chip--active) { background: #36393D !important; }

.theme--dark.container.container--fluid { margin-top: 4rem !important; }

.v-list { background: #2C2F33 !important; }

.v-data-table { background: #2C2F33 !important; }

.v-application .accent--text {
	color: #7289DA !important;
  caret-color: #7289DA !important;
}

.theme--dark.v-text-field--solo>.v-input__control>.v-input__slot { background: #2C2F33; }

.theme--dark.v-pagination .v-pagination__item { background: #2C2F33; }

button, input, select, textarea { background: #2C2F33; }

.theme--dark.v-expansion-panels .v-expansion-panel { background: #2C2F33; }

.v-application .warning { background-color: #DD4016 !important; border-color: #DD4016 !important; }

.advertisment-rail { display: none!important; }

.content { width: 75% !important; margin-left: 12em; }

.v-toolbar__content, .v-toolbar__extension { background: inherit; }

.v-footer .footer-content { background: #23272A !important; padding-bottom: 4em !important; }

.theme--dark.v-divider { border: none; }

.advertisement-server-bar { display: none; }

.v-application a { color: #7289DA; }

.v-navigation-drawer { background: #23272A !important; margin-top: 4em; }

.v-list-item { background: #23272A !important; }

.v-application .primary--text { color: #7289DA !important; }

.v-navigation-drawer__border { background: none !important; }

.v-navigation-drawer__content { height: 75%; }

.v-list-item a { text-decoration: none; }

.v-list-item a:hover { color: white; transition: color .4s; }

::-webkit-scrollbar { width: 10px; }

::-webkit-scrollbar-track { background: #23272A; }
 
::-webkit-scrollbar-thumb { background: #7289DA; }

::-webkit-scrollbar-thumb:hover {background: #7289DA; }

.v-list-item { background: #2C2F33 !important; }

.v-breadcrumbs mt-n6 ml-10 theme--dark { display: none; }
}
